) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `majors`
--

INSERT INTO `majors` (`id`, `name`, `subjects`, `department_id`, `created_at`, `updated_at`) VALUES
(1, 'Chuyên ngành toán tin ứng dụng', '[{\"id\":973379780218,\"name\":\"Phát triển ứng dụng\",\"description\":\"<ul><li>Phát triển ứng dụng là một trong những hướng tiên tiến nhất trong lĩnh vực công nghệ thông tin. Sinh viên sẽ được trang bị kiến thức vững vàng về lập trình và thiết kế ứng dụng, từ đó có khả năng tạo ra các sản phẩm phần mềm hiệu quả và tiện ích.<br>Kĩ năng đầu ra: HTML, CSS, My SQL, SQL Server, Java, .Net....<br>Các công việc liên quan:<br>Web developer, Font - end, Back-end, React...<br>Cấu trúc các học phần</li><li><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1069/261;\\\" src=\\\"http://localhost:8000/assets/user/images/about\\\\image.png\\\" width=\\\"1069\\\" height=\\\"261\\\"></figure></li></ul>\",\"categories\":[{\"id\":596343874421,\"name\":\"Lập trình .Net\",\"description\":\"<p><strong>Mã học phần</strong>: MTH10310&nbsp;</p><p>Thuộc khối kiến thức: Chuyên ngành Số tín chỉ: 04 (3LT + 1TH)&nbsp;</p><p>Điều kiện đăng ký học phần: Học phần học trước hoặc tiên quyết: <strong>Lập trình Hướng Đối Tượng.&nbsp;</strong></p><p>Học phần song hành: không. Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình căn bản.</p><p><strong>Mục tiêu</strong> <strong>môn học: </strong>Phát triển kỹ năng lập trình ứng dụng với C# trên môi trường lập trình tích hợp Visual Studio .NET.</p><p>&nbsp;Mô tả môn học Sử dụng môi trường Visual Studio 2005: thiết kế giao diện, hiện thực tác vụ của ứng dụng, tạo các thành phần phần mềm… dựa trên .NET, kiểm tra bẩy lỗi, xữ lý lỗi, và triển khai ứng dụng.</p>\"},{\"id\":501990971961,\"name\":\"Lập trình Java\",\"description\":\"<p><strong>Mã học phần:</strong> MTH10316&nbsp;</p><p><strong>Thuộc khối kiến thức: </strong>Chuyên ngành Số tín chỉ: 03 (2LT + 1TH)&nbsp;</p><p><strong>Điều kiện đăng ký học phần: </strong>Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt): Lập trình hướng đối tượng.&nbsp;</p><p><strong>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có):</strong> Kỹ năng lập trình.&nbsp;</p><p><strong>Mô tả môn học: </strong>Trong học phần này sinh viên sẽ học được kiến thức tổng quan về công nghệ phát triển phần mềm bằng Java. Trong chương 2, sinh viên sẽ học thuần túy về ngôn ngữ lập trình Java như các kiểu dữ liệu, từ khóa, biến, toán tử, cấu trúc điều khiển, vòng lập … tương tự như ngôn ngữ lập trình C. - Chương 3, sẽ giới thiệu lại các khái niệm và cách tạo ra lớp (cả lớp nội, lớp trừu tượng), đối tượng và các đặc tính trong lập trình hướng đối tượng như tính kế thừa, tính đa hình. Đặt biệt giới thiệu một khái niệm về lập trình “giao diện - interface” để giải quyết vấn đề kế thừa bội (rắc rối gặp phải khi dùng ngôn ngữ C/C++ để lập trình hướng đối tượng). Bên cạnh đó sẽ giới thiệu một số lớp có sẵn được cung cấp bởi Sun. - Chương 4, giới thiệu về cơ chế quản lý lỗi và thu gom rác trong Java. Sinh viên sẽ được học về khái niệm “biệt lệ - exception”, tạo biệt lệ và cách sử dụng chúng trong lập trình. Hiểu rõ cơ chế “try - catch - finally” và “ném – throws” lỗi. - Chương 5, giới thiệu về cách làm việc với các dòng – stream dữ liệu. Sinh viên sẽ tìm hiểu về cách làm việc với các dòng nhập xuất ký tự, nhị phân được cung cấp trong gói java.io. Tìm hiểu về lớp System và làm việc với các dòng nhập xuất chuẩn. - Chương 6,7,8 giới thiệu về các tao dựng giao diện sử dụng bằng đồ họa được cung cấp trong bộ thư viện AWT. Sinh viên sẽ được học về cách sử lý các sự kiện xảy ra trên chuột, bàn phím để cho phép người dùng có thể điều khiển các chức năng trên giao diện phần mềm. - Chương 9, giới thiệu về khái niệm luồng, các lập trình đa luồng nói chung và trong Java nói riêng. Sinh viên sẽ có kỹ năng làm việc với luồng – Thread trong Java như thiết lập trạng thái, độ ưu tiên và đồ bộ dữ liệu giữa các luồng. Vấn đề Deadlock, cơ chế Chờnhận biết.&nbsp;</p><p><strong>Mục tiêu môn học</strong> Sinh viên sẽ có được các kiến thức và kỹ năng cơ bản đến nâng cao về lập trình Java bao gồm: lập trình cơ bản, lập trình hướng đối tượng, lập trình giao diện. Từ đó sinh viên có thể tìm hiểu chuyên sâu hơn về công nghệ J2EE như lập trình ứng dụng cho môi trường mạng, web, lập trình với CSDL, lập trình phân tán … Đây là những kiến thức cần thiết để sinh viên có thể trở thành chuyên gia lập trình về công nghệ J2EE trong tương lai.</p>\"},{\"id\":499026602841,\"name\":\"Cơ sở dữ liệu\",\"description\":\"<p><strong>Mã môn học:</strong> MTH10344</p><p><strong>&nbsp;Thuộc khối kiến thức:</strong> Chuyên ngành Số tín chỉ: 04(3LT+1TH)</p><p><strong>&nbsp;Điều kiện đăng ký học phần:</strong> Học phần tiên quyết: Cơ sở dữ liệu</p><p>Học phần học trước: Học phần song hành:&nbsp;</p><p>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình&nbsp;</p><p><strong>Nội dung môn học:</strong> - Trình bày ngắn gọn vai trò, vị trí học phần/môn học, kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong chương trình đào tạo) - Các phương pháp thống kê trong nhận dạng mẫu. - Ước lượng thông số. - Học có giám sát. - Học không có giám sát Mục tiêu môn học: Giúp sinh viên tiếp cận các kiến thức về cơ sở dữ liệu, hệ quản trị cơ sở dữ liệu: các thành phần của hệ quản trị cơ sở dữ liệu SQL Server và chức năng của chúng, các cơ chế quản lý truy xuất đồng thời, an toàn và khôi phục dữ liệu sau sự cố, phân quyền và bảo mật.</p>\"},{\"id\":883701629167,\"name\":\"Hệ QT CSDL\",\"description\":\"<p>Mã môn học: MTH10344&nbsp;</p><p>Thuộc khối kiến thức: Chuyên ngành Số tín chỉ: 04(3LT+1TH)</p><p>Điều kiện đăng ký học phần: Học phần tiên quyết: Cơ sở dữ liệu&nbsp;</p><p>Học phần học trước: Học phần song hành: Các yêu cầu về kiến thức, kỹ năng của SV: lập trình</p><p><strong>Nội dung môn học:</strong> - Trình bày ngắn gọn vai trò, vị trí học phần/môn học, kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong chương trình đào tạo) - Các phương pháp thống kê trong nhận dạng mẫu. - Ước lượng thông số. - Học có giám sát. - Học không có giám sát <strong>Mục tiêu môn học:</strong> Giúp sinh viên tiếp cận các kiến thức về cơ sở dữ liệu, hệ quản trị cơ sở dữ liệu: các thành phần của hệ quản trị cơ sở dữ liệu SQL Server và chức năng của chúng, các cơ chế quản lý truy xuất đồng thời, an toàn và khôi phục dữ liệu sau sự cố, phân quyền và bảo mật.</p>\"},{\"id\":363352841389,\"name\":\"PTTK HTTT\",\"description\":\"<p><strong>Mã học phần: MTH10315&nbsp;</strong></p><p><strong>Thuộc khối kiến thức</strong>: Chuyên ngành Số tín chỉ: 04 (3LT + 1TH)&nbsp;</p><p>Điều kiện đăng ký học phần: Học phần học trước hoặc tiên quyết: Cơ sở dữ liệu, Lập trình hướng đối tượng.&nbsp;</p><p>Học phần song hành: không.&nbsp;</p><p>Các yêu cầu về kiến thức, kỹ năng của SV: phương pháp luận hướng đối tượng, thiết kế cơ sở dữ liệu.</p><p>&nbsp;<strong>Mô tả môn học:</strong> - Các khái niệm cơ bản về hệ thống. - Phương pháp khảo sát hiện trạng và tìm hiểu nhu cầu. - Phương pháp phân tích có cấu trúc (SA). - Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ. - Phương pháp SART. - Phương pháp SD.&nbsp;</p><p><strong>Mục tiêu môn học:</strong> Giới thiệu cho sinh viên các phương pháp phân tích và thiết kế kinh điển sử dụng trong việc phân tích chức năng, phân tích dữ liệu, phân tích động thái và thiết kế hệ thống.</p>\"},{\"id\":437084124372,\"name\":\"Cấu trúc dữ liệu\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br>Mục tiêu của môn học<br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":212698985452,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":311223219662,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":355426453611,\"name\":\"Quản lý ĐA PM\",\"description\":\"<p>Mã học phần: MTH10314<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Phát triển phần mềm hướng đối tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình hướng đối tượng.<br><strong>Mô tả môn học</strong><br>- Các khái niệm cơ bản về quản lý dự án phần mềm.<br>- Biểu đồ GANTT, PERT.<br>- Tiêu chuẩn phần mềm, tiêu chuẩn tài liệu.<br>- Rủi ro trong phần mềm.<br>- Cấu hình phần mềm.<br>- Mô hình ước lượng giá phần mềm.<br><strong>Mục tiêu môn học:</strong> Giới thiệu cho sinh viên những vấn đề cơ bản về việc lập kế hoạch,<br>tổ chức và quản lý dự án phần mềm.</p><p><strong>Tài liệu học tập:</strong><br>[1] P. Jalote. Software Project Management in Practice, Addison Wesley, 2002.<br>[2] I. Sommerville. Software Engineering , Addison Wesley, 1996.<br>[3] W. A. Randolph. Effective Project Planning and Management, Prentice Hall, 1998.</p>\"},{\"id\":791696031592,\"name\":\"Kiểm thử PM 1\",\"description\":\"\"},{\"id\":367552731519,\"name\":\"PT PM HĐT\",\"description\":\"<p>Mã học phần: MTH10308<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>- Số tiết lý thuyết: 45<br>- Số tiết thực hành, thực tập: 30<br>- Số tiết bài tập trên lớp: 15<br>- Số tiết thảo luận: 30<br>- Số tiết làm việc nhóm: 30<br>- Số tiết tự học: 30<br><strong>Mục tiêu của học phần</strong><br>Cung cấp cho các sinh viên các kiến thức chuyên sâu liên quan đến các đối tượng chính<br>yếu trong lĩnh vực công nghệ phần mềm hướng đối tượng (qui trình công nghệ, phương<br>pháp kỹ thuật thực hiện, công cụ và môi trường triển khai phần mềm,…).<br><strong>Tóm tắt nội dung học phần</strong><br>Các khái niệm cơ bản về phát triển phần mềm hướng đối tượng.<br>Mô hình đối tượng, mô hình trạng thái, mô hình chức năng.<br>Thiết kế hệ thống, thiết kế đối tượng, thiết kế giao diện.<br>Sử dụng các IDE và công cụ quản lý mã nguồn.</p><p><strong>Tài liệu học tập:</strong><br>[1] James Rumbaugh, Michael Blaha, Wiliam Premerlani, Frederick Eddy,<br>Wiliam Lorensen. Object-Oriented Modeling and Design, Prentice-Hall<br>International Editions, 1991.<br>[2] Erich Gamma, Richard Helm, Ralph Johnson and John Vlissides. Design Patterns,<br>Addison-Wesley, 1995.<br>[3] d. Martin Fowler. UML Distilled, 3rd edition, Addison-Wesley, 2004.</p>\"},{\"id\":420148747729,\"name\":\"Kiểm thử PM 2\",\"description\":\"\"}]},{\"id\":993419102369,\"name\":\"Lập trình ứng dụng mạng\",\"description\":\"<p>Lập trình ứng dụng mạng là một trong những hướng tiên tiến nhất trong lĩnh vực công nghệ thông tin. Sinh viên sẽ được trang bị kiến thức vững vàng về lập trình và thiết kế ứng dụng, từ đó có khả năng tạo ra các sản phẩm phần mềm hiệu quả và tiện ích.<br>Kĩ năng đầu ra: HTML, CSS, My SQL, SQL Server, Java, .Net....<br>Các công việc liên quan:<br>Web developer, Font - end, Back-end, React…</p><p>Cấu trúc các học phần:</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1724/508;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.32.12.png\\\" width=\\\"1724\\\" height=\\\"508\\\"></figure>\",\"categories\":[{\"id\":238720201820,\"name\":\"Cấu trúc dữ liệu\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br>Mục tiêu của môn học<br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":569154207772,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":834115062124,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":206836895851,\"name\":\"Mạng máy tính\",\"description\":\"<p>Mã môn học: MTH10311<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Trang bị cho người học những kiến thức, kỹ năng về:<br>- Có kiến thức cơ bản về mạng máy tính như: phân biệt được các loại hình mạng, nắm<br>được cơ sở truyền thông và truyền dữ liệu trong mạng, nắm được chức năng và dịch vụ<br>hoạt động ở mỗi tầng trong mô hình OSI và TCP/IP.<br>- Sinh viên sẽ học được những kỹ năng sử dụng các công cụ phần mềm để bắt và phân<br>tích dữ liệu tại mỗi tầng. Sử dụng các dịch vụ phổ biến như web, chuyển tập tin, thư<br>điện tử, tên miền, tường lửa. Cấu hình thiết bị định tuyến.<br>Đây là những kiến thức và kỹ năng cơ bản để giúp sinh viên định hướng nghiên cứu<br>trong các lĩnh vực như mạng máy tính, quản trị hệ thống mạng hay lập trình ứng dụng<br>cho mạng.<br><strong>Nội dung môn học:</strong><br>Giúp sinh viên hiểu và nắm được các kiến thức cơ bản như sau:<br>• Chương 1: Giới thiệu về tổng quan về mạng máy tính và các ứng dụng của mạng máy<br>tính trong thực tế. Phân loại các loại hình mạng máy tính và các hệ điều hành mạng.<br>Giới thiệu sơ lược về chồng giao thức OSI và TCP/IP.<br>• Chương 2, 3, 4, 5: Giới thiệu chi tiết chức năng và dịch vụ các tầng ứng dụng, giao<br>vận, mạng, liên kế dữ liệu. Nội dung giảng dạy trong những chương này theo các tiếp<br>cận theo kiểu top-down sẽ giúp sinh viên dễ dành trong việc tiếp thu kiến thức về mạng.<br>• Chương 6: Giới thiệu về vấn đề an toàn mạng và vấn đề cơ bản như các phương pháp<br>mã hóa, các phương thức chứng thực, hiểu một số kiểu tấn công mạng và các phòng<br>chống để đảm bảo vấn đề toàn vẹn dữ liệu mạng.</p><p><strong>Tài liệu học tập:</strong><br>1. J.F. Kurose, Computer Networking, A Top-Down Approach Featuring the<br>Internet, 3rd edition, Addison Wesley, 2004.<br>2. Fred Halsall, Computer Networking and the Internet, Fifth edition, addisonwesley, 2005<br>3. Nguyễn Thúc Hải, Mạng máy tính và các hệ thống mở, NXB Giáo Dục, 1997.<br>4. Andrew S. Tanenbaum, Computer Network, 4th edition, Prentice Hall, 2003.</p>\"},{\"id\":207088293455,\"name\":\"Cơ sở dữ liệu\",\"description\":\"<p>Mã môn học: MTH10312<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các khái niệm cơ bản về cơ sở dữ liệu; các mô hình dữ liệu; các<br>tiêu chuẩn về thiết kế cơ sở dữ liệu và các dạng chuẩn.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản về cơ sở dữ liệu.<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Các phép toán trên mô hình dữ liệu quan hệ.<br>- Ngôn ngữ truy vấn SQL.<br>- Các dạng chuẩn và thuận toán chuẩn hóa cơ sở dữ liệu.</p><p><strong>Tài liệu học tập:</strong><br>[1] R. Elsmari, S. Navathe. Fundamentals of Database Systems 4th edition, Addison<br>Wesley, 2004.<br>[2] Nguyễn Kim Anh. Nguyên lý của các hệ cơ sở dữ liệu, NXB ĐHQG Hà Nội, 2004.</p>\"},{\"id\":881686260461,\"name\":\"Lập trình .Net\",\"description\":\"<p>Mã học phần: MTH10310<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Lập trình Hướng Đối Tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình căn bản.<br><strong>Mục tiêu môn học</strong><br>Phát triển kỹ năng lập trình ứng dụng với C# trên môi trường lập trình tích hợp Visual<br>Studio .NET.<br><strong>Mô tả môn học</strong><br>Sử dụng môi trường Visual Studio 2005: thiết kế giao diện, hiện thực tác vụ của ứng dụng,<br>tạo các thành phần phần mềm… dựa trên .NET, kiểm tra bẩy lỗi, xữ lý lỗi, và triển khai<br>ứng dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đề cương Lập Trình .Net của khoa Công Nghê Thông Tin – Đại Học Sư Phạm Hà<br>Nội.<br>[2] Đề cương Lập Trình .Net của khoa Công Nghê Thông Tin – Đại Học Bán Công Tôn<br>Đức Thắng.<br>[3] Developing Window Based Applications With Microsoft.Net MCAD_MCSD<br>[4] Course Microsoft 70-316</p>\"},{\"id\":699059465340,\"name\":\"Lập trình Java\",\"description\":\"<p>Mã học phần: MTH10316<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 03 (2LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt): Lập trình<br>hướng đối tượng.<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có): Kỹ năng lập trình.<br><strong>Mô tả môn học</strong><br>Trong học phần này sinh viên sẽ học được kiến thức tổng quan về công nghệ phát triển<br>phần mềm bằng Java. Trong chương 2, sinh viên sẽ học thuần túy về ngôn ngữ lập trình<br>Java như các kiểu dữ liệu, từ khóa, biến, toán tử, cấu trúc điều khiển, vòng lập … tương<br>tự như ngôn ngữ lập trình C.<br>- Chương 3, sẽ giới thiệu lại các khái niệm và cách tạo ra lớp (cả lớp nội, lớp trừu tượng),<br>đối tượng và các đặc tính trong lập trình hướng đối tượng như tính kế thừa, tính đa hình.<br>Đặt biệt giới thiệu một khái niệm về lập trình “giao diện - interface” để giải quyết vấn<br>đề kế thừa bội (rắc rối gặp phải khi dùng ngôn ngữ C/C++ để lập trình hướng đối<br>tượng). Bên cạnh đó sẽ giới thiệu một số lớp có sẵn được cung cấp bởi Sun.<br>- Chương 4, giới thiệu về cơ chế quản lý lỗi và thu gom rác trong Java. Sinh viên sẽ được<br>học về khái niệm “biệt lệ - exception”, tạo biệt lệ và cách sử dụng chúng trong lập trình.<br>Hiểu rõ cơ chế “try - catch - finally” và “ném – throws” lỗi.<br>- Chương 5, giới thiệu về cách làm việc với các dòng – stream dữ liệu. Sinh viên sẽ tìm<br>hiểu về cách làm việc với các dòng nhập xuất ký tự, nhị phân được cung cấp trong gói<br>java.io. Tìm hiểu về lớp System và làm việc với các dòng nhập xuất chuẩn.<br>- Chương 6,7,8 giới thiệu về các tao dựng giao diện sử dụng bằng đồ họa được cung cấp<br>trong bộ thư viện AWT. Sinh viên sẽ được học về cách sử lý các sự kiện xảy ra trên<br>chuột, bàn phím để cho phép người dùng có thể điều khiển các chức năng trên giao diện<br>phần mềm.<br>- Chương 9, giới thiệu về khái niệm luồng, các lập trình đa luồng nói chung và trong Java<br>nói riêng. Sinh viên sẽ có kỹ năng làm việc với luồng – Thread trong Java như thiết lập<br>trạng thái, độ ưu tiên và đồ bộ dữ liệu giữa các luồng. Vấn đề Deadlock, cơ chế Chờnhận biết.<br>Mục tiêu môn học<br>Sinh viên sẽ có được các kiến thức và kỹ năng cơ bản đến nâng cao về lập trình Java<br>bao gồm: lập trình cơ bản, lập trình hướng đối tượng, lập trình giao diện. Từ đó sinh<br>viên có thể tìm hiểu chuyên sâu hơn về công nghệ J2EE như lập trình ứng dụng cho môi<br>trường mạng, web, lập trình với CSDL, lập trình phân tán … Đây là những kiến thức<br>cần thiết để sinh viên có thể trở thành chuyên gia lập trình về công nghệ J2EE trong<br>tương lai.</p><p><strong>Tài liệu học tập:</strong><br>1. James Gosling - The Java Language Specification - 2005 (3rd edition)<br>2. Ken Arnold, James Gosling - The Java Programming Language<br>3. Trần Tiến Dũng - Giáo trình lý thuyết và bài tập Java - 1999</p>\"},{\"id\":483268461578,\"name\":\"Thiết kế web\",\"description\":\"<p>Mã môn học: MTH10333<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Nhập môn lập trình<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản<br><strong>Nội dung môn học:</strong><br>- Kiến trúc ứng dụng web<br>- Quy trình thiết kế web<br>- Các ngôn ngữ thiết kế web (client)<br>- Các công cụ đồ họa, framework xây dựng trang web<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Môn học cung cấp những kiến thức cơ bản trong lĩnh vực thiết kế<br>website. Ứng dụng các công cụ thiết kế đồ họa cho trang web.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững kiến trúc ứng dụng web, quy trình thiết kế website, các<br>công cụ và ngôn ngữ xây dựng trang web<br>- Kỹ năng: khảo sát, phân tích, thiết kế, xây dựng giao diện và triển khai trang<br>web<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Nguyễn Quang Hải, Nhập môn HTML và CSS, 2014<br>2. Frank Boumphrey, HTML5 for dummies: elearning kit, 2012<br>3. Các tài liệu thiết kế web nguồn mở, 2010, <a href=\\\"http://www.w3schools.com/\\\">http://www.w3schools.com</a></p>\"},{\"id\":736548049631,\"name\":\"PT TK HTTT\",\"description\":\"<p>Mã học phần: MTH10315<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Cơ sở dữ liệu, Lập trình hướng đối tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: phương pháp luận hướng đối tượng, thiết kế<br>cơ sở dữ liệu.<br><strong>Mô tả môn học:</strong><br>- Các khái niệm cơ bản về hệ thống.<br>- Phương pháp khảo sát hiện trạng và tìm hiểu nhu cầu.<br>- Phương pháp phân tích có cấu trúc (SA).<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Phương pháp SART.<br>- Phương pháp SD.<br><strong>Mục tiêu môn học:</strong> Giới thiệu cho sinh viên các phương pháp phân tích và thiết kế kinh<br>điển sử dụng trong việc phân tích chức năng, phân tích dữ liệu, phân tích động thái và<br>thiết kế hệ thống.</p><p><strong>Tài liệu học tập:</strong><br>[1] Nguyễn Văn Ba. Phân tích và thiết kế hệ thống thông tin, NXB ĐHQG Hà Nội,<br>2005.<br>[2] Hawryczkiewycz I. T.. Introduction to System Analysis and Design, Prentice Hall<br>New Delhi, 1989.</p>\"},{\"id\":802707830411,\"name\":\"LTW ASP.NET\",\"description\":\"<p>Mã môn học: MTH10345<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Thiết kế web, Cơ sở dữ liệu, Lập trình Hướng Đối Tượng<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Thiết kế và xây dựng ứng dụng web bằng ASP.NET.<br>Xây dựng các dịch vụ web trên .NET<br>Sử dụng các framework hiện đại trên .NET để phát triền ứng dụng web<br>Triển khai ứng dụng web.<br>Bảo trì ứng dụng web<br>Mục tiêu môn học:<br>Mục tiêu chung: Cung cấp cho sinh viên các kiến thức để xây dựng và phát triển các<br>ứng dụng web bằng công nghệ ASP.NET:<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững kiến thức xây dựng ứng dụng web<br>- Kỹ năng: khảo sát, phân tích, thiết kế, xây dựng, triển khai và bảo trì website<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Nguyễn Ngọc Bình Phương, Các giải pháp lập trình <a href=\\\"http://asp.net/\\\">ASP.NET</a> 2.0: tập I, 2007<br>2. Building <a href=\\\"http://asp.net/\\\">ASP.NET</a> application for the web: class notes - ASP0018D, 2010<br>3. Các bài giảng mã nguồn chính thức của Microsoft về công nghệ <a href=\\\"http://asp.net/\\\">ASP.NET</a>, 2010,<br><a href=\\\"http://www.asp.net/\\\">http://www.asp.net/</a></p>\"},{\"id\":850775671248,\"name\":\"LTW J2EE\",\"description\":\"\"},{\"id\":220395588481,\"name\":\"LTW PHP\",\"description\":\"<p>Mã môn học: MTH10337<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Thiết kế web, Cơ sở dữ liệu<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Thiết kế và xây dựng ứng dụng web bằng PHP.<br>Xây dựng các dịch vụ web trên PHP<br>Sử dụng các framework hiện đại trên PHP để phát triền ứng dụng web<br>Triển khai ứng dụng web.<br>Bảo trì ứng dụng web<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Ứng dụng công nghệ để xây dựng các ứng dụng cho các tổ chức,<br>doanh nghiệp.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: các kiến thức về công nghệ mới trên nền tảng .NET<br>• Kỹ năng: tìm hiểu và ứng dụng công nghệ mới trên nền tảng .NET<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Phạm Hữu Khang - Phương Lan, Lập trình web bằng php 5.3 và cơ sở dữ liệu<br>MySQL 5.1: tập I, II, 2010<br>2. Luke Welling - Laura Thomson, PHP and MySQL Web development, 2008,<br><a href=\\\"http://www.asp.net/\\\">http://www.asp.net/</a><br>3. Refsnes Data, Các bài giảng về PHP nguồn mở, 2010,<br><a href=\\\"http://www.w3schools.com/\\\">http://www.w3schools.com/</a></p>\"},{\"id\":379473579740,\"name\":\"Kiểm thử PM 1\",\"description\":\"\"},{\"id\":488734074307,\"name\":\"Chuyên đề .Net\",\"description\":\"<p>Mã môn học: MTH10341<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Lập trình web ASP.NET<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>- Các công nghệ trên nền tảng .NET.<br>- Lập trình trên Windows Phone<br>Mục tiêu môn học:<br>Mục tiêu chung: Ứng dụng công nghệ để xây dựng các ứng dụng cho các tổ chức,<br>doanh nghiệp.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: các kiến thức về công nghệ mới trên nền tảng .NET<br>- Kỹ năng: tìm hiểu và ứng dụng công nghệ mới trên nền tảng .NET<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Microsoft - Tài liệu, mã nguồn các công cụ trên .NET - 2002 -<br><a href=\\\"https://msdn.microsoft.com/\\\">https://msdn.microsoft.com/</a><br>2. Robert Powell, Richard Weeks, C# and the .NET framework: the C++ perspective, 2002<br>3. Chris Maunder - David Cunningham, Các ứng dụng nguồn mở, 2010,&nbsp;<br><a href=\\\"http://www.codeproject.com/\\\">http://www.codeproject.com/</a></p>\"},{\"id\":283519728421,\"name\":\"Chuyên đề J2EE\",\"description\":\"\"}]},{\"id\":225114843948,\"name\":\"Ứng dụng di động\",\"description\":\"<p>Đào tạo các chuyên gia trong lĩnh vực phát triển ứng dụng di động, từ thiết kế giao diện đến lập trình nền tảng, để đáp ứng nhu cầu ngày càng cao của thị trường công nghệ di động toàn cầu.</p><p>Kỹ năng đầu ra:</p><p>Các công việc liên quan:</p><p>Cấu trúc các học phần:</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1698/420;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.35.04.png\\\" width=\\\"1698\\\" height=\\\"420\\\"></figure>\",\"categories\":[{\"id\":367060500041,\"name\":\"Cấu trúc dữ liệu\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br>Mục tiêu của môn học<br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":988163875156,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":733227610798,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":641197240475,\"name\":\"Mạng máy tính\",\"description\":\"<p>Mã môn học: MTH10311<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Trang bị cho người học những kiến thức, kỹ năng về:<br>- Có kiến thức cơ bản về mạng máy tính như: phân biệt được các loại hình mạng, nắm<br>được cơ sở truyền thông và truyền dữ liệu trong mạng, nắm được chức năng và dịch vụ<br>hoạt động ở mỗi tầng trong mô hình OSI và TCP/IP.<br>- Sinh viên sẽ học được những kỹ năng sử dụng các công cụ phần mềm để bắt và phân<br>tích dữ liệu tại mỗi tầng. Sử dụng các dịch vụ phổ biến như web, chuyển tập tin, thư<br>điện tử, tên miền, tường lửa. Cấu hình thiết bị định tuyến.<br>Đây là những kiến thức và kỹ năng cơ bản để giúp sinh viên định hướng nghiên cứu<br>trong các lĩnh vực như mạng máy tính, quản trị hệ thống mạng hay lập trình ứng dụng<br>cho mạng.<br><strong>Nội dung môn học:</strong><br>Giúp sinh viên hiểu và nắm được các kiến thức cơ bản như sau:<br>• Chương 1: Giới thiệu về tổng quan về mạng máy tính và các ứng dụng của mạng máy<br>tính trong thực tế. Phân loại các loại hình mạng máy tính và các hệ điều hành mạng.<br>Giới thiệu sơ lược về chồng giao thức OSI và TCP/IP.<br>• Chương 2, 3, 4, 5: Giới thiệu chi tiết chức năng và dịch vụ các tầng ứng dụng, giao<br>vận, mạng, liên kế dữ liệu. Nội dung giảng dạy trong những chương này theo các tiếp<br>cận theo kiểu top-down sẽ giúp sinh viên dễ dành trong việc tiếp thu kiến thức về mạng.<br>• Chương 6: Giới thiệu về vấn đề an toàn mạng và vấn đề cơ bản như các phương pháp<br>mã hóa, các phương thức chứng thực, hiểu một số kiểu tấn công mạng và các phòng<br>chống để đảm bảo vấn đề toàn vẹn dữ liệu mạng.</p><p><strong>Tài liệu học tập:</strong><br>1. J.F. Kurose, Computer Networking, A Top-Down Approach Featuring the<br>Internet, 3rd edition, Addison Wesley, 2004.<br>2. Fred Halsall, Computer Networking and the Internet, Fifth edition, addisonwesley, 2005<br>3. Nguyễn Thúc Hải, Mạng máy tính và các hệ thống mở, NXB Giáo Dục, 1997.<br>4. Andrew S. Tanenbaum, Computer Network, 4th edition, Prentice Hall, 2003.</p>\"},{\"id\":389641023067,\"name\":\"Cơ sở dữ liệu\",\"description\":\"<p>Mã môn học: MTH10312<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các khái niệm cơ bản về cơ sở dữ liệu; các mô hình dữ liệu; các<br>tiêu chuẩn về thiết kế cơ sở dữ liệu và các dạng chuẩn.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản về cơ sở dữ liệu.<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Các phép toán trên mô hình dữ liệu quan hệ.<br>- Ngôn ngữ truy vấn SQL.<br>- Các dạng chuẩn và thuận toán chuẩn hóa cơ sở dữ liệu.</p><p><strong>Tài liệu học tập:</strong><br>[1] R. Elsmari, S. Navathe. Fundamentals of Database Systems 4th edition, Addison<br>Wesley, 2004.<br>[2] Nguyễn Kim Anh. Nguyên lý của các hệ cơ sở dữ liệu, NXB ĐHQG Hà Nội, 2004.</p>\"},{\"id\":460804601287,\"name\":\"Lập trình .Net\",\"description\":\"<p>Mã học phần: MTH10310<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Lập trình Hướng Đối Tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình căn bản.<br><strong>Mục tiêu môn học</strong><br>Phát triển kỹ năng lập trình ứng dụng với C# trên môi trường lập trình tích hợp Visual<br>Studio .NET.<br><strong>Mô tả môn học</strong><br>Sử dụng môi trường Visual Studio 2005: thiết kế giao diện, hiện thực tác vụ của ứng dụng,<br>tạo các thành phần phần mềm… dựa trên .NET, kiểm tra bẩy lỗi, xữ lý lỗi, và triển khai<br>ứng dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đề cương Lập Trình .Net của khoa Công Nghê Thông Tin – Đại Học Sư Phạm Hà<br>Nội.<br>[2] Đề cương Lập Trình .Net của khoa Công Nghê Thông Tin – Đại Học Bán Công Tôn<br>Đức Thắng.<br>[3] Developing Window Based Applications With Microsoft.Net MCAD_MCSD<br>[4] Course Microsoft 70-316</p>\"},{\"id\":294476861273,\"name\":\"Lập trình Java\",\"description\":\"<p>Mã học phần: MTH10316<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 03 (2LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt): Lập trình<br>hướng đối tượng.<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có): Kỹ năng lập trình.<br><strong>Mô tả môn học</strong><br>Trong học phần này sinh viên sẽ học được kiến thức tổng quan về công nghệ phát triển<br>phần mềm bằng Java. Trong chương 2, sinh viên sẽ học thuần túy về ngôn ngữ lập trình<br>Java như các kiểu dữ liệu, từ khóa, biến, toán tử, cấu trúc điều khiển, vòng lập … tương<br>tự như ngôn ngữ lập trình C.<br>- Chương 3, sẽ giới thiệu lại các khái niệm và cách tạo ra lớp (cả lớp nội, lớp trừu tượng),<br>đối tượng và các đặc tính trong lập trình hướng đối tượng như tính kế thừa, tính đa hình.<br>Đặt biệt giới thiệu một khái niệm về lập trình “giao diện - interface” để giải quyết vấn<br>đề kế thừa bội (rắc rối gặp phải khi dùng ngôn ngữ C/C++ để lập trình hướng đối<br>tượng). Bên cạnh đó sẽ giới thiệu một số lớp có sẵn được cung cấp bởi Sun.<br>- Chương 4, giới thiệu về cơ chế quản lý lỗi và thu gom rác trong Java. Sinh viên sẽ được<br>học về khái niệm “biệt lệ - exception”, tạo biệt lệ và cách sử dụng chúng trong lập trình.<br>Hiểu rõ cơ chế “try - catch - finally” và “ném – throws” lỗi.<br>- Chương 5, giới thiệu về cách làm việc với các dòng – stream dữ liệu. Sinh viên sẽ tìm<br>hiểu về cách làm việc với các dòng nhập xuất ký tự, nhị phân được cung cấp trong gói<br>java.io. Tìm hiểu về lớp System và làm việc với các dòng nhập xuất chuẩn.<br>- Chương 6,7,8 giới thiệu về các tao dựng giao diện sử dụng bằng đồ họa được cung cấp<br>trong bộ thư viện AWT. Sinh viên sẽ được học về cách sử lý các sự kiện xảy ra trên<br>chuột, bàn phím để cho phép người dùng có thể điều khiển các chức năng trên giao diện<br>phần mềm.<br>- Chương 9, giới thiệu về khái niệm luồng, các lập trình đa luồng nói chung và trong Java<br>nói riêng. Sinh viên sẽ có kỹ năng làm việc với luồng – Thread trong Java như thiết lập<br>trạng thái, độ ưu tiên và đồ bộ dữ liệu giữa các luồng. Vấn đề Deadlock, cơ chế Chờnhận biết.<br>Mục tiêu môn học<br>Sinh viên sẽ có được các kiến thức và kỹ năng cơ bản đến nâng cao về lập trình Java<br>bao gồm: lập trình cơ bản, lập trình hướng đối tượng, lập trình giao diện. Từ đó sinh<br>viên có thể tìm hiểu chuyên sâu hơn về công nghệ J2EE như lập trình ứng dụng cho môi<br>trường mạng, web, lập trình với CSDL, lập trình phân tán … Đây là những kiến thức<br>cần thiết để sinh viên có thể trở thành chuyên gia lập trình về công nghệ J2EE trong<br>tương lai.</p><p><strong>Tài liệu học tập:</strong><br>1. James Gosling - The Java Language Specification - 2005 (3rd edition)<br>2. Ken Arnold, James Gosling - The Java Programming Language<br>3. Trần Tiến Dũng - Giáo trình lý thuyết và bài tập Java - 1999</p>\"},{\"id\":148847083136,\"name\":\"Thiết kế Web\",\"description\":\"<p>Mã môn học: MTH10333<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Nhập môn lập trình<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản<br><strong>Nội dung môn học:</strong><br>- Kiến trúc ứng dụng web<br>- Quy trình thiết kế web<br>- Các ngôn ngữ thiết kế web (client)<br>- Các công cụ đồ họa, framework xây dựng trang web<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Môn học cung cấp những kiến thức cơ bản trong lĩnh vực thiết kế<br>website. Ứng dụng các công cụ thiết kế đồ họa cho trang web.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững kiến trúc ứng dụng web, quy trình thiết kế website, các<br>công cụ và ngôn ngữ xây dựng trang web<br>- Kỹ năng: khảo sát, phân tích, thiết kế, xây dựng giao diện và triển khai trang<br>web<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Nguyễn Quang Hải, Nhập môn HTML và CSS, 2014<br>2. Frank Boumphrey, HTML5 for dummies: elearning kit, 2012<br>3. Các tài liệu thiết kế web nguồn mở, 2010, <a href=\\\"http://www.w3schools.com/\\\">http://www.w3schools.com</a></p>\"},{\"id\":684458228929,\"name\":\"Nhập môn LT Di Động\",\"description\":\"\"},{\"id\":139261203630,\"name\":\"LTW ASP.NET\",\"description\":\"<p>Mã môn học: MTH10345<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Thiết kế web, Cơ sở dữ liệu, Lập trình Hướng Đối Tượng<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Thiết kế và xây dựng ứng dụng web bằng ASP.NET.<br>Xây dựng các dịch vụ web trên .NET<br>Sử dụng các framework hiện đại trên .NET để phát triền ứng dụng web<br>Triển khai ứng dụng web.<br>Bảo trì ứng dụng web<br>Mục tiêu môn học:<br>Mục tiêu chung: Cung cấp cho sinh viên các kiến thức để xây dựng và phát triển các<br>ứng dụng web bằng công nghệ ASP.NET:<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững kiến thức xây dựng ứng dụng web<br>- Kỹ năng: khảo sát, phân tích, thiết kế, xây dựng, triển khai và bảo trì website<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Nguyễn Ngọc Bình Phương, Các giải pháp lập trình <a href=\\\"http://asp.net/\\\">ASP.NET</a> 2.0: tập I, 2007<br>2. Building <a href=\\\"http://asp.net/\\\">ASP.NET</a> application for the web: class notes - ASP0018D, 2010<br>3. Các bài giảng mã nguồn chính thức của Microsoft về công nghệ <a href=\\\"http://asp.net/\\\">ASP.NET</a>, 2010,<br><a href=\\\"http://www.asp.net/\\\">http://www.asp.net/</a></p>\"},{\"id\":890758012431,\"name\":\"LTW J2EE\",\"description\":\"\"},{\"id\":635288036081,\"name\":\"PT TK HTTT\",\"description\":\"<p>Mã học phần: MTH10315<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Cơ sở dữ liệu, Lập trình hướng đối tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: phương pháp luận hướng đối tượng, thiết kế<br>cơ sở dữ liệu.<br><strong>Mô tả môn học:</strong><br>- Các khái niệm cơ bản về hệ thống.<br>- Phương pháp khảo sát hiện trạng và tìm hiểu nhu cầu.<br>- Phương pháp phân tích có cấu trúc (SA).<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Phương pháp SART.<br>- Phương pháp SD.<br><strong>Mục tiêu môn học:</strong> Giới thiệu cho sinh viên các phương pháp phân tích và thiết kế kinh<br>điển sử dụng trong việc phân tích chức năng, phân tích dữ liệu, phân tích động thái và<br>thiết kế hệ thống.</p><p><strong>Tài liệu học tập:</strong><br>[1] Nguyễn Văn Ba. Phân tích và thiết kế hệ thống thông tin, NXB ĐHQG Hà Nội,<br>2005.<br>[2] Hawryczkiewycz I. T.. Introduction to System Analysis and Design, Prentice Hall<br>New Delhi, 1989.</p>\"},{\"id\":739125317372,\"name\":\"Kiến trúc Lập trình di động\",\"description\":\"<p>Mã môn học: MTH10355<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>• Lập trình bằng ngôn ngữ máy tính hiện đại.<br>• Khóa học đầu tiên về kiến trúc máy tính<br>• Truy cập vào điện thoại thông minh hoặc máy tính bảng cá nhân<br><strong>Nội dung môn học:</strong><br>- Trong những năm gần đây, các thiết bị di động mới, nhỏ và nhẹ nhưng thông minh hơn và<br>các hệ thống nhúng, chẳng hạn như điện thoại thông minh, thiết bị máy tính bảng và Internet di<br>động khác thiết bị đã trở nên phổ biến. Những thiết bị này đưa ra những thách thức mới trong<br>lập trình, bảo mật và quản lý dữ liệu. Môn học này sẽ giới thiệu các khái niệm và vấn đề cơ bản<br>trong lập trình thiết bị di động; kiến trúc thiết bị di động; cơ sở hạ tầng cần thiết để hỗ trợ các<br>thiết bị đó; vấn đề quản lý năng lượng; và bảo mật thông tin, dữ liệu các vấn đề về quản lý và<br>quyền riêng tư. Học sinh sẽ khám phá những chủ đề này thông qua thực hành kinh nghiệm lập<br>trình với các thiết bị.<br>- Khóa học chủ yếu bao gồm các bài giảng trực tuyến và bài tập về nhà sử dụng hai hoặc<br>nhiều nền tảng di động phổ biến hơn như Android của Google (một số điện thoại thông minh<br>và bây giờ là máy tính bảng), iOS của Apple (iPhone, iPod Touch và iPad), Windows của<br>Microsoft Phone 7 (điện thoại thông minh) và Nghiên cứu hệ điều hành Blackberry của Motion<br>(điện thoại thông minh và máy tính bảng).<br><strong>Mục tiêu môn học:</strong><br>- Đối với khóa học này, thế giới điện toán hiện đại được định nghĩa là thế giới dựa trên thiết bị di<br>động thiết bị (ví dụ: điện thoại thông minh, netbook, máy tính bảng, v.v.) làm công nghệ<br>điện toán cơ bản các đơn vị dựa vào bộ nhớ đám mây (ví dụ: Google Apps, Flickr/Picasa,<br>Facebook, sao lưu tệp các hệ thống như Carbonite, và tương tự).</p><p><strong>Tài liệu học tập:</strong><br>Text: Jochen Schiller – Mobile Communications, 2nd edition, ISBN-13: 007-<br>6092019329.</p>\"}]},{\"id\":767545267931,\"name\":\"Quản trị mạng\",\"description\":\"<p>Cung cấp kiến thức chuyên sâu về quản lý và bảo trì hệ thống mạngmáy tính, từ cơ sở dữ liệu đến an ninh mạng, nhằm đảm bảo hoạt động liên tục và an toàn của các hệ thống thông tin.</p><p>Kỹ năng đầu ra:</p><p>Các công việc liên quan:</p><p>Cấu trúc các học phần:</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1718/418;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.35.41.png\\\" width=\\\"1718\\\" height=\\\"418\\\"></figure>\",\"categories\":[{\"id\":961608659498,\"name\":\"Cấu trúc dữ liệu\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br>Mục tiêu của môn học<br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":696585696662,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":986281387932,\"name\":\"Mạng máy tính\",\"description\":\"<p>Mã môn học: MTH10311<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Trang bị cho người học những kiến thức, kỹ năng về:<br>- Có kiến thức cơ bản về mạng máy tính như: phân biệt được các loại hình mạng, nắm<br>được cơ sở truyền thông và truyền dữ liệu trong mạng, nắm được chức năng và dịch vụ<br>hoạt động ở mỗi tầng trong mô hình OSI và TCP/IP.<br>- Sinh viên sẽ học được những kỹ năng sử dụng các công cụ phần mềm để bắt và phân<br>tích dữ liệu tại mỗi tầng. Sử dụng các dịch vụ phổ biến như web, chuyển tập tin, thư<br>điện tử, tên miền, tường lửa. Cấu hình thiết bị định tuyến.<br>Đây là những kiến thức và kỹ năng cơ bản để giúp sinh viên định hướng nghiên cứu<br>trong các lĩnh vực như mạng máy tính, quản trị hệ thống mạng hay lập trình ứng dụng<br>cho mạng.<br><strong>Nội dung môn học:</strong><br>Giúp sinh viên hiểu và nắm được các kiến thức cơ bản như sau:<br>• Chương 1: Giới thiệu về tổng quan về mạng máy tính và các ứng dụng của mạng máy<br>tính trong thực tế. Phân loại các loại hình mạng máy tính và các hệ điều hành mạng.<br>Giới thiệu sơ lược về chồng giao thức OSI và TCP/IP.<br>• Chương 2, 3, 4, 5: Giới thiệu chi tiết chức năng và dịch vụ các tầng ứng dụng, giao<br>vận, mạng, liên kế dữ liệu. Nội dung giảng dạy trong những chương này theo các tiếp<br>cận theo kiểu top-down sẽ giúp sinh viên dễ dành trong việc tiếp thu kiến thức về mạng.<br>• Chương 6: Giới thiệu về vấn đề an toàn mạng và vấn đề cơ bản như các phương pháp<br>mã hóa, các phương thức chứng thực, hiểu một số kiểu tấn công mạng và các phòng<br>chống để đảm bảo vấn đề toàn vẹn dữ liệu mạng.</p><p><strong>Tài liệu học tập:</strong><br>1. J.F. Kurose, Computer Networking, A Top-Down Approach Featuring the<br>Internet, 3rd edition, Addison Wesley, 2004.<br>2. Fred Halsall, Computer Networking and the Internet, Fifth edition, addisonwesley, 2005<br>3. Nguyễn Thúc Hải, Mạng máy tính và các hệ thống mở, NXB Giáo Dục, 1997.<br>4. Andrew S. Tanenbaum, Computer Network, 4th edition, Prentice Hall, 2003.</p>\"},{\"id\":675029548908,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":753227684706,\"name\":\"Thiết kế mạng\",\"description\":\"<p>Mã môn học: MTH10335<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Mạng máy tính<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV:<br><strong>Nội dung môn học:</strong><br>Tiến trình xây dựng một mạng máy tính và các vấn đề cần quan tâm.<br>Thiết kế mạng LAN<br>Thiết kế mạng WAN<br>Cơ bản về bảo mật mạng<br>Mục tiêu môn học:<br>Mục tiêu chung: Môn học cung cấp những kiến thức cơ bản trong lĩnh vực thiết kế, xây<br>dựng mạng<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững quy trình thiết kế mạng LAN, WAN<br>- Kỹ năng: khảo sát, phân tích, thiết kế, xây dựng, triển khai và bảo trì mạng<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Diane Teara - Catherine Paquet, Campus network design fundamentals, 2006<br>2. Khương Anh, Giáo trình hệ thống mạng máy tính CCNA 1,2,3,4, 2005<br>3. James F.Kurose - Keith W.Ross, Computer networking: a top-down approach</p>\"},{\"id\":584062812562,\"name\":\"HĐH Unix\",\"description\":\"<p>Mã môn học: MTH10313<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Tin học cơ sở<br>- Học phần học trước: Nhập môn lập trình<br>- Học phần song hành: Mạng máy tính<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có<br><strong>Mục tiêu môn học</strong><br>Giúp cho sinh viên có những kiến thức tổng quan về hệ điều hành máy tính nói chung<br>và chuyên sâu về hệ điều hành Unix/Linux nói riêng. Sinh viên sẽ được trang bị những<br>kỹ năng về cài đặt hệ điều hành, cài đặt/biên dịch gói phần mềm, sử dụng các tiện ích và<br>lập trình (shell, hệ thống) trên hệ điều hành Unix/Linux. Môn học sẽ định hướng cho<br>sinh viên theo đuổi nghề quản trị hệ thống mạng hoặc lập trình phần mềm cho các hệ<br>thống nhúng.<br><strong>Nội dung môn học:</strong><br>- Giúp sinh viên hiểu và nắm được các kiến thức cơ bản như sau:<br>- Quá trình cài đặt HĐH Unix/Linux.<br>- Sử dụng Unix/Linux để phục vụ người dùng cuối.<br>- Tổng quan về kiến trúc bên ngoài của Unix/Linux.<br>- Các chương trình tiện ích cơ bản trong Unix/Linux.<br>- Cài đặt/biên dịch các gói phần mềm trong Unix/Linux.<br>- Quản trị hệ thống máy tính.<br>- Sử dụng Unix/Linux để phục vụ người lập trình Shell:<br>- Thực hiện các công việc cơ bản bằng Shells. Giới thiệu các chủ đề và tương ứng là các<br>tập lệnh sử dụng.<br>- Lập trình Shells. Làm việc với ngôn ngữ lập trình Shells Script và xây dựng các chương<br>trình ứng dụng.<br>- Sử dụng Unix/Linux để phục vụ người lập trình hệ thống (lập trình C):<br>- Sơ lược về cách xấy dựng và biên dịch các chương trình C trong Unix/Linux.<br>- Tổng quan về kiến trúc bên trong của HĐH Unix/Linux (Unix/Linux Internal).<br>- Giới thiệu các thành phần HĐH như hệ thống tập tin, Quản lý tiến trình và các viết các<br>chương trình minh họa để hiểu rõ hơn về nguyên tắc làm việc của HĐH.</p><p><strong>Tài liệu học tập:</strong><br>1. Graham Glass, King Ables, Linux for Programmers and Users, Prentice Hall,<br>2006.<br>2. Bill McCarty, Learning Red Hat Linux, 3rd Edition, O\'Reilly, 2003.<br>3. Brian Ward, How Linux Works: What Every Super-User Should Know, No<br>Starch Press, 2004.<br>4. Matthias Kalle Dalheimer, Matt Welsh, Running Linux, 5th Edition, O\'Reilly,<br>2005.<br>5. Stephen G. Kochan, Patrick Wood, Unix® Shell Programming, Third Edition,<br>Sams Publishing, 2003<br>6. Cameron Newham, Learning the bash Shell, 3rd Edition, O\'Reilly, 2005.<br>7. Sams Publishing, Sams Teach Yourself Shell Programming in 24 Hour, 1999.<br>8. Ellie Quigley, UNIX® Shells by Example Fourth Edition, Prentice Hall PTR,<br>2004<br>9. Richard Stevens, Sam Advanced Unix Programming, Sams Publishing, 1999.<br>10. Neil Matthew, Richard Stones, Beginning Linux® Programming, 4th<br>Edition, Wiley Publishing Inc., 2008.</p>\"},{\"id\":901300076960,\"name\":\"Cơ sở dữ liệu\",\"description\":\"<p>Mã môn học: MTH10312<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các khái niệm cơ bản về cơ sở dữ liệu; các mô hình dữ liệu; các<br>tiêu chuẩn về thiết kế cơ sở dữ liệu và các dạng chuẩn.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản về cơ sở dữ liệu.<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Các phép toán trên mô hình dữ liệu quan hệ.<br>- Ngôn ngữ truy vấn SQL.<br>- Các dạng chuẩn và thuận toán chuẩn hóa cơ sở dữ liệu.</p><p><strong>Tài liệu học tập:</strong><br>[1] R. Elsmari, S. Navathe. Fundamentals of Database Systems 4th edition, Addison<br>Wesley, 2004.<br>[2] Nguyễn Kim Anh. Nguyên lý của các hệ cơ sở dữ liệu, NXB ĐHQG Hà Nội, 2004.</p>\"},{\"id\":268389445054,\"name\":\"Quản trị HT mạng\",\"description\":\"<p>Mã học phần: MTH10309<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (2LT + 2TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Mạng máy tính, Hệ điều hành mạng Unix/Linux<br>Học phần học trước: Cơ sở dữ liệu, Hệ điều hành mạng.<br>Học phần song hành: Không có.<br>Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu học phần</strong><br>Cung cấp những kiến thức về dịch vụ mạng và các nguyên tắc quản trị một hệ thống<br>mạng nói chung. Trang bị cho sinh viên kỹ năng cài đặt và cấu hình thuần thục các dịch<br>vụ mạng trên môi trường máy chủ Unix/Linux. Sau khi học, sinh viên có thể đảm nhiệm<br>được một mảng công việc quản trị mạng máy chủ Unix/Linux trong những công ty lớn<br>hoặc quản trị toàn bộ hệ thống mạng Unix/Linux cho một doanh nghiệp vừa và nhỏ.<br><strong>Nội dung chi tiết học phần</strong><br>Chương 1: Trong học phần này sẽ giúp sinh viên có những kiến thức chuyên sâu hơn<br>trên môi trường hệ điều hành Unix/Linux. Sinh viên biết cách phân vùng và cài đặt một<br>máy chủ dùng hệ điều hành Unix/Linux để hoạt động hiểu quả nhất. Bên cạnh đó, sinh<br>viên sẽ được học về phương pháp quản lý và cài đặt gói phần mềm. Đồng thời học cách<br>biên dịch và tối ưu tính năng phần mềm từ mã nguồn.<br>Chương 2: Tập trung vào các nguyên tắc và phương thức quản trị người dùng. Quản trị<br>cơ chế khởi động và tắt hệ thống. Quản trị hệ thống tập tin và quản trị một số dịch vụ cơ<br>bản. Quản lý nhật ký hệ thống. Thiết lập cơ chế sao lưu dữ liệu tự động.<br>Chương 3: Tập trung vào cấu hình thông số mạng, tìm hiểu một số lệnh để tra cứu thông<br>tin mạng, sau đó sẽ tìm hiểu những kiến thức bảo mật mạng bằng tường lửa.<br>Chương 4: Tìm hiểu vai trò của các dịch vụ trên internet. Tiến hành tìm hiểu và thử<br>nghiệm các phần mềm như bind, vsftp, apache, postfix, courier, openssh.<br>Chương 5: Tìm hiểu vai trò của các dịch vụ trên intranet để thiết lập mạng Workgroup,<br>mạng Domain Controller để quản lý và chia sẽ tài nguyên trong mạng nội bộ. Quản lý<br>truy cập tài nguyên internet. Tiến hành tìm hiểu và thử nghiệm các phần mềm samba,<br>dhcp, squid.<br>Chương 6: Giới thiệu dịch vụ thư mục, cài đặt cấu hình và chuẩn bị cơ sở dữ liệu mẫu<br>cho dịch vụ thư mục. Sau đó tiến hành tích hợp các dịch vụ trên dịch vụ thư mục LDAP.</p><p><strong>Tài liệu học tập:</strong><br>[1] Lars Wirzenius, Joanna Oja, Unix/Linux System Administrators Guide.<br>[2] Matt Welsh, Unix/Linux Installation and Getting Started.<br>[3] Steve Frampton, Unix/Linux System Adminstration Made Easy.<br>[4] Olaf Kirch, Terry Dawson, Unix/Linux Network Administrator\'s Guide, O\'Reilly,<br>2000<br>[5] Matthias Kalle Dalheimer, Matt Welsh, Running Unix/Linux, Fifth Edition,<br>O’Reilly, 2005<br>[6] Kho tài liệu về Unix/Linux, <a href=\\\"http://www.tldp.org/\\\">http://www.tldp.org/</a></p>\"},{\"id\":229921286579,\"name\":\"PT TK HTTT\",\"description\":\"<p>Mã học phần: MTH10315<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần học trước hoặc tiên quyết: Cơ sở dữ liệu, Lập trình hướng đối tượng.<br>Học phần song hành: không.<br>Các yêu cầu về kiến thức, kỹ năng của SV: phương pháp luận hướng đối tượng, thiết kế<br>cơ sở dữ liệu.<br><strong>Mô tả môn học:</strong><br>- Các khái niệm cơ bản về hệ thống.<br>- Phương pháp khảo sát hiện trạng và tìm hiểu nhu cầu.<br>- Phương pháp phân tích có cấu trúc (SA).<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Phương pháp SART.<br>- Phương pháp SD.<br><strong>Mục tiêu môn học:</strong> Giới thiệu cho sinh viên các phương pháp phân tích và thiết kế kinh<br>điển sử dụng trong việc phân tích chức năng, phân tích dữ liệu, phân tích động thái và<br>thiết kế hệ thống.</p><p><strong>Tài liệu học tập:</strong><br>[1] Nguyễn Văn Ba. Phân tích và thiết kế hệ thống thông tin, NXB ĐHQG Hà Nội,<br>2005.<br>[2] Hawryczkiewycz I. T.. Introduction to System Analysis and Design, Prentice Hall<br>New Delhi, 1989.</p>\"},{\"id\":538822017740,\"name\":\"Bảo mật HTM\",\"description\":\"\"},{\"id\":241574705791,\"name\":\"Chuyên đề mạng\",\"description\":\"\"}]}]', 8, '2024-07-16 08:30:47', '2024-07-25 05:57:33');
INSERT INTO `majors` (`id`, `name`, `subjects`, `department_id`, `created_at`, `updated_at`) VALUES
(3, 'Chuyên ngành giải tích', '[{\"id\":328983579602,\"name\":\"Sơ đồ cấu trúc các học phần\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1252/1042;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 07.23.06.png\\\" width=\\\"1252\\\" height=\\\"1042\\\"></figure>\",\"categories\":[{\"id\":852009687631,\"name\":\"GIải tích 1A\",\"description\":\"<p><strong>Mã học phần : MTH00010 và MTH00011 (</strong>môn đại cương, học kỳ 1 năm 1, ngành đào tạo: Toán - Tin học<strong>)</strong></p><p><strong>Số tín chỉ&nbsp; mỗi môn: 3 tín chỉ (2 Lý thuyết + 1 Bài tập).</strong></p><p><strong>Thời khóa biểu</strong> : Học kỳ 1, năm 2020-2021, CS Linh Trung.</p><p><strong>Lớp: 20TTH1</strong></p><p><strong>Lịch học: Thứ Tư, 12g30-16g00, E203.</strong></p><p><strong>Thời lượng tổng hai môn: </strong>Gồm 15 bài giảng, 4 tiết / 1 bài giảng, không bao gồm tiết bài tập.</p><p>Thời gian tự học: ít nhất 5 tiếng/ tuần.</p><p><strong>Lịch tiếp sinh viên</strong>:&nbsp;</p><p>Tại Linh Trung: 12g10-12g30 thứ 4, tại phòng GV, nhà D, CS Linh Trung.</p><p>Tại Nguyễn Văn Cừ: 14g10 đến 15g00 thứ 5, tại bộ môn Giải tích, E201A, 227, Nguyễn Văn Cừ, Quận 5.</p><p>Trong cả 2 trường hợp, sinh viên phải hẹn và gửi câu hỏi trước qua mail ít nhất 1 ngày:&nbsp;</p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;lkha@hcmus.edu.vn.</strong></p><p>Hay đối với sinh viên cư trú tại các quận trung tâm Tp. HCM có thể gửi mail hẹn gặp trao đổi tại bộ môn Giải tích, CS Nguyễn Văn Cừ, quận 5.</p><p><strong>Nội dung chính</strong> : Học phần bao gồm các kiến thức cơ bản về tập hợp, suy diễn cơ bản, ánh xạ, số thực, dãy và chuỗi số thực. Hàm một biến thực liên tục. Ứng dụng của các hàm liên tục trong thực tế. Nền tảng của phép tính vi phân, tích phân và ứng dụng.</p><p><strong>Mục tiêu môn học: </strong>Mục tiêu của học phần là trang bị cho sinh viên các kiến thức cơ bản về giải tích hàm thực một biến, làm nền tảng cho các học phần chuyên ngành. Môn học này cũng là môn đầu tiên dạy sinh viên cách lập luận chặt chẽ toán học, không đi sâu vào tính toán hình thức.</p><p><strong>Tài liệu học tập bắt buộc</strong>:&nbsp;</p><p>[1] Dương Minh Đức, Giáo trình Giải tích 1. Nhà xuất bản Thống Kê, Tp Hồ Chí Minh, 2006.&nbsp;</p><p>(có trên thư viện trường và quầy sách Linh trung).&nbsp;</p><p>Bài giảng dựa trên các slides soạn theo quyển sách “Toán Giải Tích” của GS.TS. Dương Minh Đức, Nhà xuất bản Thống Kê 2005. Các slides này được để trên webpage<a href=\\\"https://sites.google.com/view/dmduc-giangday/\\\"> https://sites.google.com/view/dmduc-giangday/</a> và được photocopy để sinh viên đọc trước khi nghe bài giảng.</p><p><i><strong>Tài liệu tham khảo thêm</strong></i></p><p>[2] Walter Rudin, Principles of mathematical analysis, McGraw-Hill, New York, 1964. ( Baby Rudin)</p><p>[3] James Stewart, Calculus, Early Transcendentals, 7 th edition, Brooks Cole, 2018.</p><p>[4] Terence Tao, Analysis 1, Springer, third edition, 2016.</p><p><strong>Hình thức thi:</strong> cho môn Giải tích 1a: Điểm cuối kỳ (10 điểm) = Bài tập&nbsp; (2 điểm) + bài thi giữa kỳ (3 điểm) + điểm thi viết cuối kỳ theo lịch trường (5 điểm)&nbsp; (+ điểm cộng tối đa là 1) .</p><p>Môn Vi tích phân 1a: Bài tập (2₫)+ điểm thi viết cuối kỳ (8₫) theo lịch trường (+ điểm cộng toi đa là 1).</p>\"},{\"id\":392695598800,\"name\":\"Giải tích 3\",\"description\":\"\"},{\"id\":242361381977,\"name\":\"Giải tích 4\",\"description\":\"\"},{\"id\":870172762743,\"name\":\"Giải tích hàm\",\"description\":\"\"},{\"id\":973788408339,\"name\":\"Hàm biến phức\",\"description\":\"<p><strong>Mã học phần : MTH10412 (bắt buộc riêng chuyên ngành Giải tích, tự chọn cho các chuyên ngành khác)</strong></p><p><strong>Số tín chỉ : 4 tín chỉ (Lý thuyết + Bài tập).</strong></p><p><strong>Thời khóa biểu</strong> : Học kỳ 2, năm 2019-2020, CS Linh Trung:</p><p><strong>Chính qui</strong>+<strong>Cử Nhân Tài Năng:&nbsp; 12g30 - 16g00, thứ 4, NĐH 7.2.</strong></p><p><strong>Thời lượng: </strong>Gồm 15 bài giảng, 3 tiết lý thuyết+1 tiết bài tập/ 1 bài giảng.&nbsp;</p><p>Thời gian tự học: ít nhất 3 tiếng/ tuần.</p><p><strong>Lịch tiếp sinh viên</strong> :&nbsp;</p><p>Tại Linh Trung: 12g10-12g30 thứ 4, tại phòng GV, nhà D, CS Linh Trung.</p><p>Tại Nguyễn Văn Cừ: 12g00 đến 12g30 thứ 5, tại bộ môn Giải tích, E201A, 227, Nguyễn Văn Cừ, Quận 5.</p><p>Trong cả 2 trường hợp, sinh viên phải hẹn và gửi câu hỏi trước qua mail ít nhất 1 ngày:&nbsp;</p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;lkha@hcmus.edu.vn.</strong></p><p>Hay đối với sinh viên cư trú tại các quận trung tâm Tp. HCM có thể gửi mail hẹn gặp trao đổi tại bộ môn Giải tích, CS Nguyễn Văn Cừ, quận 5.</p><p><strong>Nội dung chính</strong> : Học phần 4 tín chỉ này bao gồm toàn bộ nội dung cơ bản nhất của giải tích phức 1 biến: Số phức (đại số, hình học và topo trên mặt phẳng phức), hàm chỉnh hình 1 biến phức (định nghĩa, ví dụ, tính chất,...), Lý thuyết tích phân Cauchy trên mặt phẳng phức và các hệ quả của Công thức tích phân Cauchy, Lý thuyết chuỗi số phức (miền hội tụ) và chuỗi hàm phức (Chuỗi Taylor, chuỗi Laurentz), Lý thuyết thặng dư Cauchy và ứng dụng trong tính toán các tích phân thực (tích phân Riemann, tích phân suy rộng, tích phân giá trị chính Cauchy), một nhập môn về phương trình Cauchy-Riemann một chiều (nếu còn thời gian).</p><p><strong>Mục tiêu môn học: </strong>kết thúc môn hôc, sinh viên cần có khả năng:</p><p>- Phân tích về định tính, định lượng của hàm phức một biến bằng các phương pháp: khai triển chuỗi, tích phân Cauchy, hình học của hàm và kỹ thuật phương trình đạo hàm riêng.</p><p>- Giải thích những định lý quan trọng phân biệt giải tích phức với giải tích thực.</p><p>- Áp dụng phương pháp giải tích phức trong các lĩnh vực khác và tính toán vài tích phân thực cơ bản.</p><p><strong>Môn học bắt buộc trước đó</strong>: Giải tích 1,2,3 và Đại số tuyến tính.</p><p><strong>Các môn học có thể học sau môn này:&nbsp; </strong>Giải tích thực, Phương trình đạo hàm riêng, Lý thuyết hàm phức nhiều biến, Giải tích phức và mặt Riemann, Giải tích Fourier, Biến đổi tích phân, Hình học đại số, Lý thuyết số giải tích, Cơ học.</p><p><strong>Tài liệu học tập bắt buộc</strong>:&nbsp;</p><p>[1]Theodore Gamelin, \\\"<i>Complex Analysis</i>\\\", 2001, Springer.&nbsp;</p><p>(Dạng giấy : tìm tại Bộ môn Giải tích. Dạng ebook : bên dưới cùng của trang.)</p><p><strong>Tham khảo thêm</strong> :</p><p>[2] Stephen D. Fisher, \\\"<i>Complex Variables</i>\\\", Second edition, 2012.(đây là tài liệu tôi dùng dạy Hệ Cao học Giải tích năm 2014.)&nbsp;</p><p>[3] David Wunsch, \\\"<i>Complex variables with Applications</i>\\\", Third edition, 2004. (đây là tài liệu có nhiều ứng dụng vào Vật lý và Điện-Điện tử.) .</p><p>[4] Walter Rudin, \\\"<i>Real and Complex analysis</i>\\\", Third edition, 1986. (đây là tài liệu chuyên sâu của lý thuyết này, khá hàn lâm.) .</p><p><strong>Hình thức thi</strong> : Điểm cuối kỳ (10 điểm) = Bài tập&nbsp; (2 điểm, có điểm danh) + bài thu hoạch giữa kỳ (3 điểm) + điểm thi viết cuối kỳ theo lịch trường (5 điểm) &nbsp; (+ điểm cộng tối đa là 1).</p><p>(i) Bài tập: Sẽ có bài tập về nhà cuối mỗi bài giảng (<strong>được đính kèm ở trang web này</strong>). Sinh viên cần làm hết và nộp lại vào đầu tiết của buổi học hôm sau. Nếu vì lý do bất khả kháng mà sinh viên không nộp vào buổi học này, thì bằng mọi cách sinh viên cần nộp tại Bộ môn Giải tích (Cơ Sở NVC) hay nộp qua mail trước khi buổi học tiếp theo diễn ra. Sinh viên có thể trao đổi với nhau, nhưng khi làm bài thì cần viết những lập luận riêng của bản thân. Các bài làm giống nhau sẽ bị điểm 0. Bài làm cuối cùng thì nộp cho một đại diện nào đó có thể đến CS NVC trước khi bắt đầu môn thi đầu tiên.</p><p><strong>(ii) Thi Giữa kỳ</strong>: Sau khi kết thúc Chương 2.</p><p>- Nội dung: Chương 1,2.</p><p>- Thời gian: <strong>từ......, nộp bài vào tiết học ngày ......&nbsp;</strong></p><p><strong>(iii) Thi cuốí kỳ:&nbsp;</strong></p><p>- tự luận, ĐƯỢC sử dụng tài liệu GIẤY, 120 phút,&nbsp;</p><p>- Nội dung: chương 1,2,4,5,6,7.</p><p><strong>Lưu ý:&nbsp;</strong></p><p>- điểm 10 không chỉ dành cho sinh viên giỏi, mà còn dành cho sinh viên siêng năng, nên khối lượng bài tập sẽ nhiều, nhưng không khó.</p><p>- S<strong>inh viên nghỉ quá 1/3 số buổi học (5 buổi trở lên)</strong> vẫn được tham gia mọi kỳ thi, nhưng sinh viên này sẽ không được điểm Bài tập , điểm cộng và các bài thi của sinh viên này sẽ được chấm chi tiết hơn bình thường.</p><p><strong>- Thi Cuối kỳ</strong>: thi viết tự luận, không dùng tài liệu hay công cụ tin học, thời gian 120 phút, có cả lý thuyết, hình vẽ và tính toán . Lập luận rõ ràng, không khẳng định cho có.</p><p>Nếu sinh viên nào không thể tham dự một trong các kỳ thi (có lý do chính đáng và có xác nhận), hãy liên hệ tôi biết trước kỳ thi đó ít nhất 1 tuần. Tôi sẽ có cách xử lý.</p><p>&nbsp;Các nội dung sau của Chương I đến Chương VII.</p><p>Complex numbers, polar form, complex multiplication, roots of complex numbers (much of this is review).</p><p>Stereographic projection .</p><p>Elementary functions, including power, root, exponential, logarithm, and trigonometric functions</p><p>Complex derivatives, basic rules of differentiation.</p><p>Cauchy-Riemann equations; inverse functions; harmonic functions; conformality; fractional linear transformations.</p><p>Review line integrals and Green\'s theorem; harmonic conjugates.</p><p>Complex line integrals, ML-estimate, fundamental theorem of complex calculus.Cauchy\'s theorem, Cauchy integral formulae, Liouville\'s theorem, Morera\'s theorem.</p><p>Weierstrass M-test, power series, radius of convergence, operations on power series, order of zeros.</p><p>Laurent decomposition, isolated singularities, orders of poles and zeros, partial fractions decomposition.</p><p>Residue theory, applications of residue calculus to evaluate integrals.</p>\"},{\"id\":805814831493,\"name\":\"PT toán lý\",\"description\":\"<p><strong>Chương 1. Phương trình vi phân tuyến tính cấp hai với hệ số hằng</strong></p><p>1.1. Các bài toán dao động</p><p>1.2. Tính duy nhất nghiệm của bài toán Cauchy</p><p>1.3. Cách giải phương trình vi phân tuyến tính cấp hai với hệ số hằng</p><p>1.4. Cách giải phương trình vi phân tuyến tính cấp hai với hệ số hằng liên quan tới các bài toán dao động.</p><p><strong>Chương 2. Các phương trình toán lý</strong></p><p>2.1. Mở đầu, các ví dụ.</p><p>2.2. Phương trình đạo hàm riêng tuyến tính; Các tính chất nghiệm.</p><p>2.3. Phân loại phương trình đạo hàm riêng tuyến tính cấp hai theo hai biến độc lập.</p><p>2.4. Thiết lập các bài toán cơ bản cho các phương trình đạo hàm riêng tuyến tính cấp hai.</p><p><strong>Chương 3. Phương trình sóng</strong></p><p>3.1. Thiết lập phương trình sóng</p><p>3.2. Nghiệm của bài toán Cauchy (Bài toán giá trị ban đầu) phương trình sóng cho dây vô hạn. Công thức D’Alembert.</p><p>3.3. Bài toán đặt đúng. Ví dụ của Hadamard về bài toán không chỉnh.</p><p>3.4. Dao động tự do của một sợi dây cố định tại hai đầu. Phương pháp tách biến Fourier.</p><p>3.5. Dao động cưỡng bức của một sợi dây cố định tại hai đầu.</p><p>3.6. Dao động cưỡng bức của một sợi dây có tại hai đầu không cố định.</p><p>3.7. Dao động cưỡng bức của một sợi dây có gắn lò xo ở hai đầu.</p><p>3.8. Sơ đồ tổng quát của phương pháp tách biến Fourier.</p><p>3.9. Tính duy nhất nghiệm của bài toán hỗn hợp.</p><p>3.10. Dao động của một màng tròn.</p><p>3.11. Áp dụng biến đổi Laplace để giải bài toán hỗn hợp.</p><p><strong>Chương 4. Phương trình nhiệt</strong></p><p>4.1. Thiết lập phương trình nhiệt</p><p>4.2. Nghiệm của bài toán Cauchy phương trình nhiệt</p><p>4.3. Truyền nhiệt trong thanh hữu hạn. Phương pháp tách biến Fourier.</p><p>4.4. Truyền nhiệt trong thanh hữu hạn với các loại điều kiện biên Dirichlet không thuần nhất, Robin không thuần nhất, Robin - Dirichlet không thuần nhất.</p><p><strong>Chương 5. Phương trình Laplace</strong></p><p>5.1. Thiết lập bài toán biên.</p><p>5.2. Nghiệm cơ bản của phương trình Laplace.</p><p>5.3. Công thức Green</p><p>5.4. Tính chất của hàm điều hòa.</p><p>5.5. Bài toán Dirichlet cho phương trình Laplace trong miền hình tròn. Phương pháp tách biến Fourier.</p><p>5.6. Tích phân Poisson.</p><p>5.7. Bài toán Dirichlet cho phương trình Laplace trong miền hình chữ nhật. Phương pháp tách biến Fourier.</p>\"},{\"id\":581049741264,\"name\":\"Phương trình ĐHR\",\"description\":\"<p><strong>Chương 1: Phương trình elliptic.</strong></p><p>1.1. Không gian Sobolev</p><p>1.2. Nghiệm suy rộng, Tính trơn của nghiệm.</p><p>1.3. Định lý Lax-Milgram</p><p>1.4. Bài toán gía trị riêng, véctơ riêng.</p><p>1.5. Nguyên lý cực đại.</p><p><strong>Chương 2: Phương trình parabolic.</strong></p><p>2.1. Không gian C(I;X)</p><p>2.2. Lý thuyết nửa nhóm</p><p>2.3. Sự tồn tại nghiệm của phương trình parabolic.</p><p>2.4. Nguyên lý cực đại.</p><p>2.5. Khai triển nghiệm theo véc-tơ riêng của toán tử Elliptic.</p><p><strong>Chương 3: Phương trình hyperbolic.</strong></p><p>3.1. Sự tồn tại nghiệm của phương trình Hyperbolic.</p><p>3.2. Khai triển nghiệm theo véc-tơ riêng của toán tử Elliptic.</p>\"},{\"id\":140260909232,\"name\":\"LT định tính PTVP\",\"description\":\"<p><strong>Chương 1: Các Định lý cơ bản tựa đề</strong></p><p>1.1 Bổ đề Gronwall- Bellman và mở rộng</p><p>1.2 Định lý về sai lệch nghiệm</p><p>1.3 Định lý về sự tồn tại và duy nhất nghiệm</p><p>1.4 Định lý về sự kéo dài của nghiệm</p><p>1.5 Định lý về sự phụ thuộc liên tục của nghiệm</p><p>1.6 Định lý về sự tính khả vi của nghiệm</p><p><strong>Chương 2: Các loại Phương trình Vi phân khác (số tiết 15)</strong></p><p>2.1 Phương trình Vi phân phụ thuộc tham số</p><p>2.2 Phương trình Vi phân Chậm</p><p>2.3 Phương trình Vi phân Ngẫu nhiên</p><p>2.4 Phương trình Vi phân Phức</p><p>2.5 Phương trình Vi phân trên Đa tạp</p><p>2.6 Phương trình Vi phân Đa trị</p><p>2.7 Phương trình Vi phân mờ</p><p><strong>Chương 3: Dáng điệu nghiệm (số tiết 10)</strong></p><p>3.1 Hệ động lực chất điểm</p><p>3.2 Ổn định</p><p>3.3 Rẽ nhánh</p><p>3.4 Tương đương Topo</p>\"},{\"id\":680979945558,\"name\":\"Giải tích PTHH\",\"description\":\"<p><strong>Chương 1: Khái niệm cơ bản (6 tiết)</strong></p><p>Giới thiệu tổng quan các khái niệm giải tích hàm: chuẩn, tích trong, không gian Sobolev, đạo hàm yếu, định lý Lax-Milgram.</p><p><strong>Chương 2: Lý thuyết xấp xĩ và nội suy (8 tiết)</strong></p><p>Giới thiệu các phương pháp xấp xỉ cho đạo hàm, tích phân và các phương pháp nội suy. Trình bày các bổ đề Bramble-Hilbert, Friedrichs, Cea... được sử dụng để đánh giá cá phương pháp xấp xĩ và nội suy trong không gian Sobolev.</p><p><strong>Chương 3: Phương pháp phần tử hữu hạn (8 tiết)</strong></p><p>Giới thiệu phương pháp phần tử hữu hạn dưới dạng xấp xĩ cho Ritz- Galerkin áp dụng cho bài toán biến phân. Trình bày các không gian của các hàm liên tục tuyến tính từng phần (continuous piecewise linear) trên các phần tử của lưới được chia trên miền của bài toán. Điều kiện lưới chuẩn và inverse assumption của lưới. Trình bày các đánh giá sai số giữa một hàm trong không gian Sobolev với nội suy của nó, và sai số gradient giữa hai hàm này trong đánh giá, được xét trên lưới chuẩn.</p><p><strong>Chương 4: Phương pháp phần tử hữu hạn cho bài toán khuyếch tán và đối lưu (8 tiết)</strong></p><p>Xem xét sự ổn định, tồn tại và duy nhất nghiệm xấp xỉ của phương pháp phần tử hữu hạn cho bài toán khuếch tán và đối lưu. Đánh giá sai số tiên nghiệm. Sử dụng Matlab để mô phỏng bài toán khuyếch tán và đối lưu bằng phương pháp phần tử hữu hạn.</p><p>&nbsp;</p><p><i><strong>Tôpô</strong></i></p>\"},{\"id\":626438271387,\"name\":\"Topo\",\"description\":\"<p><strong>Chương 1: Tập hợp vô hạn</strong></p><p>1.1 Lực lượng</p><p>1.2 Lực lượng của <strong>Z, Q, R</strong>. Sự không tồn tại lực lượng cực đại.</p><p>1.3 Tiên đề chọn</p><p><strong>Chương 2: Không gian tôpô</strong></p><p>2.1 Tôpô</p><p>2.2 Phần trong, bao đóng, biên</p><p>2.4 Cơ sở của tôpô</p><p><strong>Chương 3: Không gian con</strong></p><p>3.1 Tôpô của không gian con.</p><p><strong>Chương 4: Sự liên thông</strong></p><p>4.1 Liên thông, liên thông đường và quan hệ</p><p>4.2 Thành phần liên thông</p><p>4.3 Hàm liên tục trên không gian liên thông</p><p><strong>Chương 5: Sự tách</strong></p><p>5.1 Không gian Hausdorff, chính tắc, chuẩn tắc và quan hệ</p><p><strong>Chương 6: Sự hội tụ</strong></p><p>6.1 Lưới</p><p>6.2 Lưới và sự hội tụ</p><p>6.3 Dãy và sự cần thiết của lưới</p><p><strong>Chương 7: Không gian compắc</strong></p><p>7.1 Không gian compắc</p><p>7.2 Sự compắc theo dãy và quan hệ</p><p>7.3 Số Lebesgue cho không gian metric compắc</p><p>7.4 Compắc hóa Alexandroff</p><p><strong>Chương 8: Không gian tích</strong></p><p>8.1 Tôpô tích</p><p>8.2 Định lí Tikhonov</p><p>8.3 Compắc hóa Stone-Cech</p><p><strong>Chương 9: Hàm thực và không gian hàm</strong></p><p>9.1 Bổ đề Urysohn</p><p>9.2 Tôpô hội tụ đều và tôpô compắc-mở</p><p>9.3 Định lí Tiestze</p><p><strong>Chương 10: Không gian thương</strong></p><p>10.1 Tôpô thương</p><p>10.2 Sự nhúng và sự nhúng chìm</p><p>10.3 Các không gian hay gặp: mặt xuyến, mặt Mobius, mặt Klein, không gian xạ ảnh.</p>\"},{\"id\":781391419976,\"name\":\"Topo vi phân\",\"description\":\"<p><strong>Chương 1: Đa tạp trong R^n</strong></p><p>Bài 1.1: Đa tạp</p><p>Bài 1.2: Không gian tiếp xúc</p><p>Bài 1.3: Ánh xạ khả vi và đạo hàm của ánh xạ</p><p><strong>Chương 2: Giá trị chính qui</strong></p><p>Bài 2.1: Định lí về tập mức ứng với giá trị chính qui</p><p>Bài 2.2: Đa tạp có biên</p><p><strong>Chương 3: Bổ đề Morse</strong></p><p>Bài 3.1: Hessian và Bổ đề Morse</p><p>Bài 3.2: Dòng</p><p>Bài 3.3: Topo của tập mức</p><p><strong>Chương 4: Bậc của ánh xạ</strong></p><p>Bài 4.1: Định hướng trên đa tạp</p><p>Bài 4.2: Bậc Brouwer của ánh xạ. Định lí điểm bất động Brouwer</p><p>Bài 4.3: Chỉ số của trường vector</p><p><strong>Chương 5: Tích phân</strong></p><p>Bài 5.1: Phân hoạch đơn vị</p><p>Bài 5.2: Tích phân của một hàm thực trên đa tạp</p><p>Bài 5.3: Các đề tài khác.</p>\"},{\"id\":432294834383,\"name\":\"Biến đổi tích phân\",\"description\":\"<p><strong>Chương 1: Phép biến đổi Fourier</strong></p><p>Bài 1.1: Bổ đề Riemann - Lebesgue, tích phân kỳ dị Cauchy, tích phân kỳ dị Weierstrass.</p><p>Bài 1.2: Chuỗi Dirichlet, công thức Perron</p><p>Bài 1.3: Biến đổi Fourier-Plancherel trong không gian L^2</p><p>Bài 1.4: Biến đổi Fourier-Plancherel trong không gian L^p</p><p><strong>Chương 2: Một số biến đổi tích phân khác</strong></p><p>Bài 2.1: Phép biến đổi Laplace, Mellin, Hilbert</p><p>Bài 2.2: Phép biến đổi tổng quát, Định lý Hankel và các hệ quả</p><p><strong>Chương 3: Đại cương về phép biến đổi Radon</strong></p>\"},{\"id\":313880593066,\"name\":\"Bài toán không chỉnh\",\"description\":\"<p><strong>Chương 1: Bài toán không chỉnh</strong></p><p>Bài 1.1: Bài toán chỉnh, không chỉnh theo nghĩa Hadamard</p><p>Bài 1.2: Thành lập một số bài toán không chỉnh quy và phương trình tích phân loại I</p><p>Bài 1.3: Khảo sát tính không hcinr tổng quát của toán tử tích phân loại I</p><p>Bài 1.4: Nghiệm chỉnh hóa</p><p><strong>Chương 2: Phương hóa chỉnh hóa Tikhonov</strong></p><p>Bài 2.1: Định lý tồn tại nghiệm của phương tình chỉnh hóa. Sự phụ thuộc liên tục của nghiệm chỉnh hóa</p><p>Bài 2.2: Cấp sai số giữa nghiệm chỉnh hóa và nghiệm chính xác</p><p>Bài 2.3: Phép xấp xĩ hữu hạn chiều</p><p><strong>Chương 3: Phương pháp bài toán Moment</strong></p><p>Bài 3.1: Thành lập một số bài toán moment tương đương với các phương tình tích phân loại I</p><p>Bài 3.2: Sự hội tụ của nghiệm chỉnh hóa hữu hạn chiều trong hai trường hợp:</p><p>- Nghiệm chính xác là duy nhất</p><p>- Nghiệm chính xác không duy nhất.</p>\"},{\"id\":447940365977,\"name\":\"LT độ đo\",\"description\":\"\"},{\"id\":721525007585,\"name\":\"Lý Thuyết Hàm\",\"description\":\"<p><strong>Mã học phần : TTH370,&nbsp; 4 tín chỉ.</strong></p><p><strong>Thời khóa biểu</strong> : 8g30-12g00 Thứ Hai, phòng&nbsp; ....., &nbsp; Cơ sở Nguyễn Văn Cừ.</p><p><strong>Thời lượng: </strong>Gồm 15 bài giảng, 4 tiết / 1 bài giảng.</p><p><strong>Giờ gặp sinh viên (2015): 12g30 - 13g30 Trưa Thứ 6 hằng tuần, tại phòng F207A Bô môn Giải tích.</strong></p><p><strong>Nội dung chính</strong> : Khái niệm Hàm giải tích nhiều biến phức. Phương trình Cauchy-Riemann trong không gian phức nhiều chiều. Các định lý cơ bản của Hartogs và Hiện tượng Hartogs. Miền Reinhardt. Miền chỉnh hình. Miền giả lồi. Hàm đa điều hòa dưới. Lý thuyết L^2 cho Phương trình Cauchy-Riemann nhiều biến. Ứng dụng vào Đại số Banach giao hoán. Các biểu diễn tích phân trong giải tích phức nhiều biến.&nbsp;</p><p><strong>Môn học bắt buộc trước đó </strong>: Khối kiến thức Toán lý thuyết đại cương, thi đạt môn <i><strong>Hàm Biến Phức</strong></i> (nên đọc thêm kiến thực lý thuyết cơ bản từ tài liệu của Theodore Gamelin, \\\"Complex Analysis\\\" mà tôi dạy ở lớp Đại học Chính Qui) và <i><strong>Giải Tích Hàm</strong></i>.</p><p><strong>Hình thức thi</strong> : tùy thuộc từng năm học (tự luận được sử dụng tài liêu, seminar, nộp bài tập, tiểu luận). Đây là môn chuyên sâu về lý thuyết, nhất là về Giải tích phức, không phải môn toán cơ sở, nên hình thức thi chỉ mang tính chất chất đánh giá khả năng học hỏi kiến thức mới và nghiên cứu vấn đề của sinh viên.&nbsp;</p><p><strong>Nội dung môn học :</strong></p><p><i>Chương 1 : Nhắc lại Lý thuyết Hàm giải tích phức một biến. (12 tiết)</i></p><p>Dạng phức của Phương trình Cauchy-Riemann. Định lý tích phân Cauchy-Green. Công thức tích phân Cauchy-Pompeiu. Định lý xấp xỉ Runge. Hàm nửa điều hòa dưới.</p><p><i>Chương 2 : (Đây là nội dung trọng tâm) Lý thuyết hàm phức trên miền trong C^n (Lý thuyết địa phương). (40 tiết)</i></p><p>Không gian vector phức C^n. Hàm giải tích nhiều biến phức. Công thức tích phân Cauchy trên đa đĩa. Phương trình Cauchy-Riemann trên đa đĩa. Miền Reinhardt. Hiện tượng Hartogs. Miền chỉnh hình. Miền giả lồi. Hàm đa điều hòa dưới. Miền Runge.</p><p>(hai chương này do tôi trình bày)</p><p><strong>Chương Cuối sẽ là Một trong Ba chương sau , tùy vào thời khòa biểu của mỗi giảng viên tương ứng.</strong></p><p><i>Chương 3 : Lý thuyết L^2 cho phương trình Cauchy-Riemann. (8 tiết)</i></p><p>Sự tồn tại nghiệm của phương trình Cauchy-Riemann và tính chất giả lồi.</p><p>(Giảng viên : Tiến sĩ Trần Vũ Khanh - Australia)</p><p><i>Chương 3 : Lý thuyết biểu diễn tích phân cho phương trình Cauchy-Riemann. (8 tiết)</i></p><p>Công thức Bochner – Martinelli cho hàm và dạng vi phân phức trong C^n. Vài nhân tích phân trong giải tích phức nhiều biến. Công thức nghiệm tích phân cho phương trình Cauchy-Riemann trên miền lồi.</p><p>(Giảng viên : Tiến sĩ Trần Vũ Khanh - Australia</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Tiến sĩ Lý Kim Hà - HCMUS, ĐH QG TP HCM)</p><p><i>Chương 3 : Ứng dụng vào Đại số giao hoán Banach. (8 tiết)</i></p><p>Khái niệm cơ bản trong Lý thuyết Đại số. Đại số Banach giao hoán. Phép biến đổi Gelfand. Phổ và các tính chất trong Đại số Banach gioa hoán. Biên Shilov.</p><p>(Giảng viên : Tiến sĩ Chung Nhân Phú - HCMUS, ĐH QG TPHCM)</p><p><strong>Tài liệu học tập</strong> :&nbsp;</p><p><i>Giáo trình chính:</i></p><p>M. Range, “<i><strong>Holomorphic functions and Integral representations in Several Complex Variables</strong></i>”, Springer, 1986.</p><p>(có thể tìm được file trên libgen.org)</p><p><i>Tham khảo thêm:</i></p><p>[1]&nbsp;<strong> </strong>L. Hormander, “<i>An introduction to Complex Analysis in Several Variables</i>”,&nbsp;</p><p>[2]&nbsp; S. C. Chen, M. C. Shaw, “<i>Partial Differential Equations in Several Complex Variables</i>”, 2001.</p><p>[3]&nbsp; S. Krantz, “<i>Function Theory of&nbsp; Several Complex Variables</i>”, 2nd edition, 2001.</p><p>[4]&nbsp; Takeo Ohsawa, \\\"<i>Analysis of Several Complex Variables</i>\\\", AMS, 2002&nbsp;</p><p>[5]&nbsp; G. Zampieri, “<i>Complex analysis and CR geometry</i>.”, 2008.</p><p>[6]. B. V. Sabat, “<i>Nhập môn Giải tích phức-phần II, Hàm nhiều biến</i>”, Dịch: Nguyễn Thủy Thanh, Hà Huy Khoái, NXB ĐH và TH Chuyên nghiệp, 1979.</p>\"}]}]', 2, '2024-07-16 09:43:31', '2024-07-25 03:39:39'),
(4, 'Chuyên ngành giải tích số', '[{\"id\":849997477096,\"name\":\"Giải tích số\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1618/1138;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 10.40.16.png\\\" width=\\\"1618\\\" height=\\\"1138\\\"></figure>\",\"categories\":[{\"id\":531087272974,\"name\":\"Giải tích số 1\",\"description\":\"<p><strong>Chương 1: Khái niệm cơ bản</strong></p><p>Các loại sai số thường dùng: truncation error, rounding error, cancellation error.</p><p><strong>Chương 2: Các phương pháp xấp xỉ</strong></p><p>Các phương pháp xấp xỉ nghiệm phương trình một biến f(x) =0 bao gồm: Bisection method. Fixed point iteration. Newton Raphson method. Iterations to solve simultaneous equations.</p><p><strong>Chương 3: Phương pháp giải hệ phương trình tuyến tính</strong></p><p>Các phương pháp giải hệ phương trình tuyến tính: Gaussian elimination, LU factorization. Pivoting. Cholesky\'s method. Illconditioned equations.</p><p><strong>Chương 4: Phương pháp nội suy đa thức</strong></p><p>Các phương pháp nội suy đa thức: Lagrange interpolation. Divided differences. Finite differences.</p><p><strong>Chương 5: Phương pháp xấp xỉ đạo hàm và tích phân</strong></p><p>Các phương pháp xấp xỉ đạo hàm và tích phân: Numerical differentiation. The mid point rule, trapezium rule, Simpson\'s rule. Composite formulae. Estimating the error term.</p>\"},{\"id\":253431357731,\"name\":\"Giải tích thực\",\"description\":\"<p><strong>Chương 1: Không gian</strong></p><p>Bài 1.1: Nhắc lại về tích phân Lebesgue</p><p>Bài 1.2 Định nghĩa và tính chất căn bản của</p><p>Bài 1.3 Tính phản xạ, tính tách được và đốing ẫu của</p><p>Bài 1.4 Tích chập</p><p>Bài 1.5 Tiêu chuẩn Compact trong</p><p>Bài 1.6 Biến đổi Fourier và biến đổi Plancherel</p><p><strong>Chương 2: Không gian Sobolev 1 chiều</strong></p><p>Bài 2.1 Định nghĩa đạo hàm suy rộng</p><p>Bài 2.2 Không gian ;</p><p>Bài 2.3 Áp dụng vào bài toán biên 1 chiều.</p><p><strong>Chương 3: Không gian Sobolev nhiều chiều.</strong></p><p>Bài 3.1 Định nghĩa</p><p>Bài 3.2 Toán tử thác triển và bất đẳng thức Sobolev, định lý nhúng compact</p><p>Bài 3.3 Vết và không gian</p><p>Bài 3.4 Ứng dụng vào bài toán Elliptic.</p><p>Ôn tập</p>\"},{\"id\":684959242258,\"name\":\"Phương pháp số trong đại số tuyến tính\",\"description\":\"<p><strong>Chương 1: Khái niệm cơ bản</strong></p><p>Giới thiệu tổng quan các khái niệm đại số tuyến tính: các phép toánma trận, hệ phương trình, các loại ma trận, các phương pháp phân tích ma trận...</p><p><strong>Chương 2: QR decomposition and Least Squares problem (12 tiết).</strong></p><p>Giới thiệu phương pháp QR decomposition: phương pháp chiếu (projectors), phương pháp Gram-Schmidt, phương pháp Householder, bài toán bình phương tối thiểu.</p><p><strong>Chương 3: Hệ phương trình tuyến tính.</strong></p><p>Giới thiệu các thuật toán giải hệ phương trình tuyến tính</p><p><strong>Chương 4: Eigenvalues</strong></p><p>Giới thiệu bài toán trị riêng ma trận và các thuật toán liên quan.</p><p><strong>Chương 5: Độ ổn định</strong></p><p>Đánh giá độ ổn định của các thuật toán</p>\"},{\"id\":299114376418,\"name\":\"Phương trình đạo hàm riêng\",\"description\":\"<p><strong>Chương 1: Phương trình elliptic.</strong></p><p>1.1. Không gian Sobolev</p><p>1.2. Nghiệm suy rộng, Tính trơn của nghiệm.</p><p>1.3. Định lý Lax-Milgram</p><p>1.4. Bài toán gía trị riêng, véctơ riêng.</p><p>1.5. Nguyên lý cực đại.</p><p><strong>Chương 2: Phương trình parabolic.</strong></p><p>2.1. Không gian C(I;X)</p><p>2.2. Lý thuyết nửa nhóm</p><p>2.3. Sự tồn tại nghiệm của phương trình parabolic.</p><p>2.4. Nguyên lý cực đại.</p><p>2.5. Khai triển nghiệm theo véc-tơ riêng của toán tử Elliptic.</p><p><strong>Chương 3: Phương trình hyperbolic.</strong></p><p>3.1. Sự tồn tại nghiệm của phương trình Hyperbolic.</p><p>3.2. Khai triển nghiệm theo véc-tơ riêng của toán tử Elliptic.</p>\"},{\"id\":873735891375,\"name\":\"Phương pháp số trong đại số tuyến tính nâng cao\",\"description\":\"<p><strong>Chương 1: Direction Methods for large sparse linear systems</strong></p><p>Bài 1.1: Introduction to Sparse Cholesky Factorization</p><p>Bài 1.2: Basic of graph representation of sparse matrices and relation to elimination</p><p>Bài 1.3: Symmetric Minimum Degree Ordering</p><p>Bài 1.4: Basic algorithm and data structure for Sparse Cholesky</p><p>Bài 1.5: Survey of modern methods and software (Super LU)</p><p><strong>Chương 2: The linear least squares problem</strong></p><p>Bài 2.1: Brief review of basic least squares algorithms and geometry</p><p>Bài 2.2: The SVD solution</p><p>Bài 2.3: Principal Component Analysis</p><p><strong>Chương 3: Matrix Theory</strong></p><p>Bài 3.1: Jordan Normal Form</p><p>Bài 3.2: The Courant-Fisher theorem</p><p>Bài 3.3: Basic pertubation theory for a simple eigenvalue</p><p>Bài 3.4: The Schur Decomposition and consequences</p><p>Bài 3.5: The Sylvester Equations</p><p><strong>Chương 4 : Preconditioned iterative methods for linear systems</strong></p><p>Bài 4.1: Brief review of classic iterative methods</p><p>Bài 4.2: The Krylov subspace and the Arnoldi process</p><p>Bài 4.3: GMRES derivation</p><p>Bài 4.4: Two sided Lanczos process</p><p>Bài 4.5: QMR, BICG, BICCstab</p><p>Bài 4.6: The IDR family of iterative methods</p><p>Bài 4.7: Jacobi, Block Jacobi, ICC, ILU preconditioners</p>\"},{\"id\":212347207103,\"name\":\"Giải tích phần tử hữu hạn\",\"description\":\"<p><strong>Chương 1: Khái niệm cơ bản (6 tiết)</strong></p><p>Giới thiệu tổng quan các khái niệm giải tích hàm: chuẩn, tích trong, không gian Sobolev, đạo hàm yếu, định lý Lax-Milgram.</p><p><strong>Chương 2: Lý thuyết xấp xĩ và nội suy (8 tiết)</strong></p><p>Giới thiệu các phương pháp xấp xỉ cho đạo hàm, tích phân và các phương pháp nội suy. Trình bày các bổ đề Bramble-Hilbert, Friedrichs, Cea... được sử dụng để đánh giá cá phương pháp xấp xĩ và nội suy trong không gian Sobolev.</p><p><strong>Chương 3: Phương pháp phần tử hữu hạn (8 tiết)</strong></p><p>Giới thiệu phương pháp phần tử hữu hạn dưới dạng xấp xĩ cho Ritz- Galerkin áp dụng cho bài toán biến phân. Trình bày các không gian của các hàm liên tục tuyến tính từng phần (continuous piecewise linear) trên các phần tử của lưới được chia trên miền của bài toán. Điều kiện lưới chuẩn và inverse assumption của lưới. Trình bày các đánh giá sai số giữa một hàm trong không gian Sobolev với nội suy của nó, và sai số gradient giữa hai hàm này trong đánh giá, được xét trên lưới chuẩn.</p><p><strong>Chương 4: Phương pháp phần tử hữu hạn cho bài toán khuyếch tán và đối lưu (8 tiết)</strong></p><p>Xem xét sự ổn định, tồn tại và duy nhất nghiệm xấp xỉ của phương pháp phần tử hữu hạn cho bài toán khuếch tán và đối lưu. Đánh giá sai số tiên nghiệm. Sử dụng Matlab để mô phỏng bài toán khuyếch tán và đối lưu bằng phương pháp phần tử hữu hạn.</p>\"},{\"id\":168523415997,\"name\":\"Nhập môn phương pháp thể tích hữu hạn và ứng dụng\",\"description\":\"<p><strong>Chương 1: Tổng quan về phương pháp thể tích hữu hạn</strong></p><p>Giới thiệu tổng quan về phương pháp thể tích hữu hạn qua những bài toán cụ thể, rời rạc hóa theo biến không gian và thời gian…</p><p><strong>Chương 2: Phương trình elliptic 1 chiều</strong></p><p>Trình bày chi tiết phương pháp sai phân và thể tích hữu hạn cho phương trình elliptic 1 chiều. Rời rạc hoá theo biến không gian. Đánh giá sự hội tụ và sai số của sơ đồ số cho phương pháp thể tích hữu hạn.</p><p><strong>Chương 3: Phương trình parabolic 1 chiều</strong></p><p>Trình bày chi tiết phương pháp thể tích hữu hạn cho bài toán parabolic 1 chiều. Rời rạc hoá theo biến thời gian và không gian. So sánh phương pháp sai phân và thể tích hữu hạn qua bài toán mô hình: phương trình nhiệt.</p><p><strong>Chương 4: Phương trình hyperbolic 1 chiều.</strong></p><p>Trình bày chi tiết phương pháp thể tích hữu hạn cho bài toán hyperbolic 1 chiều trong trường hợp tuyến tính và phi tuyến. Rời rạc hoá theo biến thời gian và không gian. Phân tích kỹ thuật upwind và trung tâm qua bài toán mô hình: phương trình Burgers.</p>\"},{\"id\":448038886846,\"name\":\"Giải tích số ứng dụng cho các bài toán công nghiệp\",\"description\":\"\"},{\"id\":361037026215,\"name\":\"Giải tích hàm nâng cao\",\"description\":\"\"},{\"id\":165419570888,\"name\":\"Phương pháp thể tích hữu hạn cho PTĐHR của chuyển động dòng chảy\",\"description\":\"<p><strong>Chương 1: Khái niệm cơ bản của dòng chảy chất lỏng</strong></p><p>Bài 1.1: Giới thiệu</p><p>Bài 1.2: Định luật bảo toàn</p><p>Bài 1.3: Định luật bảo toàn khối lượng</p><p>Bài 1.4: Định luật bảo toàn momen động lượng</p><p>Bài 1.5: Định luật bảo toàn năng lượng</p><p><strong>Chương 2: Giới thiệu phương pháp số</strong></p><p>Bài 2.1: Sự xấp xỉ cho những bài toán dòng chảy</p><p>Bài 2.2: CFD là gì?</p><p>Bài 2.3: Những phần của phương pháp nghiệm số</p><p>Bài 2.4: Tính chất của phương pháp nghiệm số</p><p>Bài 2.5: Những phương pháp số</p><p><strong>Chương 3: Phương pháp thể tích hữu hạn</strong></p><p>Bài 3.1: Giới thiệu</p><p>Bài 3.2: Sự xấp xỉ của tích phân mặt</p><p>Bài 3.3: Sự xấp xỉ của tích phân thể tích</p><p>Bài 3.4: Nội suy và xấp xỉ đạo hàm</p><p>Bài 3.5: Hệ phương trình tuyến tính</p><p><strong>Chương 4: Nghiệm của phương trình Navier-Stokes</strong></p><p>Bài 4.1: Tính chất đặc biệt của nghiệm phương trình Navier-Stokes</p><p>Bài 4.2:Chọn vị trí của nghiệm</p><p>Bài 4.3: Tính toán áp suất</p><p>Bài 4.4: Phương pháp nghiệm cho phương trình Navier-Stokes</p><p>Bài 4.5: Điều kiện biên và điều kiện đầu</p>\"},{\"id\":901425228050,\"name\":\"Hệ phương trình đạo hàm riêng Hyperbolic của định luật bảo toàn\",\"description\":\"<p><strong>Chương 1: Phương trình động lực học chất lỏng</strong></p><p>Dạng định luật bảo toàn và một số dạng khác của phương trình động lực học chất lỏng. Sơ lược về nhiệt động học. Tìm hiểu dạng tích phân của phương trình: đạo hàm theo thời gian, bảo toàn khối lượng, động lượng và năng lượng. Mô hình Navier-Stokes nén và không nén được. Nghiệm yếu của định luật bảo toàn.</p><p><strong>Chương 2: Phương trình đạo hàm riêng hyperbolic tuyến tính</strong></p><p>Hệ phương trình tựa tuyến tính. Phương trình bình lưu tuyến tính. Bài toán giá trị đầu tổng quát. Sự chéo hoá và những biến đặc trưng. Đặc trưng và nghiệm tổng quát của bài toán Riemann. Bài toán khí đông lực học tuyến tính.</p><p><strong>Chương 3: Hệ hyperbolic phi tuyến và định luật bảo toàn</strong></p><p>Dạng tích phân của định luật bảo toàn. Hệ hyperbolic phi tuyến. Trường đặc trưng. Dạng phương trình bảo toàn và không bảo toàn. Điều kiện Rankine-Hugoniot. Những dạng sóng và cách tìm nghiệm chính xác (tổng quát) của bài toán Riemann.</p><p><strong>Chương 4: Phép giải Riemann và phương pháp số</strong></p><p>Tìm nghiệm chính xác từ phép giải Riemann cho bài toán SWE (shallow water equations). Giải nghiệm chính xác và sử dụng phương pháp thể tích hữu hạn để tìm nghiệm của phương trình bình lưu tuyến tính và phương trình Burgers.</p>\"},{\"id\":328036607045,\"name\":\"Phương trình toán lý\",\"description\":\"<p><strong>Chương 1. Phương trình vi phân tuyến tính cấp hai với hệ số hằng</strong></p><p>1.1. Các bài toán dao động</p><p>1.2. Tính duy nhất nghiệm của bài toán Cauchy</p><p>1.3. Cách giải phương trình vi phân tuyến tính cấp hai với hệ số hằng</p><p>1.4. Cách giải phương trình vi phân tuyến tính cấp hai với hệ số hằng liên quan tới các bài toán dao động.</p><p><strong>Chương 2. Các phương trình toán lý</strong></p><p>2.1. Mở đầu, các ví dụ.</p><p>2.2. Phương trình đạo hàm riêng tuyến tính; Các tính chất nghiệm.</p><p>2.3. Phân loại phương trình đạo hàm riêng tuyến tính cấp hai theo hai biến độc lập.</p><p>2.4. Thiết lập các bài toán cơ bản cho các phương trình đạo hàm riêng tuyến tính cấp hai.</p><p><strong>Chương 3. Phương trình sóng</strong></p><p>3.1. Thiết lập phương trình sóng</p><p>3.2. Nghiệm của bài toán Cauchy (Bài toán giá trị ban đầu) phương trình sóng cho dây vô hạn. Công thức D’Alembert.</p><p>3.3. Bài toán đặt đúng. Ví dụ của Hadamard về bài toán không chỉnh.</p><p>3.4. Dao động tự do của một sợi dây cố định tại hai đầu. Phương pháp tách biến Fourier.</p><p>3.5. Dao động cưỡng bức của một sợi dây cố định tại hai đầu.</p><p>3.6. Dao động cưỡng bức của một sợi dây có tại hai đầu không cố định.</p><p>3.7. Dao động cưỡng bức của một sợi dây có gắn lò xo ở hai đầu.</p><p>3.8. Sơ đồ tổng quát của phương pháp tách biến Fourier.</p><p>3.9. Tính duy nhất nghiệm của bài toán hỗn hợp.</p><p>3.10. Dao động của một màng tròn.</p><p>3.11. Áp dụng biến đổi Laplace để giải bài toán hỗn hợp.</p><p><strong>Chương 4. Phương trình nhiệt</strong></p><p>4.1. Thiết lập phương trình nhiệt</p><p>4.2. Nghiệm của bài toán Cauchy phương trình nhiệt</p><p>4.3. Truyền nhiệt trong thanh hữu hạn. Phương pháp tách biến Fourier.</p><p>4.4. Truyền nhiệt trong thanh hữu hạn với các loại điều kiện biên Dirichlet không thuần nhất, Robin không thuần nhất, Robin - Dirichlet không thuần nhất.</p><p><strong>Chương 5. Phương trình Laplace</strong></p><p>5.1. Thiết lập bài toán biên.</p><p>5.2. Nghiệm cơ bản của phương trình Laplace.</p><p>5.3. Công thức Green</p><p>5.4. Tính chất của hàm điều hòa.</p><p>5.5. Bài toán Dirichlet cho phương trình Laplace trong miền hình tròn. Phương pháp tách biến Fourier.</p><p>5.6. Tích phân Poisson.</p><p>5.7. Bài toán Dirichlet cho phương trình Laplace trong miền hình chữ nhật. Phương pháp tách biến Fourier.</p>\"},{\"id\":729511720536,\"name\":\"Giải tích số cho bài toán ngược\",\"description\":\"<p><strong>Chapter 1:</strong> <strong>Inverse mathematical physics problems</strong></p><p>1.1 Boundary value problems.</p><p>1.2 Well-posed problems for partial differential equations.</p><p>1.3 Ill-posed problems.</p><p>1.4 Classiﬁcation of inverse mathematical physics problems.</p><p><strong>Chapter 2: Solution methods for ill-posed problems</strong></p><p>2.1 Tikhonov regularization method..</p><p>2.2 The rate of convergence in the regularization method.</p><p>2.3 Choice of regularization parameter.</p><p>2.4 Iterative solution methods for ill-posed problems.</p><p>2.5 Program implementation and computational experiments.</p><p><strong>Chapter 3: Evolutionary inverse problems</strong></p><p>3.1 Non-local perturbation of initial conditions.</p><p>3.2 Regularized difference schemes.</p><p>3.3 Iterative solution of retrospective problems.</p><p>3.4 Second-order evolution equation.</p><p>3.5 Computational experiments for some methods.</p><p>&nbsp;</p>\"},{\"id\":519004644028,\"name\":\"Cơ học chất lỏng\",\"description\":\"\"},{\"id\":144627958450,\"name\":\"Chuyên đề giải tích số\",\"description\":\"\"},{\"id\":803671319717,\"name\":\"Cơ sở dữ liệu\",\"description\":\"<p>Mã môn học: MTH10312<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Tin học cơ sở.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các khái niệm cơ bản về cơ sở dữ liệu; các mô hình dữ liệu; các<br>tiêu chuẩn về thiết kế cơ sở dữ liệu và các dạng chuẩn.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản về cơ sở dữ liệu.<br>- Mô hình dữ liệu thực thể liên kết, mô hình dữ liệu quan hệ.<br>- Các phép toán trên mô hình dữ liệu quan hệ.<br>- Ngôn ngữ truy vấn SQL.<br>- Các dạng chuẩn và thuận toán chuẩn hóa cơ sở dữ liệu.</p><p><strong>Tài liệu học tập:</strong><br>[1] R. Elsmari, S. Navathe. Fundamentals of Database Systems 4th edition, Addison<br>Wesley, 2004.<br>[2] Nguyễn Kim Anh. Nguyên lý của các hệ cơ sở dữ liệu, NXB ĐHQG Hà Nội, 2004.</p>\"},{\"id\":391140748734,\"name\":\"Phát triển phần mềm hướng đối tượng\",\"description\":\"<p>Mã học phần: MTH10308<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>- Số tiết lý thuyết: 45<br>- Số tiết thực hành, thực tập: 30<br>- Số tiết bài tập trên lớp: 15<br>- Số tiết thảo luận: 30<br>- Số tiết làm việc nhóm: 30<br>- Số tiết tự học: 30<br><strong>Mục tiêu của học phần</strong><br>Cung cấp cho các sinh viên các kiến thức chuyên sâu liên quan đến các đối tượng chính<br>yếu trong lĩnh vực công nghệ phần mềm hướng đối tượng (qui trình công nghệ, phương<br>pháp kỹ thuật thực hiện, công cụ và môi trường triển khai phần mềm,…).<br><strong>Tóm tắt nội dung học phần</strong><br>Các khái niệm cơ bản về phát triển phần mềm hướng đối tượng.<br>Mô hình đối tượng, mô hình trạng thái, mô hình chức năng.<br>Thiết kế hệ thống, thiết kế đối tượng, thiết kế giao diện.<br>Sử dụng các IDE và công cụ quản lý mã nguồn.</p><p><strong>Tài liệu học tập:</strong><br>[1] James Rumbaugh, Michael Blaha, Wiliam Premerlani, Frederick Eddy,<br>Wiliam Lorensen. Object-Oriented Modeling and Design, Prentice-Hall<br>International Editions, 1991.<br>[2] Erich Gamma, Richard Helm, Ralph Johnson and John Vlissides. Design Patterns,<br>Addison-Wesley, 1995.<br>[3] d. Martin Fowler. UML Distilled, 3rd edition, Addison-Wesley, 2004.</p>\"},{\"id\":373448879850,\"name\":\"Seminar Giải tích số\",\"description\":\"\"}]}]', 2, '2024-07-16 09:44:19', '2024-07-25 03:56:17'),
(5, 'Chuyên ngành đại số', '[{\"id\":354107878262,\"name\":\"Đại số\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1886/1294;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 10.12.29.png\\\" width=\\\"1886\\\" height=\\\"1294\\\"></figure>\",\"categories\":[{\"id\":906150012315,\"name\":\"Đại số hiện đại\",\"description\":\"\"},{\"id\":873270070286,\"name\":\"Nhập môn Lý thuyết vành\",\"description\":\"\"},{\"id\":686385156174,\"name\":\"Đại số giao hoán\",\"description\":\"\"},{\"id\":505405526574,\"name\":\"Lý thuyết trường và Galois\",\"description\":\"\"},{\"id\":983759648774,\"name\":\"Đại số đồng đều\",\"description\":\"\"},{\"id\":460416205673,\"name\":\"Đại số phân bậc\",\"description\":\"\"},{\"id\":147632477552,\"name\":\"Nhập môn Lý thuyết Tổ hợp\",\"description\":\"\"},{\"id\":756688893410,\"name\":\"Topo Đại số\",\"description\":\"<p><strong>Chương 1: Phức đơn hình và phức ô</strong></p><p>Bài 1.1: Phức đơn hình. Đa diện. Phân chia tam giác. Cắt và dán. Các ví dụ quan trọng.</p><p>Bài 1.2: Phân loại mặt hai chiều</p><p>Bài 1.3: Đặc trưng Euler</p><p>Bài 1.4: Phức ô. Các ví dụ quan trọng: Lens spaces, không gian xạ ảnh, Seifert, Poincare dodecahedron spaces…</p><p><strong>Chương 2: Đồng luân</strong></p><p>Bài 2.1: Nhóm cơ bản</p><p>Bài 2.2: Nhóm cơ bản của đường tròn</p><p>Bài 2.3: Định lý Seifert-Van Kampen</p><p>Bài 2.4: Nhóm cơ bản của phức ô. Ví dụ</p><p><strong>Chương 3: Đồng điều</strong></p><p>Bài 3.1: Đồng điều đơn hình</p><p>Bài 3.2: Đồng điều kì dị</p><p>Bài 3.3: Định lý Mayer - Vietoris. Nhóm đồng điều của mặt cầu</p><p>Bài 3.4: Đồng điều ô. Cách tính. Ví dụ</p><p>Bài 3.5: Mối quan hệ giữa Nhóm cơ bản và nhóm đồng điều. Số Betti, đặt trưng Euler</p><p><strong>Chương 4: Ứng dụng (đọc thêm)</strong></p><p>- Định lý Jordan, Định lý Bất biến miền, Định lý điểm bất động Brouwer, Định lý Borsuk-Ulam. Ứng dụng vào Tôpô Tính toán…</p><p>&nbsp;</p>\"},{\"id\":540446452524,\"name\":\"Mođun và ứng dụng\",\"description\":\"\"},{\"id\":833755600868,\"name\":\"Lý thuyết trường hữu hạn\",\"description\":\"\"},{\"id\":136962282080,\"name\":\"Đại số đồ thị\",\"description\":\"\"},{\"id\":738801357437,\"name\":\"Đại số máy tính\",\"description\":\"\"},{\"id\":847574982869,\"name\":\"Lý thuyết biểu diễn nhóm hữu hạn\",\"description\":\"\"},{\"id\":389073197912,\"name\":\"Nhập môn Lý thuyết số\",\"description\":\"\"},{\"id\":168933174091,\"name\":\"Lý thuyết nhóm\",\"description\":\"\"},{\"id\":676790908827,\"name\":\"Seminar Đại số\",\"description\":\"\"},{\"id\":672780162108,\"name\":\"Khoá luận tốt nghiệp\",\"description\":\"\"}]}]', 3, '2024-07-16 09:44:42', '2024-07-25 03:57:47');
INSERT INTO `majors` (`id`, `name`, `subjects`, `department_id`, `created_at`, `updated_at`) VALUES
(6, 'Xác suất thống kê', '[{\"id\":780975020131,\"name\":\"Xác suất thống kê\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:720/748;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 07.38.32.png\\\" width=\\\"720\\\" height=\\\"748\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:550/734;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 07.40.37.png\\\" width=\\\"550\\\" height=\\\"734\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1154/78;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 07.40.51.png\\\" width=\\\"1154\\\" height=\\\"78\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1232/420;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 07.41.01.png\\\" width=\\\"1232\\\" height=\\\"420\\\"></figure>\",\"categories\":[{\"id\":741094787329,\"name\":\"CTDL&GT\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":292232374565,\"name\":\"LT độ đo và XS\",\"description\":\"<ul><li><strong>MTH 10401 (4TC): Lý thuyết&nbsp; độ đo và xác suất</strong></li></ul><p>Giảng viên phụ trách: GS. TS. Đặng Đức Trọng<br>Học phần cung cấp kiến thức về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến ngẫu nhiên, luật số lớn và các định lý giới hạn.</p>\"},{\"id\":676983496066,\"name\":\"Đại số A2\",\"description\":\"<p><strong>MTH 10402 (4TC): Đại số A2</strong><br>Học phần trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.&nbsp; Cụ thể, sinh viên sẽ nắm vững sự chéo hoá và Jordan hoá của toán tử tuyến tính trên các không gian vector hữu hạn chiều, cấu trúc của không gian Euclide, dạng song tuyến tính và dạng toàn phương.</p>\"},{\"id\":573368808205,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":416570246603,\"name\":\"LTHDT\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":499845616104,\"name\":\"LT thống kê\",\"description\":\"<ul><li><strong>MTH 10404 (4TC): Lý thuyết thống kê</strong></li></ul><p>Giảng viên phụ trách: TS. Hoàng Văn Hà, TS. Nguyễn Thị Mộng Ngọc<br>Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê Toán-phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê cũng như sử dụng thành thạo phần mềm thống kê chuyên dụng R để phân tích, xử lý số liệu thống kê.<br>Phần lý thuyết: bao gồm các kiến thức về mẫu (sample), các đặc trưng mẫu, ước lượng, các phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai và tỷ lệ; cùng với bài toán xác định cỡ mẫu. Kiểm định giả thuyết: trường hợp 1 mẫu và trường hợp 2 mẫu; kiểm định giả thuyết về phân phối, kiểm định giả thuyết về tính độc lập. Kiến thức về hồi quy thống kê và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần mềm R để thực hành các bài toán thống kê trên máy tính với số liệu thực: mô phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.</p>\"},{\"id\":510056264279,\"name\":\"LT xác suất cơ bản\",\"description\":\"<ul><li><strong>MTH 10516 (4TC): Lý thuyết Xác suất cơ bản</strong></li></ul><p>Giảng viên phụ trách: ThS. Nguyễn Văn Thìn<br>Học phần cung cấp các kiến thức cơ bản trong Lý thuyết Xác suất - ngành khoa học nghiên cứu về các hiện tượng ngẫu nhiên.<br>Sinh viên sẽ học các khái niệm cơ bản về xác suấ như phép thử, sự kiện, các định nghĩa và tính chất của xác suất, định lý Bayes. Biến ngẫu nhiên, vector ngẫu nhiên: các đặc trưng, phân phối lề, phân phối có điều kiện, kỳ vọng có điều kiện, tính độc lập, hiệp phương sai, hệ số tương quan và Định lý giới hạn trung tâm.</p>\"},{\"id\":555824996760,\"name\":\"Giải tích hàm\",\"description\":\"<p><strong>MTH 10401 (4TC): Giải tích hàm</strong><br>Học phần cung cấp những kiến thức về: không gian metric, không gian định chuẩn, ánh xạ tuyến tính liện tục giữa hai không gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn học được coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ trợ cho các môn hình toán học dùng không gian hàm và không gian vô hạn chiều.&nbsp;</p>\"},{\"id\":841896940634,\"name\":\"Thống kê kinh tế\",\"description\":\"<ul><li><strong>MTH 10512 (3TC): Thống kê kinh tế</strong></li></ul><p>Giảng viên phụ trách: ThS. Nguyễn Văn Thìn<br>Giảng viên sẽ hướng dẫn và thảo luận với sinh viên các vấn đề căn bản và ứng dụng của lý thuyết thống kê đương thời. Sinh viên có thể thu nhận các kỹ năng cần thiết để hiểu và đánh giá mô hình thống kê kinh tế.&nbsp;</p><p>Học phần tập trung vào các kỹ thuật để ước lượng mô hình hồi quy, các vấn đề gặp phải trong khi ước lượng và giải thích được các ước lượng từ các mô hình này. Dựa trên các phương pháp trong thống kê, cung cấp những điều cơ bản về lý thuyết và thực hành kinh tế và cho phép sinh viên làm quen với việc đánh giá các mô hình kinh tế với các dữ liệu thực tế.</p>\"},{\"id\":620601682320,\"name\":\"Xử lý số liệu\",\"description\":\"<ul><li><strong>MTH 10513 (3TC): Xử lý số liệu thống kê</strong></li></ul><p>Giảng viên phụ trách: TS. Nguyễn Đăng Minh (GV. thỉnh giảng)<br>Học phần trang bị cho sinh viên các kiến thức cơ bản về xử lý dữ liệu thống kê một cách cơ bản, từ đó có thể dễ dàng hơn khi tiếp cận các bài toán trong Data Analysis hay Computer Vision. Chẳng hạn, sinh viên sẽ biết dạng các dữ liệu số, âm thanh, hình ảnh, ... ; đồng thời, sử dụng được các phần mềm để thao tác trên các dữ liệu này.</p><p>Học phần giúp sinh viên có thể xử lý một cách cơ bản các dữ liệu dạng dataframe, dạng hình ảnh, video và sóng âm thanh với các thư viện trong Python như Pandas, open cv, seaborn, ... Một số học phần có liên quan trong chương trình: Thống kê nhiều chiều, Dữ liệu lớn, Xử lý ảnh, ... .</p>\"},{\"id\":434267091603,\"name\":\"Thống kê toán NC\",\"description\":\"<ul><li><strong>MTH 10424 (4TC): Thống kê toán nâng cao</strong></li></ul><p>Giảng viên phụ trách: TS.&nbsp; Hoàng Văn Hà<br>Học phần giới thiệu về cấu trúc thống kê và hàm mật độ tổng quát. Về <i>Thống kê đủ</i>: phân phối có điều kiện đối với thống kê đủ, tiêu chuẩn tách, cải tiến ước lượng nhờ thống kê đủ. Về <i>Ước lượng thống kê</i>: Bất đẳng thức Crámer-Rao, thông tin Fisher, ước lượng hữu hiệu. Bên cạnh đó, sinh viên sẽ tìm hiểu về tính chất tiệm cận; cũng như các phương pháp tìm hàm ước lượng và&nbsp; hàm hợp lý. Về <i>Kiểm định giả thuyết</i>: bổ đề Neyman-Pearson. Ngoài ra, còn có các nội dung về: Đối thuyết hợp, tiêu chuẩn mạnh đều nhất, họ phân phối có tỉ số hợp lý đơn điệu. Về <i>Tập hợp tin cậy</i>: liên hệ với họ tiêu chuẩn kiểm định giả thuyết.</p>\"},{\"id\":985729534700,\"name\":\"Xác suất NC\",\"description\":\"<ul><li><strong>MTH 10423 (4TC): Xác suất nâng cao</strong></li></ul><p>Giảng viên phụ trách: GS. TS. Đặng Đức Trọng<br>to-be-updated!!</p>\"},{\"id\":757290513474,\"name\":\"Mô hình TKTT\",\"description\":\"<ul><li><strong>MTH 10511 (4TC): Mô hình thống kê tuyến tính</strong></li></ul><p>Giảng viên phụ trách: TS. Nguyễn Thị Mộng Ngọc<br>Học phần trang bị cho sinh viên các kiến thức về phân tích hồi quy (phân tích sâu về mô hình hồi quy tuyến tính bội), phân tích phương sai (ANOVA) 1 nhân tố, 2 nhân tố. Kế đến là phần lựa chọn mô hình phù hợp nhất (các tiêu chuẩn chọn mô hình tối ưu, các phương pháp chọn mô hình phù hợp nhất: phương pháp chọn tập con tốt nhất - best subset selection, phương pháp chọn bước từng bước - stepwise selection, phương pháp shrinkage, phương pháp giảm số chiều - principal component regeression PCR).&nbsp;<br>Ngoài ra, việc kiểm tra các giả định của mô hình hồi quy; xác định và xử lý hiện tượng đa cộng tuyến; các phép biến đổi (transformations) của các biến phản hồi và biến dự đoán để làm cho mô hình phù hợp với dữ liệu hơn.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>Giải quyết các bài toán thực tế được đưa ra trong mỗi chương bằng phần mềm thống kê R (hoặc Python).</p>\"},{\"id\":898717021777,\"name\":\"Thống kê trong sinh học\",\"description\":\"<ul><li><strong>MTH 10510 (3TC): Thống kê trong sinh học</strong></li></ul><p>Giảng viên phụ trách: TS. Tô Đức Khánh<br>Mục tiêu của môn học là phát triển năng lực của sinh viên trong việc áp dụng các phương pháp phân tích thống kê để giải quyết các nghiên cứu sinh học thông dụng. Nội dung chính của môn học sẽ dựa trên các kiến thức nền đã được giảng dạy ở các môn học trước đó, bao gồm: Xác suất, Lý thuyết Thống kê, Thống kê Toán nâng cao, Mô hình thống kê tuyến tính và Mô hình thống kê tuyến tính nâng cao. Từ đây, một góc nhìn tổng thể về thống kê hay áp dụng thống kê (tại sao, khi nào và sử dụng như thế nào) sẽ được mang tới cho sinh viên, dựa vào các kiều dữ liệu từ bài toán nghiên cứu sinh học khác nhau. Ngoài ra, môn học cũng sẽ cung cấp cho sinh viên một số cung cụ mở rộng (nằm ngoài các môn học trước đó) dành cho một bài toán thống kê sinh học đặc thù. Môn học sẽ thiên về kỹ năng chuyển đổi câu hỏi nghiên cứu sinh học sang câu hỏi nghiên cứu thống kê, và áp dụng các phương pháp phân tích thống kê tương ứng, do đó, ký năng về sử dụng phần mềm xử lý số liệu thống kê R (hoặc python) cũng được trang bị cho sinh viên.</p><p><strong>Lưu ý:</strong>&nbsp; Sinh viên chỉ cần đã học qua học phần Lý thuyết Thống kê là có thể đăng ký được học phần.</p>\"},{\"id\":107712753970,\"name\":\"LT&PP chọn mẫu\",\"description\":\"<ul><li><strong>MTH 10517 (4TC): Lý thuyết và phương pháp chọn mẫu</strong></li></ul><p>Giảng viên phụ trách: TS. Nguyễn Tiến Đạt<br>Học phần cung cấp kiến thức cơ bản về phương pháp thiết kế và phân tích chọn mẫu, để có thể xác định những yếu tố sử dụng cho các thiết kế mẫu khác nhau, hữu ích cho việc nghiên cứu và quản lý trong nhiều lĩnh vực; sử dụng phần mềm thống kê R để phân tích và thiết kế chọn mẫu thống kê.<br>Phần lý thuyết: bao gồm các kiến thức về chọn mẫu và ước lượng, đơn vị lấy mẫu, sai số trong chọn mẫu, các thiết kế mẫu cơ bản và các phương pháp chọn mẫu thông dụng như: chọn mẫu ngẫu nhiên đơn giản, xác định kích cỡ mẫu, chọn mẫu với xác suất không cân bằng, chọn mẫu phân tầng, chọn mẫu theo nhóm và hệ thống, chọn mẫu đa tầng.<br>Phần thực hành: áp dụng các kiến thức trong phần lý thuyết và sử dụng phần mềm R để giải quyết các bài toán chọn mẫu thống kê trên máy tính với số liệu thực: mô phỏng, chọn mẫu, phân tích mẫu.</p>\"},{\"id\":666526202713,\"name\":\"Chuỗi thời gian\",\"description\":\"<ul><li><strong>MTH 10485 (4TC): Chuỗi thời gian</strong></li></ul><p>Giảng viên phụ trách: ThS. Nguyễn Thị Hồng Nhung<br>Trang bị cho sinh viên các kiến thức về phân tích chuỗi thời gian và quá trình ARIMA. Từ đó sinh viên có thể nhận dạng quá trình ARMA đối với một tập dữ liễu cho trước và dùng phần mềm R phân tích chuỗi thời gian để đưa ra các dự báo.</p>\"},{\"id\":671689456976,\"name\":\"Quá trình ngẫu nhiên\",\"description\":\"<ul><li><strong>MTH 10426 (4TC): Quá trình ngẫu nhiên</strong></li></ul><p>Giảng viên phụ trách: TS. Lê Thị Xuân Mai<br>Lý thuyết xác suất có nhiều ứng dụng rộng rãi không chỉ trong toán học mà còn trong các ngành khoa học khác. Học phần này sẽ trang bị cho sinh viên những kiến thức nền tảng và quan trọng của lý thuyết xác suất, mà cụ thể là quá trình ngẫu nhiên hiện đại.<br>Các khái niệm về quá trình ngẫu nhiên, quá trình Markov, bước chuyển ngẫu nhiên (random walk) và chuyển động Brownian (Brownian motion) cùng một số tính chất cơ bản sẽ được giới thiệu và khảo sát tương đối đầy đủ.<br>Một số mô hình ứng dụng trong vật lý, sinh học, ..., cũng sẽ được giới thiệu trong môn học.&nbsp;</p>\"},{\"id\":409508176586,\"name\":\"Thống kê nhiều chiều\",\"description\":\"<ul><li><strong>MTH 10619 (4TC): Thống kê nhiều chiều</strong></li></ul><p>Giảng viên phụ trách: TS.&nbsp; Nguyễn Thị Mộng Ngọc<br>Học phần cung cấp một số phương pháp phân tích số liệu thống kê nhiều chiều. Môn học giúp sinh viên hiểu rõ về phân phối chuẩn nhiều chiểu, ước lượng và kiểm định giả thuyết về các tham số trong phân phối chuẩn nhiều chiều. Các phương pháp suy luận liên quan đến các trung bình nhiều chiều như: Hotelling’s T2;&nbsp; Phân tích phương sai nhiều chiều (MANOVA) và Hồi quy tuyến&nbsp; tính bội nhiều chiều. Các phương pháp suy luận về cấu trúc Hiệp phương sai như:&nbsp; phân tích thành phần chính (PCA), phân tích nhân tố (FA).&nbsp;</p><p>&nbsp;Xử lý số liệu thống kê nhiều chiều bằng phần mềm thống kê R ( hoặc Python)&nbsp; cho các bộ dữ liệu khác nhau để hiểu rõ các phương pháp trên. Qua đó trang bị cho sinh viên các kiến thức cơ bản về xử lý số liệu thống kê nhiều chiều và các kỹ năng ứng dụng trong việc giải quyết một số bài toán thực tế.</p>\"},{\"id\":249745004651,\"name\":\"MHTK tuyến tính NC\",\"description\":\"<ul><li><strong>MTH 10621 (4TC): Mô hình thống kê tuyến tính nâng cao</strong></li></ul><p>Giảng viên phụ trách: TS. Tô Đức Khánh<br>Môn học nhằm cung cấp cho sinh viên các kiến thức cần thiết để xây dựng và sử dụng mô hình hồi quy tuyến tính trong các trường hợp khác nhau của dữ liệu quan sát, ví dụ: dữ liệu nhị phân (binary data), dữ liệu phân loại (categorical data), dữ liệu là số đếm (count data). Đây là những loại dữ liệu thường được quan sát trong các vấn đề của thống kê kinh tế, y khoa, sinh học, hay xã hội học. Bên cạnh các kiến thức lý thuyết về mô hình thống kê, các kỹ năng sử dụng phần mềm xử lý số liệu thống kê R cũng được trang bị cho sinh viên, để giải quyết các mô hình.</p>\"},{\"id\":454521897678,\"name\":\"Giải tích hàm trong TK\",\"description\":\"<ul><li><strong>MTH 10518 (4TC): Giải tích hàm trong thống kê</strong></li></ul><p>Giảng viên phụ trách: GS. TS. Đặng Đức Trọng<br>to-be-updated!!</p>\"},{\"id\":694371746129,\"name\":\"Thống kê Bayes\",\"description\":\"<ul><li><strong>MTH 10514 (4TC): Thống kê Bayes</strong></li></ul><p>Giảng viên phụ trách: TS. Lê Thị Xuân Mai<br>Học phần cung cấp kiến thức về phân phối có điều kiện, phân phối đồng thời, phân phối lề, ước lượng mật độ phi tham số, phân phối tiên nghiệm, phân phối hậu nghiệm, phân phối dự báo, hàm niềm tin, luật Bayes, tính hoán đổi, liên hợp, bán liên hợp, phương pháp Monte Carlo, tạo mẫu theo phương pháp Gibbs, mô hình nhị thức, mô hình Poisson, mô hình chuẩn một chiều, mô hình chuẩn nhiều chiều, phân phối Wishart và Wishart ngược, dữ liệu bị thiếu, hồi quy Bayes, chọn lựa mô hình.</p>\"},{\"id\":297778120794,\"name\":\"Seminar Thống kê\",\"description\":\"<p><strong>MTH 10508 (4TC): Seminar Xác suất - Thống kê</strong><br>Sinh viên sẽ liên hệ với sự đồng ý của giảng viên hướng dẫn để thực hiện báo cáo seminar khoa học về một chủ đề phù hợp theo các <a href=\\\"https://sites.google.com/hcmus.edu.vn/math-xstk/research/l%C4%A9nh-v%E1%BB%B1c-nghi%C3%AAn-c%E1%BB%A9u?authuser=0\\\">hướng nghiên cứu của bộ môn Xác suất-Thống kê</a>.</p>\"},{\"id\":854181567863,\"name\":\"Kiểm định phi tham số\",\"description\":\"<ul><li><strong>MTH 10609 (4TC): Kiểm định phi tham số</strong></li></ul><p>Giảng viên phụ trách: TS. Lê Thị Xuân Mai<br>Học phần sẽ giới thiệu về kiểm định trung vị tổng thể hoặc so sánh trung vị của hai tổng thể. Kiểm định phi tham số sẽ là phương án thay thế trong trường hợp mẫu nhỏ hoặc dữ liệu vi phạm các giả thiết kiểm định phi tham số.</p>\"},{\"id\":441566736182,\"name\":\"TK phi tham số\",\"description\":\"<ul><li><strong>MTH 10515 (4TC): Thống kê phi tham số</strong></li></ul><p>Giảng viên phụ trách: GS. TS. Đặng Đức Trọng<br>Học phần trình bày phương pháp xây dựng các ước lượng phi tham số cho hàm phân phối tích luỹ, hàm mật độ, bài toán hồi quy. Học phần cũng khảo sát tính vững và tính minimax của các ước lượng này.</p>\"},{\"id\":316009929206,\"name\":\"Khoá luận tốt nghiệp\",\"description\":\"\"},{\"id\":947180280316,\"name\":\"Các môn tự chọn khác\",\"description\":\"<p>Chọn các môn tự chọn khác = 10TC</p>\"}]}]', 6, '2024-07-16 15:41:05', '2024-07-25 01:05:43');
INSERT INTO `majors` (`id`, `name`, `subjects`, `department_id`, `created_at`, `updated_at`) VALUES
(7, 'Phương Pháp Toán Tin Học', '[{\"id\":233923345785,\"name\":\"Xử lý ảnh & Nhận dạng\",\"description\":\"<p>Nghiên cứu và phát triển các phương pháp toán học và thuật toán xử lý ảnh số và nhận dạng mẫu, với ứng dụng rộng rãi từ y tế đến an ninh. Ngành học này là chìa khóa cho sự tiến bộ trong công nghệ nhận dạng.</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:520/362;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 03.40.00.png\\\" width=\\\"520\\\" height=\\\"362\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1210/342;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 03.40.18.png\\\" width=\\\"1210\\\" height=\\\"342\\\"></figure><p>&nbsp;</p>\",\"categories\":[{\"id\":947416266654,\"name\":\"Cấu trúc dữ liệu\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p>&nbsp;</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":601087151626,\"name\":\"Đại số A2\",\"description\":\"<p>Mã môn học: MTH10402<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết: Đại số tuyến tính A1<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br>Tóm tắt nội dung môn học:<br>Học phần giới thiệu các kiến thức cơ bản về rút gọn toán tử tuyến tính (sự chéo hóa,<br>dạng chính tắc Jordan), không gian Eucclid, dạng song tuyến tính và dạng toàn phương.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: Trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính<br>nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững sự chéo hóa và Jordan hóa của toán tử tuyến tính trên các<br>KGVT hữu hạn chiều, cấu trúc của không gian Euclid, dang song tuyến tính và<br>dạng toàn phương.<br>- Kỹ năng: Chéo hóa và Jordan hóa các ma trận trên các trường số, tính toán trong<br>không gian Euclid, biến đổi dạng toàn phương, sử dụng tốt MAPLE.<br>- Thái độ, chuyên cần: Nghiêm túc, cầu tiến.</p><p><strong>Tài liệu học tập</strong><br>1. Bùi Xuân Hải - Trần Ngọc Hội - Lê Văn Luyện, Đại số tuyến tính và Ứng dụng, Tập 2,<br>2017<br>2. Ngô Việt Trung, Giáo trình Đại số tuyến tính, 2001<br>3. Nguyễn Hữu Việt Hưng, Đại số tuyến tính, 2004</p>\"},{\"id\":756378871010,\"name\":\"LT độ đo & XS\",\"description\":\"<p>Mã học phần: MTH10401<br>Thuộc khối kiến thức: Cơ sở ngành<br>Bộ môn – Khoa phụ trách: Toán Tin<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Số tiết lý thuyết: 45<br>Số tiết thực hành, thực tập: 0<br>Số tiết bài tập trên lớp: 30<br>Số tiết thảo luận:<br>Số tiết làm việc nhóm:<br>Số tiết tự học:<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt):<br>- Học phần học trước (các học phần SV phải đăng ký học trước): Giải tích A1-<br>Giải tích cơ sở, Giải tích A1 - Vi tích phân, Giải tích A2.<br>- Học phần song hành (SV phải đăng ký học trong cùng học kỳ):<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có):<br><strong>Mô tả môn học</strong><br>- Tiếng Việt: Trình bày về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến<br>ngẫu nhiên, luật số lớn và các định lý giới hạn.<br>- Tiếng Anh: Giving the introduction of elementary probability theory, abstract measure<br>theory, random variables, law of large numbers and limit theorems.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đ. Đ. Áng, Nhập môn giải tích, NXBGD, 1997.<br>[2] Đ. Đ. Áng, Lý thuyết tích phân, NXBGD, 1997.<br>[3] P. Billingsley, Probability and measure, 3rd edition, John Wiley &amp; Sons, 1995.<br>[4] W. M. Bolstad, Introduction to Bayesian statistics, 2nd edition, Wiley, 2007.<br>[5] M. Bouyssel, Intégrale de Lebesgue, Cépaduès-éditions, 1997.<br>[6] K. L. Chung, A course in probability theory, 3rd edition, Academic Press, 2001.<br>[7] D. M. Đức, Lý thuyết độ đo và tích phân, NXB Đại Học Quốc Gia Tp. HCM,<br>2006.<br>[8] W. Feller, An introduction to probability theory and its applications, Vol. I, John<br>Wiley &amp; Sons, 1957.<br>[9] A. M. Mathai, H. J. Houbold, Special functions for applied scientists, Springer,<br>New York, 2008.<br>[10] P.H. Quân, Đ.N. Thanh, Xác suất thống kê, NXBGD, 2011<br>[11] S. Ross, A first course in probability, 5th edition, Prentice Hall, 1998.<br>[12] W. Rudin, Real and complex analysis, 3rd edition, McGraw-Hill, 1986.<br>[13] N. D. Tiến, V. V. Yên, Lý thuyết xác suất, NXBGD, 2006.<br>[14] Y. Viniotis, Probability and random processes for electrical engineers, McGrawHill,<br>1998.<br>[15] T. A. Dũng, Lý thuyết xác suất và thống kê toán, NXB Đại Học Quốc Gia Tp.<br>HCM, 2007.</p>\"},{\"id\":992769344772,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":889970811601,\"name\":\"Lý thuyết thống kê\",\"description\":\"<p>Mã môn học: MTH10404<br>Số tín chỉ: 03 (1LT+1BT+1TH)<br>Số tiết đối với các hoạt động:<br>Nghe giảng lý thuyết: 15 tiết<br>Làm bài tập trên lớp: 30 tiết<br>Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>Thảo luận: 30 tiết<br>Tự học: 60 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê toán –<br>phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê; sử dụng<br>thành thạo các phần mềm thống kê chuyên dụng (R, SPSS, … ) để phân tích, xử lý số<br>liệu thống kê.<br><strong>Mô tả vắn tắt nội dung môn học:</strong> (trình bày ngắn gọn vai trò, vị trí học phần/môn học,<br>kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong<br>chương trình đào tạo)<br>Phần lý thuyết: bao gồm các kiến thức về mẫu, các đặc trưng mẫu, ước lượng, các<br>phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai, tỷ lệ; xác định cỡ<br>mẫu. Kiểm định giả thuyết: trường hợp một mẫu, hai mẫu; kiểm định giả thuyết về phân<br>phối, kiểm định giả thuyết về tính độc lập. Hồi quy và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần<br>mềm thống kê R để giải quyết các bài toán thống kê trên máy tính với số liệu thực: mô<br>phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.</p><p><strong>Tài liệu học tập</strong><br>1. Trần Tuấn Điệp - Lý Hoàng Tú, Giáo trình lý thuyết xác suất và thống kê toán, 1979<br>2. Nguyễn Bác Văn, Xác suất và xử lý số liệu thống kê, 1996<br>3. Đặng Hùng Thằng, Mở đầu về lý thuyết xác suất và các ứng dụng</p>\"},{\"id\":484681691872,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":760958618538,\"name\":\"Giải tích hàm\",\"description\":\"<p>Mã môn học: MTH10403<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Điều kiện đăng ký học phần:<br>- Bắt buộc: cho sinh viên theo hướng Toán học (Giải tích, Giải tích số, Đại số, Cơ<br>học, Xác suất thống kê, Tối ưu và hệ thống).<br>- Tự chọn: Môn tự chọn thỏa yêu cầu môn bắt buộc chung hoặc bắt buộc riêng<br>cho các hướng Tin học, các chuyên ngành Toán Tài chính, Sư phạm Toán.<br>- Đại cương:<br>- Các môn học tiên quyết: Học phần học trước và đạt: Giải tích 1A, 2A, 3A.<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Giải tích hàm là nơi sinh viên có những hiểu biết đầu tiên cơ bản về<br>các không gian vô hạn chiều. Các kiến thức này là không thể thiếu cho nhiều chuyên<br>ngành Toán cả lí thuyết lẫn ứng dụng. Đây là nơi mà khả năng tiếp thu và sử dụng các lí<br>luận toán học trừu tượng và chính xác bước đầu được rèn luyện và kiểm tra.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: hiểu và vận dụng được vào tình huống cụ thể một số khái niệm và kết<br>quả căn bản về các không gian Euclid, không gian l^p, L^p, không gian các hàm<br>bị chặn, không gian các hàm liên tục, không gian các ánh xạ tuyến tính liên tục,<br>không gian Hilbert.<br>- Kỹ năng: hiểu được một cách có hệ thống về các đối tượng trên, nắm được các<br>quan hệ giữa các khái niệm và các kết quả. Thực hiện được một số lí luận không<br>có sẵn. Phân biệt và phản biện được những lí luận chưa đạt yêu cầu chính xác<br>của toán học.<br>- Thái độ, chuyên cần: Thấy được nhu cầu phát triển tổng quát hóa qua đó giúp<br>giải quyết những vấn đề ứng dụng, từ đó có thái độ học tập nghiêm túc, chủ<br>động, tự giác.<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>Không gian metric, không gian định chuẩn, ánh xạ tuyến tính liên tục giữa hai không<br>gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn này được<br>coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ ích<br>cho các mô hình toán học dùng không gian hàm và không gian vô hạn chiều (trong Đại<br>số, Tối ưu, Xác suất-Thống kê, Phương trình đạo hàm riêng, Giải tích, Phương pháp<br>Toán trong Tin học, ...).</p><p><strong>Tài liệu học tập</strong><br>1. Đinh Ngọc Thanh - Huỳnh Quang Vũ, Bài giảng Giải tích hàm,<br><a href=\\\"http://www.math.hcmus.edu.vn/~hqvu/fa.pdf\\\">http://www.math.hcmus.edu.vn/~hqvu/fa.pdf</a><br>2. Dương Minh Đức, Giải tích hàm, 2005<br>3. Đinh Ngọc Thanh - Đặng Đức Trọng, Giải tích hàm, 2011</p>\"},{\"id\":555779548591,\"name\":\"PT Xử lý ảnh\",\"description\":\"<p>Mã môn học: MTH10317<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần học trước hoặc tiên quyết: Toán cao cấp A1, A2, A3, Cấu trúc dữ liệu.<br>- Học phần song hành: không.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản.<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Giới thiệu tổng quan về xử lý và phân tích ảnh số. Trình bày từ các<br>khái niệm cơ bản nhất của xử lý ảnh đến các phương pháp xử lý trong ảnh xám và ảnh<br>màu, như các phương pháp lọc nhiễu, làm nổi bật cạnh, phép toán hình thái học,<br>wavelet, phân đoạn ảnh, tìm biên, đa giác và mô tả chúng. Và các phương phép nén ảnh<br>cũng như kỹ thuật watermarking cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các phương pháp xử lý ảnh, nén ánh, wartermarking<br>- Kỹ năng: ứng dụng kỹ thuật xử lý ảnh<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Nội dung môn học</strong><br>- Giới thiệu tổng quan và kiến thức cơ bản về ảnh số<br>- Phép biến đổi cường độ và phép lọc<br>- Phép lọc trong miền tần số<br>- Phục hồi và tái cấu trúc ảnh<br>- Xử lý ảnh màu<br>- Xử lý bằng phép biến đổi wavelet và đa phân giải<br>- Nén ảnh<br>- Xử lý ảnh bằng phép toán hình thái học<br>- Phân đoạn ảnh<br>- Biểu diễn và mô tả</p><p><strong>Tài liệu học tập</strong><br>1. Rafael C.Gonzalez - Richard E.Woods, Digital image processing, 2010<br>2. William K.Pratt, Digital image processing, 1991<br>3. Ngô Diên Tập, Xử lý ảnh bằng máy tính, 1997</p>\"},{\"id\":835938324802,\"name\":\"NM Trí Tuệ NT\",\"description\":\"<p>Mã học phần: MTH10318<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần học trước: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần song hành: Không có.<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình Visual C, Matlab.<br><strong>Mục tiêu môn học:</strong><br>Môn học cung cấp môt số kiến thức cơ bản của khoa học trí tuệ nhân tạo. Nội dung ̣<br>chính gồm: Các phương pháp giải quyết vấn đề và các áp dụng. Chú trọng phương pháp<br>heuristic. Trình bày một số phương pháp biểu diễn tri thức và môt số kỹ thuât xử lý tri<br>thức. Vấn đề lâp luận gần đúng. Giới thiệu một số hệ tri thức. Giới thiệu một số mô<br>hình và phương pháp khác. Khái niệm Ontology và Agent. Đồng thời môn học cũng<br>giới thiệu các kỹ thuật tính toán mềm như: Fuzzy, mạng Noron, thuật giải di truyền.<br><strong>Mô tả môn học</strong><br>• Tổng quan về khoa học TTNT<br>• Không gian trạng thái và vấn đề tìm kiếm<br>• Biểu diễn tri thức<br>• Giới thiệu về máy học và khám phá tri thức<br>• Giới thiệu Ontology và Intelligent Agent<br>• Giới thiệu Thuật giải di truyển(GA) ̣<br>• Giới thiệu Fuzzy Logic<br>• Mạng Neuron Network</p><p><strong>Tài liệu học tập:</strong><br>[1]. Trí tuệ nhân tạo: các phương pháp giải quyết vấn đề và kỹ thuật xử lý tri thức,<br>Nguyễn Thanh Thủy, 1996, NXB Giáo Dục<br>[2]. Giải một bài toán trên máy tính thư thế nào (1, 2, 3), Hoàng Kiếm, 2004, NXB<br>Giáo Dục<br>[3]. Trí tuệ nhân tạo, Đinh Mạnh Tường, 2002, NXB Khoa học và kỹ thuật<br>[4]. Máy học, Nguyễn Đình Thúc, 2002, NXB Lao động xả hội<br>[5]. Lập trình tiến hoá, Nguyễn Đình Thúc, 2001, NXB Giáo dục<br>[6]. Mạng Noron phương pháp và ứng dụng, Nguyễn Đình Thúc,2000, NXB Giáo<br>dục<br>[7]. Lý thuyết điều khiển mờ, Phan Xuân Minh, Nguyễn Doãn Phước, NXB Khoa<br>học và kỹ thuật<br>[8]. Logic Mờ Và Ứng Dụng, Đặng Thanh Hà, B. Bouchon Meunier, Hồ Thuần,<br>2007, NXB ĐH Quốc gia HN<br>[9]. Công nghệ phần mềm hướng tác tử, Lê Tấn Hùng, Từ Minh Phương, Huỳnh<br>Quyết Thắng, 2006, NXB Khoa học và kỹ thuật<br>[10].<br>[11]. Artificial Intelligence A Modern Approach, Stuart J. Russell, Peter Norvig,<br>1995, Prentice Hall<br>[12]. Problem – Solving and Articifial Intelligent, Jean – Louis Laurière, 1990,<br>Prentice Hall<br>[13]. An Introduction to Genetic Algorithms, Melanie Mitchell, 1999, MIT Press<br>[14]. Practical genetic algorithms, Randy L. Haupt, Sue Ellen Haupt, 2004, John<br>Wiley &amp; Sons, Inc., Hoboken, New Jersey<br>[15]. C++ Neural Networks and Fuzzy Logic, Valluru B. Rao, 1995, M&amp;T<br>Books, IDG Books Worldwide, Inc<br>[16]. Introduction to fuzzy sets, fuzzy logic, and fuzzy control systems , Guanrong<br>Chen,Trung Tat Pham, 2001, CRC Press LLC<br>[17]. Discovering Knowledge in Data: An Introduction to Data Mining, Daniel T.<br>Larose, 2004, John Wiley &amp; Sons Inc<br>[18]. First course on fuzzy theory and applications, Kwang H. Lee, 2005,<br>SpringerVerlag Berlin Heidelberg<br>[19]. Fuzzy logic: a practical approach / F. Martin McNeill, Ellen Thro, 1994 by<br>Academic Press<br>[20]. Fusion of Neural Networks, Fuzzy Systems and Genetic Algorithms: Industrial<br>Applications, Lakhmi C. Jain; N.M. Martin, 1998, CRC Press<br>[21]. Foundations of Neural Networks, Fuzzy Systems, and Knowledge Engineering,<br>Nikola K. Kasabov, 1998, MIT Press</p>\"},{\"id\":451497985216,\"name\":\"XL Tín hiệu số\",\"description\":\"<p>Mã học phần: MTH10320<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Không<br>Học phần học trước: Cấu trúc dữ liệu và thuật giải, Giải tích hàm<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình Matlab.<br><strong>Tóm tắt nội dung học phần</strong><br>- Phân tích tần số của tín hiệu – lý thuyết tổng quát.<br>- Phân tích Fourier.<br>- Khai triển Wavelet.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cơ sở toán học của xử lý tín hiệu số, biểu diễn tần số của tín hiệu như là tổ<br>hợp tuyến tính các hàm cơ sở, các cách biểu diễn tần số cổ điển (Fourier) và hiện đại<br>(wavelet).</p><p><strong>Tài liệu học tập:</strong><br>[1] Lawrence R. Rabiner, Bernard Gold, Theory and Application of Digital Signal ,<br>Prentice Hall, 1975.<br>[2] Gilbert Strang, Truong Nguyen, Wavelet and Filter bank, SIAM, 1996.<br>[3] James H. McClellan, M. A. Yoder, Mark Yoder, DSP First: A Multimedia<br>Approach, Prentice Hall, 1998.</p>\"},{\"id\":331412971900,\"name\":\"XL Đa Chiều\",\"description\":\"<p>Mã môn học: MTH10323<br>Số tín chỉ: 4<br>Loại môn học (check vào các ô):<br>- Bắt buộc:&nbsp;<br>- Tự chọn:&nbsp;<br>- Đại cương:<br>- Cơ sở ngành:<br>- Chuyên ngành:<br>Các môn học tiên quyết: xác suất thống kê, đại số tuyến tính<br>Các yêu cầu đối với môn học (nếu có): matlab cơ bản<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết<br>- Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 45 tiết<br>Bộ môn phụ trách môn học: Bộ môn Ứng Dụng Tin học<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản về xử lý dữ liệu tín hiệu đa chiều<br>trong phân loại dữ liệu hay nhận dạng đối tượng, khai khóang dữ liệu, phân tích dữ liệu,<br>thống kê, máy học, ….<br>Nội dung chính của chương trình sẽ trình bày các ý tưởng toán học của: phương pháp<br>phân tích thành phần chính, phân tích rời rạc, phân tích thành phần độc lập, ma trận<br>không âm, một số phương thức nhân. Sau đó sinh viên sẽ áp dụng các kiến thức để giải<br>quyết những bài tập lớn.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các kiến thức cơ bản về xử lý đa chiều<br>- Kỹ năng: phân tích, áp dụng các thuật toán trong thực tế<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>- Phương pháp PCA<br>- Phân tích rời rạc<br>- Phương pháp ICA<br>- Phân tích ma trận không âm<br>- Phương pháp Nhân (Kernel)<br>- Phương pháp gom nhóm và phân loại</p><p><strong>Tài liệu học tập</strong><br>1. Richard O.Duda - Peter Elliot Hart - David G. Stork, Pattern classification,<br>2004<br>2. Boris Mirkin, Mathematical classification and clustering, 1996<br>3. Martin E. Modell - Data analysis, data modeling, and classification - 1992</p>\"},{\"id\":380656715791,\"name\":\"Máy học\",\"description\":\"<p>Mã môn học: MTH10354<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Nhập môn trí tuệ nhân tạo<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Giới thiệu cơ sở hình học.<br>Mô hình một Camera<br>Mô hình hai Camera và n Camera<br>Một số thuật toán và kỹ thuật trong phân tích ảnh<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Trình bày các kiến thức nâng cao của máy học.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: Nắm vững các kiến thức nâng cao của máy học<br>• Kỹ năng: áp dụng vào bài toán thực tế<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Tom M.Mitchell, Machine learning, 1997<br>2. Stuart Russell and Peter Norvig, Artificial Intelligence: A Modern Approach, 2010<br>3. Trần Trọng Khiêm, Statistical machine learning approaches to credit risk, 2015</p>\"},{\"id\":848675313136,\"name\":\"PT Thuật Toán\",\"description\":\"<p>Mã môn học: MTH10325<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Nắm vững logic Toán học.<br><strong>Nội dung môn học:</strong><br>- Tổng quan về thuật toán và độ phức tạp của thuật toán.<br>- Hàm sinh và ứng dụng.<br>- Hoán vị, nghịch thế và ứng dụng.<br>- Đánh giá độ phức tạp của một số thuật toán thông dụng<br><strong>Mục tiêu môn học</strong><br>Trang bị cho sinh viên các kiến thức và kỹ năng lập trình đánh giá độ phức tạp của thuật<br>toán, tính đúng đắn của thuật toán, hàm sinh và ứng dụng; hoán vị và ứng dụng; đánh<br>giá một số thuật toán thông dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Kenneth H. Rosen, Discrete Mathematics and Its Application, McGraw-Hill, 1998.<br>[2] Herbert S.Wilf, Algorithm Complexity, Internet Edition 1994.<br>[3] Herbert S.Wilf, Generating Functionology, Internet Edition 1994.<br>[4] Ian Parberry, Lecture Notes on Algorithm Analysis and Computational Complexity,<br>Internet Editon 2001.</p>\"},{\"id\":464304794168,\"name\":\"Nhận dạng mẫu\",\"description\":\"<p>Mã môn học: MTH10340<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Kỹ thuật lập trình,Trí tuệ nhân tạo<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>- Trình bày ngắn gọn vai trò, vị trí học phần/môn học, kiến thức sẽ trang bị cho sinh viên,<br>quan hệ với các học phần/môn học khác trong chương trình đào tạo)<br>- Các phương pháp thống kê trong nhận dạng mẫu.<br>- Ước lượng thông số.<br>- Học có giám sát.<br>- Học không có giám sát<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Giới thiệu những cơ sở toán học về nhận dạng mẫu với các ví dụ từ<br>nhiều ứng dụng khác nhau. Các kỹ thuật xử lý dữ liệu đa chiều, các thuật giải phân loại,<br>hồi quy cũng như clustering dữ liệu sẽ được trình bày. Những sinh viên và học viên cao<br>học quan tâm về lĩnh vực nhận dạng mẫu, mạng neural nhân tạo, SVM (support vector<br>machine) và tầm nhìn máy tính (computer vision) có thể tham gia học phân này.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các kiến thức cơ bản về biến hình và xử lý ảnh<br>- Kỹ năng: phân tích, ứng dụng các thuật toán trong thực tế<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Richard O.Duda - Peter Elliot Hart - David G. Stork, Pattern classification, 2004<br>2. C. Bishop, Pattern Recognition and Machine Learning, 2007<br>3. Trevor Hastie - Robert Tibshirani - Jerome Friedman, The elements of statistical<br>learning : data mining, inference, and prediction, 2009</p>\"},{\"id\":828508582567,\"name\":\"Seminar PPT\",\"description\":\"<p>Mã môn học: MTH10346<br>Thuộc khối kiến thức: Đại cương<br>Số tín chỉ: 04(4LT)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết:<br>- Học phần học trước:<br>- Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV:<br>Nội dung môn học:<br>Sinh viên sẽ làm việc trực tiếp với giáo viên hướng dẫn về một chủ đề.<br>Phương pháp tìm tài liệu cần thiết.<br>Đọc tài liệu và tìm hiểu các kiến thức liên quan đến bài toán được nhận.<br>Viết báo cáo và tình bày những kiến thức đã nhận được trong quá trình làm việc.<br>Mục tiêu môn học:<br>Mục tiêu chung: Trang bị cho SV các kiến thức cơ bản để chuẩn bị làm luận văn tốt<br>nghiệp.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: nắm vững các kiến thức cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Kỹ năng: các kỹ năng cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p>\"}]},{\"id\":655983144203,\"name\":\"Tính toán thông minh\",\"description\":\"<p>Khám phá và ứng dụng các kỹ thuật toán học trong trí tuệ nhân tạo và học máy, đóng góp vào sự phát triển của các hệ thống thông minh và tự động hóa.</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1684/332;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 13.17.04.png\\\" width=\\\"1684\\\" height=\\\"332\\\"></figure>\",\"categories\":[{\"id\":445940199367,\"name\":\"Cấu trúc dữ liệu và giải thuật\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p>&nbsp;</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":832391951706,\"name\":\"Đại số A2\",\"description\":\"<p>Mã môn học: MTH10402<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết: Đại số tuyến tính A1<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br>Tóm tắt nội dung môn học:<br>Học phần giới thiệu các kiến thức cơ bản về rút gọn toán tử tuyến tính (sự chéo hóa,<br>dạng chính tắc Jordan), không gian Eucclid, dạng song tuyến tính và dạng toàn phương.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: Trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính<br>nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững sự chéo hóa và Jordan hóa của toán tử tuyến tính trên các<br>KGVT hữu hạn chiều, cấu trúc của không gian Euclid, dang song tuyến tính và<br>dạng toàn phương.<br>- Kỹ năng: Chéo hóa và Jordan hóa các ma trận trên các trường số, tính toán trong<br>không gian Euclid, biến đổi dạng toàn phương, sử dụng tốt MAPLE.<br>- Thái độ, chuyên cần: Nghiêm túc, cầu tiến.</p><p><strong>Tài liệu học tập</strong><br>1. Bùi Xuân Hải - Trần Ngọc Hội - Lê Văn Luyện, Đại số tuyến tính và Ứng dụng, Tập 2,<br>2017<br>2. Ngô Việt Trung, Giáo trình Đại số tuyến tính, 2001<br>3. Nguyễn Hữu Việt Hưng, Đại số tuyến tính, 2004</p>\"},{\"id\":751115773376,\"name\":\"LT Độ Đo & XS\",\"description\":\"<p>Mã học phần: MTH10401<br>Thuộc khối kiến thức: Cơ sở ngành<br>Bộ môn – Khoa phụ trách: Toán Tin<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Số tiết lý thuyết: 45<br>Số tiết thực hành, thực tập: 0<br>Số tiết bài tập trên lớp: 30<br>Số tiết thảo luận:<br>Số tiết làm việc nhóm:<br>Số tiết tự học:<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt):<br>- Học phần học trước (các học phần SV phải đăng ký học trước): Giải tích A1-<br>Giải tích cơ sở, Giải tích A1 - Vi tích phân, Giải tích A2.<br>- Học phần song hành (SV phải đăng ký học trong cùng học kỳ):<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có):<br><strong>Mô tả môn học</strong><br>- Tiếng Việt: Trình bày về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến<br>ngẫu nhiên, luật số lớn và các định lý giới hạn.<br>- Tiếng Anh: Giving the introduction of elementary probability theory, abstract measure<br>theory, random variables, law of large numbers and limit theorems.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đ. Đ. Áng, Nhập môn giải tích, NXBGD, 1997.<br>[2] Đ. Đ. Áng, Lý thuyết tích phân, NXBGD, 1997.<br>[3] P. Billingsley, Probability and measure, 3rd edition, John Wiley &amp; Sons, 1995.<br>[4] W. M. Bolstad, Introduction to Bayesian statistics, 2nd edition, Wiley, 2007.<br>[5] M. Bouyssel, Intégrale de Lebesgue, Cépaduès-éditions, 1997.<br>[6] K. L. Chung, A course in probability theory, 3rd edition, Academic Press, 2001.<br>[7] D. M. Đức, Lý thuyết độ đo và tích phân, NXB Đại Học Quốc Gia Tp. HCM,<br>2006.<br>[8] W. Feller, An introduction to probability theory and its applications, Vol. I, John<br>Wiley &amp; Sons, 1957.<br>[9] A. M. Mathai, H. J. Houbold, Special functions for applied scientists, Springer,<br>New York, 2008.<br>[10] P.H. Quân, Đ.N. Thanh, Xác suất thống kê, NXBGD, 2011<br>[11] S. Ross, A first course in probability, 5th edition, Prentice Hall, 1998.<br>[12] W. Rudin, Real and complex analysis, 3rd edition, McGraw-Hill, 1986.<br>[13] N. D. Tiến, V. V. Yên, Lý thuyết xác suất, NXBGD, 2006.<br>[14] Y. Viniotis, Probability and random processes for electrical engineers, McGrawHill,<br>1998.<br>[15] T. A. Dũng, Lý thuyết xác suất và thống kê toán, NXB Đại Học Quốc Gia Tp.<br>HCM, 2007.</p>\"},{\"id\":687368983157,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":651968510905,\"name\":\"LT Thống kê\",\"description\":\"<p>Mã môn học: MTH10404<br>Số tín chỉ: 03 (1LT+1BT+1TH)<br>Số tiết đối với các hoạt động:<br>Nghe giảng lý thuyết: 15 tiết<br>Làm bài tập trên lớp: 30 tiết<br>Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>Thảo luận: 30 tiết<br>Tự học: 60 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê toán –<br>phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê; sử dụng<br>thành thạo các phần mềm thống kê chuyên dụng (R, SPSS, … ) để phân tích, xử lý số<br>liệu thống kê.<br><strong>Mô tả vắn tắt nội dung môn học:</strong> (trình bày ngắn gọn vai trò, vị trí học phần/môn học,<br>kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong<br>chương trình đào tạo)<br>Phần lý thuyết: bao gồm các kiến thức về mẫu, các đặc trưng mẫu, ước lượng, các<br>phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai, tỷ lệ; xác định cỡ<br>mẫu. Kiểm định giả thuyết: trường hợp một mẫu, hai mẫu; kiểm định giả thuyết về phân<br>phối, kiểm định giả thuyết về tính độc lập. Hồi quy và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần<br>mềm thống kê R để giải quyết các bài toán thống kê trên máy tính với số liệu thực: mô<br>phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.</p><p><strong>Tài liệu học tập</strong><br>1. Trần Tuấn Điệp - Lý Hoàng Tú, Giáo trình lý thuyết xác suất và thống kê toán, 1979<br>2. Nguyễn Bác Văn, Xác suất và xử lý số liệu thống kê, 1996<br>3. Đặng Hùng Thằng, Mở đầu về lý thuyết xác suất và các ứng dụng</p>\"},{\"id\":236091390509,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":614708179142,\"name\":\"Giải tích hàm\",\"description\":\"<p>Mã môn học: MTH10403<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Điều kiện đăng ký học phần:<br>- Bắt buộc: cho sinh viên theo hướng Toán học (Giải tích, Giải tích số, Đại số, Cơ<br>học, Xác suất thống kê, Tối ưu và hệ thống).<br>- Tự chọn: Môn tự chọn thỏa yêu cầu môn bắt buộc chung hoặc bắt buộc riêng<br>cho các hướng Tin học, các chuyên ngành Toán Tài chính, Sư phạm Toán.<br>- Đại cương:<br>- Các môn học tiên quyết: Học phần học trước và đạt: Giải tích 1A, 2A, 3A.<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Giải tích hàm là nơi sinh viên có những hiểu biết đầu tiên cơ bản về<br>các không gian vô hạn chiều. Các kiến thức này là không thể thiếu cho nhiều chuyên<br>ngành Toán cả lí thuyết lẫn ứng dụng. Đây là nơi mà khả năng tiếp thu và sử dụng các lí<br>luận toán học trừu tượng và chính xác bước đầu được rèn luyện và kiểm tra.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: hiểu và vận dụng được vào tình huống cụ thể một số khái niệm và kết<br>quả căn bản về các không gian Euclid, không gian l^p, L^p, không gian các hàm<br>bị chặn, không gian các hàm liên tục, không gian các ánh xạ tuyến tính liên tục,<br>không gian Hilbert.<br>- Kỹ năng: hiểu được một cách có hệ thống về các đối tượng trên, nắm được các<br>quan hệ giữa các khái niệm và các kết quả. Thực hiện được một số lí luận không<br>có sẵn. Phân biệt và phản biện được những lí luận chưa đạt yêu cầu chính xác<br>của toán học.<br>- Thái độ, chuyên cần: Thấy được nhu cầu phát triển tổng quát hóa qua đó giúp<br>giải quyết những vấn đề ứng dụng, từ đó có thái độ học tập nghiêm túc, chủ<br>động, tự giác.<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>Không gian metric, không gian định chuẩn, ánh xạ tuyến tính liên tục giữa hai không<br>gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn này được<br>coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ ích<br>cho các mô hình toán học dùng không gian hàm và không gian vô hạn chiều (trong Đại<br>số, Tối ưu, Xác suất-Thống kê, Phương trình đạo hàm riêng, Giải tích, Phương pháp<br>Toán trong Tin học, ...).</p><p><strong>Tài liệu học tập</strong><br>1. Đinh Ngọc Thanh - Huỳnh Quang Vũ, Bài giảng Giải tích hàm,<br><a href=\\\"http://www.math.hcmus.edu.vn/~hqvu/fa.pdf\\\">http://www.math.hcmus.edu.vn/~hqvu/fa.pdf</a><br>2. Dương Minh Đức, Giải tích hàm, 2005<br>3. Đinh Ngọc Thanh - Đặng Đức Trọng, Giải tích hàm, 2011</p>\"},{\"id\":457901489207,\"name\":\"Phân tích xử lý ảnh\",\"description\":\"<p>Mã môn học: MTH10317<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần học trước hoặc tiên quyết: Toán cao cấp A1, A2, A3, Cấu trúc dữ liệu.<br>- Học phần song hành: không.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản.<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Giới thiệu tổng quan về xử lý và phân tích ảnh số. Trình bày từ các<br>khái niệm cơ bản nhất của xử lý ảnh đến các phương pháp xử lý trong ảnh xám và ảnh<br>màu, như các phương pháp lọc nhiễu, làm nổi bật cạnh, phép toán hình thái học,<br>wavelet, phân đoạn ảnh, tìm biên, đa giác và mô tả chúng. Và các phương phép nén ảnh<br>cũng như kỹ thuật watermarking cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các phương pháp xử lý ảnh, nén ánh, wartermarking<br>- Kỹ năng: ứng dụng kỹ thuật xử lý ảnh<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Nội dung môn học</strong><br>- Giới thiệu tổng quan và kiến thức cơ bản về ảnh số<br>- Phép biến đổi cường độ và phép lọc<br>- Phép lọc trong miền tần số<br>- Phục hồi và tái cấu trúc ảnh<br>- Xử lý ảnh màu<br>- Xử lý bằng phép biến đổi wavelet và đa phân giải<br>- Nén ảnh<br>- Xử lý ảnh bằng phép toán hình thái học<br>- Phân đoạn ảnh<br>- Biểu diễn và mô tả</p><p><strong>Tài liệu học tập</strong><br>1. Rafael C.Gonzalez - Richard E.Woods, Digital image processing, 2010<br>2. William K.Pratt, Digital image processing, 1991<br>3. Ngô Diên Tập, Xử lý ảnh bằng máy tính, 1997</p>\"},{\"id\":274958622950,\"name\":\"NM Trí Tuệ NT\",\"description\":\"<p>Mã học phần: MTH10318<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần học trước: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần song hành: Không có.<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình Visual C, Matlab.<br><strong>Mục tiêu môn học:</strong><br>Môn học cung cấp môt số kiến thức cơ bản của khoa học trí tuệ nhân tạo. Nội dung ̣<br>chính gồm: Các phương pháp giải quyết vấn đề và các áp dụng. Chú trọng phương pháp<br>heuristic. Trình bày một số phương pháp biểu diễn tri thức và môt số kỹ thuât xử lý tri<br>thức. Vấn đề lâp luận gần đúng. Giới thiệu một số hệ tri thức. Giới thiệu một số mô<br>hình và phương pháp khác. Khái niệm Ontology và Agent. Đồng thời môn học cũng<br>giới thiệu các kỹ thuật tính toán mềm như: Fuzzy, mạng Noron, thuật giải di truyền.<br><strong>Mô tả môn học</strong><br>• Tổng quan về khoa học TTNT<br>• Không gian trạng thái và vấn đề tìm kiếm<br>• Biểu diễn tri thức<br>• Giới thiệu về máy học và khám phá tri thức<br>• Giới thiệu Ontology và Intelligent Agent<br>• Giới thiệu Thuật giải di truyển(GA) ̣<br>• Giới thiệu Fuzzy Logic<br>• Mạng Neuron Network</p><p><strong>Tài liệu học tập:</strong><br>[1]. Trí tuệ nhân tạo: các phương pháp giải quyết vấn đề và kỹ thuật xử lý tri thức,<br>Nguyễn Thanh Thủy, 1996, NXB Giáo Dục<br>[2]. Giải một bài toán trên máy tính thư thế nào (1, 2, 3), Hoàng Kiếm, 2004, NXB<br>Giáo Dục<br>[3]. Trí tuệ nhân tạo, Đinh Mạnh Tường, 2002, NXB Khoa học và kỹ thuật<br>[4]. Máy học, Nguyễn Đình Thúc, 2002, NXB Lao động xả hội<br>[5]. Lập trình tiến hoá, Nguyễn Đình Thúc, 2001, NXB Giáo dục<br>[6]. Mạng Noron phương pháp và ứng dụng, Nguyễn Đình Thúc,2000, NXB Giáo<br>dục<br>[7]. Lý thuyết điều khiển mờ, Phan Xuân Minh, Nguyễn Doãn Phước, NXB Khoa<br>học và kỹ thuật<br>[8]. Logic Mờ Và Ứng Dụng, Đặng Thanh Hà, B. Bouchon Meunier, Hồ Thuần,<br>2007, NXB ĐH Quốc gia HN<br>[9]. Công nghệ phần mềm hướng tác tử, Lê Tấn Hùng, Từ Minh Phương, Huỳnh<br>Quyết Thắng, 2006, NXB Khoa học và kỹ thuật<br>[10].<br>[11]. Artificial Intelligence A Modern Approach, Stuart J. Russell, Peter Norvig,<br>1995, Prentice Hall<br>[12]. Problem – Solving and Articifial Intelligent, Jean – Louis Laurière, 1990,<br>Prentice Hall<br>[13]. An Introduction to Genetic Algorithms, Melanie Mitchell, 1999, MIT Press<br>[14]. Practical genetic algorithms, Randy L. Haupt, Sue Ellen Haupt, 2004, John<br>Wiley &amp; Sons, Inc., Hoboken, New Jersey<br>[15]. C++ Neural Networks and Fuzzy Logic, Valluru B. Rao, 1995, M&amp;T<br>Books, IDG Books Worldwide, Inc<br>[16]. Introduction to fuzzy sets, fuzzy logic, and fuzzy control systems , Guanrong<br>Chen,Trung Tat Pham, 2001, CRC Press LLC<br>[17]. Discovering Knowledge in Data: An Introduction to Data Mining, Daniel T.<br>Larose, 2004, John Wiley &amp; Sons Inc<br>[18]. First course on fuzzy theory and applications, Kwang H. Lee, 2005,<br>SpringerVerlag Berlin Heidelberg<br>[19]. Fuzzy logic: a practical approach / F. Martin McNeill, Ellen Thro, 1994 by<br>Academic Press<br>[20]. Fusion of Neural Networks, Fuzzy Systems and Genetic Algorithms: Industrial<br>Applications, Lakhmi C. Jain; N.M. Martin, 1998, CRC Press<br>[21]. Foundations of Neural Networks, Fuzzy Systems, and Knowledge Engineering,<br>Nikola K. Kasabov, 1998, MIT Press</p>\"},{\"id\":889056740745,\"name\":\"XL Tín Hiệu Số\",\"description\":\"<p>Mã học phần: MTH10320<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Không<br>Học phần học trước: Cấu trúc dữ liệu và thuật giải, Giải tích hàm<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình Matlab.<br><strong>Tóm tắt nội dung học phần</strong><br>- Phân tích tần số của tín hiệu – lý thuyết tổng quát.<br>- Phân tích Fourier.<br>- Khai triển Wavelet.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cơ sở toán học của xử lý tín hiệu số, biểu diễn tần số của tín hiệu như là tổ<br>hợp tuyến tính các hàm cơ sở, các cách biểu diễn tần số cổ điển (Fourier) và hiện đại<br>(wavelet).</p><p><strong>Tài liệu học tập:</strong><br>[1] Lawrence R. Rabiner, Bernard Gold, Theory and Application of Digital Signal ,<br>Prentice Hall, 1975.<br>[2] Gilbert Strang, Truong Nguyen, Wavelet and Filter bank, SIAM, 1996.<br>[3] James H. McClellan, M. A. Yoder, Mark Yoder, DSP First: A Multimedia<br>Approach, Prentice Hall, 1998.</p>\"},{\"id\":301751790415,\"name\":\"XL Đa Chiều\",\"description\":\"<p>Mã môn học: MTH10323<br>Số tín chỉ: 4<br>Loại môn học (check vào các ô):<br>- Bắt buộc:&nbsp;<br>- Tự chọn:&nbsp;<br>- Đại cương:<br>- Cơ sở ngành:<br>- Chuyên ngành:<br>Các môn học tiên quyết: xác suất thống kê, đại số tuyến tính<br>Các yêu cầu đối với môn học (nếu có): matlab cơ bản<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết<br>- Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 45 tiết<br>Bộ môn phụ trách môn học: Bộ môn Ứng Dụng Tin học<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản về xử lý dữ liệu tín hiệu đa chiều<br>trong phân loại dữ liệu hay nhận dạng đối tượng, khai khóang dữ liệu, phân tích dữ liệu,<br>thống kê, máy học, ….<br>Nội dung chính của chương trình sẽ trình bày các ý tưởng toán học của: phương pháp<br>phân tích thành phần chính, phân tích rời rạc, phân tích thành phần độc lập, ma trận<br>không âm, một số phương thức nhân. Sau đó sinh viên sẽ áp dụng các kiến thức để giải<br>quyết những bài tập lớn.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các kiến thức cơ bản về xử lý đa chiều<br>- Kỹ năng: phân tích, áp dụng các thuật toán trong thực tế<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>- Phương pháp PCA<br>- Phân tích rời rạc<br>- Phương pháp ICA<br>- Phân tích ma trận không âm<br>- Phương pháp Nhân (Kernel)<br>- Phương pháp gom nhóm và phân loại</p><p><strong>Tài liệu học tập</strong><br>1. Richard O.Duda - Peter Elliot Hart - David G. Stork, Pattern classification,<br>2004<br>2. Boris Mirkin, Mathematical classification and clustering, 1996<br>3. Martin E. Modell - Data analysis, data modeling, and classification - 1992</p>\"},{\"id\":238936944683,\"name\":\"Máy học\",\"description\":\"<p>Mã môn học: MTH10354<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Nhập môn trí tuệ nhân tạo<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Giới thiệu cơ sở hình học.<br>Mô hình một Camera<br>Mô hình hai Camera và n Camera<br>Một số thuật toán và kỹ thuật trong phân tích ảnh<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Trình bày các kiến thức nâng cao của máy học.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: Nắm vững các kiến thức nâng cao của máy học<br>• Kỹ năng: áp dụng vào bài toán thực tế<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Tom M.Mitchell, Machine learning, 1997<br>2. Stuart Russell and Peter Norvig, Artificial Intelligence: A Modern Approach, 2010<br>3. Trần Trọng Khiêm, Statistical machine learning approaches to credit risk, 2015</p>\"},{\"id\":590712273130,\"name\":\"PT Thuật Toán\",\"description\":\"<p>Mã môn học: MTH10325<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Nắm vững logic Toán học.<br><strong>Nội dung môn học:</strong><br>- Tổng quan về thuật toán và độ phức tạp của thuật toán.<br>- Hàm sinh và ứng dụng.<br>- Hoán vị, nghịch thế và ứng dụng.<br>- Đánh giá độ phức tạp của một số thuật toán thông dụng<br><strong>Mục tiêu môn học</strong><br>Trang bị cho sinh viên các kiến thức và kỹ năng lập trình đánh giá độ phức tạp của thuật<br>toán, tính đúng đắn của thuật toán, hàm sinh và ứng dụng; hoán vị và ứng dụng; đánh<br>giá một số thuật toán thông dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Kenneth H. Rosen, Discrete Mathematics and Its Application, McGraw-Hill, 1998.<br>[2] Herbert S.Wilf, Algorithm Complexity, Internet Edition 1994.<br>[3] Herbert S.Wilf, Generating Functionology, Internet Edition 1994.<br>[4] Ian Parberry, Lecture Notes on Algorithm Analysis and Computational Complexity,<br>Internet Editon 2001.</p>\"},{\"id\":483037146646,\"name\":\"Seminar PPT\",\"description\":\"<p>Mã môn học: MTH10346<br>Thuộc khối kiến thức: Đại cương<br>Số tín chỉ: 04(4LT)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết:<br>- Học phần học trước:<br>- Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV:<br>Nội dung môn học:<br>Sinh viên sẽ làm việc trực tiếp với giáo viên hướng dẫn về một chủ đề.<br>Phương pháp tìm tài liệu cần thiết.<br>Đọc tài liệu và tìm hiểu các kiến thức liên quan đến bài toán được nhận.<br>Viết báo cáo và tình bày những kiến thức đã nhận được trong quá trình làm việc.<br>Mục tiêu môn học:<br>Mục tiêu chung: Trang bị cho SV các kiến thức cơ bản để chuẩn bị làm luận văn tốt<br>nghiệp.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: nắm vững các kiến thức cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Kỹ năng: các kỹ năng cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p>\"}]},{\"id\":365862119807,\"name\":\"Tính toán khoa học\",\"description\":\"<p>Ứng dụng toán học để giải quyết các vấn đề phức tạp trong các lĩnh vực khoa học, từ mô hình hóa sinh học đến dự báo thời tiết, góp phần vào sự tiến bộ của khoa học và công nghệ.</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1700/340;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 13.17.24.png\\\" width=\\\"1700\\\" height=\\\"340\\\"></figure>\",\"categories\":[{\"id\":577458594210,\"name\":\"Cấu trúc dữ liệu và giải thuật\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p>&nbsp;</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":932257026409,\"name\":\"Đại số A2\",\"description\":\"<p>Mã môn học: MTH10402<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết: Đại số tuyến tính A1<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br>Tóm tắt nội dung môn học:<br>Học phần giới thiệu các kiến thức cơ bản về rút gọn toán tử tuyến tính (sự chéo hóa,<br>dạng chính tắc Jordan), không gian Eucclid, dạng song tuyến tính và dạng toàn phương.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: Trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính<br>nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững sự chéo hóa và Jordan hóa của toán tử tuyến tính trên các<br>KGVT hữu hạn chiều, cấu trúc của không gian Euclid, dang song tuyến tính và<br>dạng toàn phương.<br>- Kỹ năng: Chéo hóa và Jordan hóa các ma trận trên các trường số, tính toán trong<br>không gian Euclid, biến đổi dạng toàn phương, sử dụng tốt MAPLE.<br>- Thái độ, chuyên cần: Nghiêm túc, cầu tiến.</p><p><strong>Tài liệu học tập</strong><br>1. Bùi Xuân Hải - Trần Ngọc Hội - Lê Văn Luyện, Đại số tuyến tính và Ứng dụng, Tập 2,<br>2017<br>2. Ngô Việt Trung, Giáo trình Đại số tuyến tính, 2001<br>3. Nguyễn Hữu Việt Hưng, Đại số tuyến tính, 2004</p>\"},{\"id\":101299646079,\"name\":\"LT Độ Đo & XS\",\"description\":\"<p>Mã học phần: MTH10401<br>Thuộc khối kiến thức: Cơ sở ngành<br>Bộ môn – Khoa phụ trách: Toán Tin<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Số tiết lý thuyết: 45<br>Số tiết thực hành, thực tập: 0<br>Số tiết bài tập trên lớp: 30<br>Số tiết thảo luận:<br>Số tiết làm việc nhóm:<br>Số tiết tự học:<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt):<br>- Học phần học trước (các học phần SV phải đăng ký học trước): Giải tích A1-<br>Giải tích cơ sở, Giải tích A1 - Vi tích phân, Giải tích A2.<br>- Học phần song hành (SV phải đăng ký học trong cùng học kỳ):<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có):<br><strong>Mô tả môn học</strong><br>- Tiếng Việt: Trình bày về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến<br>ngẫu nhiên, luật số lớn và các định lý giới hạn.<br>- Tiếng Anh: Giving the introduction of elementary probability theory, abstract measure<br>theory, random variables, law of large numbers and limit theorems.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đ. Đ. Áng, Nhập môn giải tích, NXBGD, 1997.<br>[2] Đ. Đ. Áng, Lý thuyết tích phân, NXBGD, 1997.<br>[3] P. Billingsley, Probability and measure, 3rd edition, John Wiley &amp; Sons, 1995.<br>[4] W. M. Bolstad, Introduction to Bayesian statistics, 2nd edition, Wiley, 2007.<br>[5] M. Bouyssel, Intégrale de Lebesgue, Cépaduès-éditions, 1997.<br>[6] K. L. Chung, A course in probability theory, 3rd edition, Academic Press, 2001.<br>[7] D. M. Đức, Lý thuyết độ đo và tích phân, NXB Đại Học Quốc Gia Tp. HCM,<br>2006.<br>[8] W. Feller, An introduction to probability theory and its applications, Vol. I, John<br>Wiley &amp; Sons, 1957.<br>[9] A. M. Mathai, H. J. Houbold, Special functions for applied scientists, Springer,<br>New York, 2008.<br>[10] P.H. Quân, Đ.N. Thanh, Xác suất thống kê, NXBGD, 2011<br>[11] S. Ross, A first course in probability, 5th edition, Prentice Hall, 1998.<br>[12] W. Rudin, Real and complex analysis, 3rd edition, McGraw-Hill, 1986.<br>[13] N. D. Tiến, V. V. Yên, Lý thuyết xác suất, NXBGD, 2006.<br>[14] Y. Viniotis, Probability and random processes for electrical engineers, McGrawHill,<br>1998.<br>[15] T. A. Dũng, Lý thuyết xác suất và thống kê toán, NXB Đại Học Quốc Gia Tp.<br>HCM, 2007.</p>\"},{\"id\":613924623950,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":950080967163,\"name\":\"LT Thống kê\",\"description\":\"<p>Mã môn học: MTH10404<br>Số tín chỉ: 03 (1LT+1BT+1TH)<br>Số tiết đối với các hoạt động:<br>Nghe giảng lý thuyết: 15 tiết<br>Làm bài tập trên lớp: 30 tiết<br>Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>Thảo luận: 30 tiết<br>Tự học: 60 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê toán –<br>phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê; sử dụng<br>thành thạo các phần mềm thống kê chuyên dụng (R, SPSS, … ) để phân tích, xử lý số<br>liệu thống kê.<br><strong>Mô tả vắn tắt nội dung môn học:</strong> (trình bày ngắn gọn vai trò, vị trí học phần/môn học,<br>kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong<br>chương trình đào tạo)<br>Phần lý thuyết: bao gồm các kiến thức về mẫu, các đặc trưng mẫu, ước lượng, các<br>phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai, tỷ lệ; xác định cỡ<br>mẫu. Kiểm định giả thuyết: trường hợp một mẫu, hai mẫu; kiểm định giả thuyết về phân<br>phối, kiểm định giả thuyết về tính độc lập. Hồi quy và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần<br>mềm thống kê R để giải quyết các bài toán thống kê trên máy tính với số liệu thực: mô<br>phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.</p><p><strong>Tài liệu học tập</strong><br>1. Trần Tuấn Điệp - Lý Hoàng Tú, Giáo trình lý thuyết xác suất và thống kê toán, 1979<br>2. Nguyễn Bác Văn, Xác suất và xử lý số liệu thống kê, 1996<br>3. Đặng Hùng Thằng, Mở đầu về lý thuyết xác suất và các ứng dụng</p>\"},{\"id\":774989159095,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":148969307831,\"name\":\"Giải tích hàm\",\"description\":\"<p>Mã môn học: MTH10403<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Điều kiện đăng ký học phần:<br>- Bắt buộc: cho sinh viên theo hướng Toán học (Giải tích, Giải tích số, Đại số, Cơ<br>học, Xác suất thống kê, Tối ưu và hệ thống).<br>- Tự chọn: Môn tự chọn thỏa yêu cầu môn bắt buộc chung hoặc bắt buộc riêng<br>cho các hướng Tin học, các chuyên ngành Toán Tài chính, Sư phạm Toán.<br>- Đại cương:<br>- Các môn học tiên quyết: Học phần học trước và đạt: Giải tích 1A, 2A, 3A.<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Giải tích hàm là nơi sinh viên có những hiểu biết đầu tiên cơ bản về<br>các không gian vô hạn chiều. Các kiến thức này là không thể thiếu cho nhiều chuyên<br>ngành Toán cả lí thuyết lẫn ứng dụng. Đây là nơi mà khả năng tiếp thu và sử dụng các lí<br>luận toán học trừu tượng và chính xác bước đầu được rèn luyện và kiểm tra.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: hiểu và vận dụng được vào tình huống cụ thể một số khái niệm và kết<br>quả căn bản về các không gian Euclid, không gian l^p, L^p, không gian các hàm<br>bị chặn, không gian các hàm liên tục, không gian các ánh xạ tuyến tính liên tục,<br>không gian Hilbert.<br>- Kỹ năng: hiểu được một cách có hệ thống về các đối tượng trên, nắm được các<br>quan hệ giữa các khái niệm và các kết quả. Thực hiện được một số lí luận không<br>có sẵn. Phân biệt và phản biện được những lí luận chưa đạt yêu cầu chính xác<br>của toán học.<br>- Thái độ, chuyên cần: Thấy được nhu cầu phát triển tổng quát hóa qua đó giúp<br>giải quyết những vấn đề ứng dụng, từ đó có thái độ học tập nghiêm túc, chủ<br>động, tự giác.<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>Không gian metric, không gian định chuẩn, ánh xạ tuyến tính liên tục giữa hai không<br>gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn này được<br>coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ ích<br>cho các mô hình toán học dùng không gian hàm và không gian vô hạn chiều (trong Đại<br>số, Tối ưu, Xác suất-Thống kê, Phương trình đạo hàm riêng, Giải tích, Phương pháp<br>Toán trong Tin học, ...).</p><p><strong>Tài liệu học tập</strong><br>1. Đinh Ngọc Thanh - Huỳnh Quang Vũ, Bài giảng Giải tích hàm,<br><a href=\\\"http://www.math.hcmus.edu.vn/~hqvu/fa.pdf\\\">http://www.math.hcmus.edu.vn/~hqvu/fa.pdf</a><br>2. Dương Minh Đức, Giải tích hàm, 2005<br>3. Đinh Ngọc Thanh - Đặng Đức Trọng, Giải tích hàm, 2011</p>\"},{\"id\":160428504761,\"name\":\"Phân tích xử lý ảnh\",\"description\":\"<p>Mã môn học: MTH10317<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần học trước hoặc tiên quyết: Toán cao cấp A1, A2, A3, Cấu trúc dữ liệu.<br>- Học phần song hành: không.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản.<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Giới thiệu tổng quan về xử lý và phân tích ảnh số. Trình bày từ các<br>khái niệm cơ bản nhất của xử lý ảnh đến các phương pháp xử lý trong ảnh xám và ảnh<br>màu, như các phương pháp lọc nhiễu, làm nổi bật cạnh, phép toán hình thái học,<br>wavelet, phân đoạn ảnh, tìm biên, đa giác và mô tả chúng. Và các phương phép nén ảnh<br>cũng như kỹ thuật watermarking cơ bản.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các phương pháp xử lý ảnh, nén ánh, wartermarking<br>- Kỹ năng: ứng dụng kỹ thuật xử lý ảnh<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Nội dung môn học</strong><br>- Giới thiệu tổng quan và kiến thức cơ bản về ảnh số<br>- Phép biến đổi cường độ và phép lọc<br>- Phép lọc trong miền tần số<br>- Phục hồi và tái cấu trúc ảnh<br>- Xử lý ảnh màu<br>- Xử lý bằng phép biến đổi wavelet và đa phân giải<br>- Nén ảnh<br>- Xử lý ảnh bằng phép toán hình thái học<br>- Phân đoạn ảnh<br>- Biểu diễn và mô tả</p><p><strong>Tài liệu học tập</strong><br>1. Rafael C.Gonzalez - Richard E.Woods, Digital image processing, 2010<br>2. William K.Pratt, Digital image processing, 1991<br>3. Ngô Diên Tập, Xử lý ảnh bằng máy tính, 1997</p>\"},{\"id\":543801749563,\"name\":\"NM Trí Tuệ NT\",\"description\":\"<p>Mã học phần: MTH10318<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần học trước: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần song hành: Không có.<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình Visual C, Matlab.<br><strong>Mục tiêu môn học:</strong><br>Môn học cung cấp môt số kiến thức cơ bản của khoa học trí tuệ nhân tạo. Nội dung ̣<br>chính gồm: Các phương pháp giải quyết vấn đề và các áp dụng. Chú trọng phương pháp<br>heuristic. Trình bày một số phương pháp biểu diễn tri thức và môt số kỹ thuât xử lý tri<br>thức. Vấn đề lâp luận gần đúng. Giới thiệu một số hệ tri thức. Giới thiệu một số mô<br>hình và phương pháp khác. Khái niệm Ontology và Agent. Đồng thời môn học cũng<br>giới thiệu các kỹ thuật tính toán mềm như: Fuzzy, mạng Noron, thuật giải di truyền.<br><strong>Mô tả môn học</strong><br>• Tổng quan về khoa học TTNT<br>• Không gian trạng thái và vấn đề tìm kiếm<br>• Biểu diễn tri thức<br>• Giới thiệu về máy học và khám phá tri thức<br>• Giới thiệu Ontology và Intelligent Agent<br>• Giới thiệu Thuật giải di truyển(GA) ̣<br>• Giới thiệu Fuzzy Logic<br>• Mạng Neuron Network</p><p><strong>Tài liệu học tập:</strong><br>[1]. Trí tuệ nhân tạo: các phương pháp giải quyết vấn đề và kỹ thuật xử lý tri thức,<br>Nguyễn Thanh Thủy, 1996, NXB Giáo Dục<br>[2]. Giải một bài toán trên máy tính thư thế nào (1, 2, 3), Hoàng Kiếm, 2004, NXB<br>Giáo Dục<br>[3]. Trí tuệ nhân tạo, Đinh Mạnh Tường, 2002, NXB Khoa học và kỹ thuật<br>[4]. Máy học, Nguyễn Đình Thúc, 2002, NXB Lao động xả hội<br>[5]. Lập trình tiến hoá, Nguyễn Đình Thúc, 2001, NXB Giáo dục<br>[6]. Mạng Noron phương pháp và ứng dụng, Nguyễn Đình Thúc,2000, NXB Giáo<br>dục<br>[7]. Lý thuyết điều khiển mờ, Phan Xuân Minh, Nguyễn Doãn Phước, NXB Khoa<br>học và kỹ thuật<br>[8]. Logic Mờ Và Ứng Dụng, Đặng Thanh Hà, B. Bouchon Meunier, Hồ Thuần,<br>2007, NXB ĐH Quốc gia HN<br>[9]. Công nghệ phần mềm hướng tác tử, Lê Tấn Hùng, Từ Minh Phương, Huỳnh<br>Quyết Thắng, 2006, NXB Khoa học và kỹ thuật<br>[10].<br>[11]. Artificial Intelligence A Modern Approach, Stuart J. Russell, Peter Norvig,<br>1995, Prentice Hall<br>[12]. Problem – Solving and Articifial Intelligent, Jean – Louis Laurière, 1990,<br>Prentice Hall<br>[13]. An Introduction to Genetic Algorithms, Melanie Mitchell, 1999, MIT Press<br>[14]. Practical genetic algorithms, Randy L. Haupt, Sue Ellen Haupt, 2004, John<br>Wiley &amp; Sons, Inc., Hoboken, New Jersey<br>[15]. C++ Neural Networks and Fuzzy Logic, Valluru B. Rao, 1995, M&amp;T<br>Books, IDG Books Worldwide, Inc<br>[16]. Introduction to fuzzy sets, fuzzy logic, and fuzzy control systems , Guanrong<br>Chen,Trung Tat Pham, 2001, CRC Press LLC<br>[17]. Discovering Knowledge in Data: An Introduction to Data Mining, Daniel T.<br>Larose, 2004, John Wiley &amp; Sons Inc<br>[18]. First course on fuzzy theory and applications, Kwang H. Lee, 2005,<br>SpringerVerlag Berlin Heidelberg<br>[19]. Fuzzy logic: a practical approach / F. Martin McNeill, Ellen Thro, 1994 by<br>Academic Press<br>[20]. Fusion of Neural Networks, Fuzzy Systems and Genetic Algorithms: Industrial<br>Applications, Lakhmi C. Jain; N.M. Martin, 1998, CRC Press<br>[21]. Foundations of Neural Networks, Fuzzy Systems, and Knowledge Engineering,<br>Nikola K. Kasabov, 1998, MIT Press</p>\"},{\"id\":655646867669,\"name\":\"XL Tín Hiệu Số\",\"description\":\"<p>Mã học phần: MTH10320<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Không<br>Học phần học trước: Cấu trúc dữ liệu và thuật giải, Giải tích hàm<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình Matlab.<br><strong>Tóm tắt nội dung học phần</strong><br>- Phân tích tần số của tín hiệu – lý thuyết tổng quát.<br>- Phân tích Fourier.<br>- Khai triển Wavelet.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cơ sở toán học của xử lý tín hiệu số, biểu diễn tần số của tín hiệu như là tổ<br>hợp tuyến tính các hàm cơ sở, các cách biểu diễn tần số cổ điển (Fourier) và hiện đại<br>(wavelet).</p><p><strong>Tài liệu học tập:</strong><br>[1] Lawrence R. Rabiner, Bernard Gold, Theory and Application of Digital Signal ,<br>Prentice Hall, 1975.<br>[2] Gilbert Strang, Truong Nguyen, Wavelet and Filter bank, SIAM, 1996.<br>[3] James H. McClellan, M. A. Yoder, Mark Yoder, DSP First: A Multimedia<br>Approach, Prentice Hall, 1998.</p>\"},{\"id\":606705639901,\"name\":\"XL Đa Chiều\",\"description\":\"<p>Mã môn học: MTH10323<br>Số tín chỉ: 4<br>Loại môn học (check vào các ô):<br>- Bắt buộc:&nbsp;<br>- Tự chọn:&nbsp;<br>- Đại cương:<br>- Cơ sở ngành:<br>- Chuyên ngành:<br>Các môn học tiên quyết: xác suất thống kê, đại số tuyến tính<br>Các yêu cầu đối với môn học (nếu có): matlab cơ bản<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết<br>- Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 45 tiết<br>Bộ môn phụ trách môn học: Bộ môn Ứng Dụng Tin học<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản về xử lý dữ liệu tín hiệu đa chiều<br>trong phân loại dữ liệu hay nhận dạng đối tượng, khai khóang dữ liệu, phân tích dữ liệu,<br>thống kê, máy học, ….<br>Nội dung chính của chương trình sẽ trình bày các ý tưởng toán học của: phương pháp<br>phân tích thành phần chính, phân tích rời rạc, phân tích thành phần độc lập, ma trận<br>không âm, một số phương thức nhân. Sau đó sinh viên sẽ áp dụng các kiến thức để giải<br>quyết những bài tập lớn.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững các kiến thức cơ bản về xử lý đa chiều<br>- Kỹ năng: phân tích, áp dụng các thuật toán trong thực tế<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>- Phương pháp PCA<br>- Phân tích rời rạc<br>- Phương pháp ICA<br>- Phân tích ma trận không âm<br>- Phương pháp Nhân (Kernel)<br>- Phương pháp gom nhóm và phân loại</p><p><strong>Tài liệu học tập</strong><br>1. Richard O.Duda - Peter Elliot Hart - David G. Stork, Pattern classification,<br>2004<br>2. Boris Mirkin, Mathematical classification and clustering, 1996<br>3. Martin E. Modell - Data analysis, data modeling, and classification - 1992</p>\"},{\"id\":862566359680,\"name\":\"Máy học\",\"description\":\"<p>Mã môn học: MTH10354<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Nhập môn trí tuệ nhân tạo<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình<br><strong>Nội dung môn học:</strong><br>Giới thiệu cơ sở hình học.<br>Mô hình một Camera<br>Mô hình hai Camera và n Camera<br>Một số thuật toán và kỹ thuật trong phân tích ảnh<br><strong>Mục tiêu môn học:</strong><br>Mục tiêu chung: Trình bày các kiến thức nâng cao của máy học.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: Nắm vững các kiến thức nâng cao của máy học<br>• Kỹ năng: áp dụng vào bài toán thực tế<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Tom M.Mitchell, Machine learning, 1997<br>2. Stuart Russell and Peter Norvig, Artificial Intelligence: A Modern Approach, 2010<br>3. Trần Trọng Khiêm, Statistical machine learning approaches to credit risk, 2015</p>\"},{\"id\":277658947982,\"name\":\"PT Thuật Toán\",\"description\":\"<p>Mã môn học: MTH10325<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Nắm vững logic Toán học.<br><strong>Nội dung môn học:</strong><br>- Tổng quan về thuật toán và độ phức tạp của thuật toán.<br>- Hàm sinh và ứng dụng.<br>- Hoán vị, nghịch thế và ứng dụng.<br>- Đánh giá độ phức tạp của một số thuật toán thông dụng<br><strong>Mục tiêu môn học</strong><br>Trang bị cho sinh viên các kiến thức và kỹ năng lập trình đánh giá độ phức tạp của thuật<br>toán, tính đúng đắn của thuật toán, hàm sinh và ứng dụng; hoán vị và ứng dụng; đánh<br>giá một số thuật toán thông dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Kenneth H. Rosen, Discrete Mathematics and Its Application, McGraw-Hill, 1998.<br>[2] Herbert S.Wilf, Algorithm Complexity, Internet Edition 1994.<br>[3] Herbert S.Wilf, Generating Functionology, Internet Edition 1994.<br>[4] Ian Parberry, Lecture Notes on Algorithm Analysis and Computational Complexity,<br>Internet Editon 2001.</p>\"},{\"id\":659475334288,\"name\":\"Tính toán hiệu năng\",\"description\":\"<p>Mã học phần: MTH10321<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần học trước hoặc tiên quyết: Cấu trúc dữ liệu, toán rời rạc,<br>lập trình hướng đối tượng, lý thuyết đồ thị<br>- Học phần song hành: không.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: lập trình.<br><strong>Mô tả môn học:</strong><br>- Khái niệm cơ bản<br>- Tối ưu hóa<br>- Song song hóa<br>- GPU<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản trong xây dựng mô hình tính toán<br>song song. Một số phương pháp lập trình tính toán song song dựa trên chuẩn MPI và<br>PVM.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>• Kiến thức: Nắm vững các kiến thức cơ bản trong xây dựng và lập trình tính toán<br>song song<br>• Kỹ năng: Xây dựng mô hình và lập trình tính toán song song dựa trên chuẩn<br>MPI và PVM<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p><p><strong>Tài liệu học tập</strong><br>1. Joseph JáJá , An introduction to parallel algorithms, 1992<br>2. Michael J.Quinn, Parallel computing, 1994<br>3. John N.Tsitsiklis - Dimitri P.Bertsekas, Parallel and distributed computation: numerical<br>methods, 1997</p>\"},{\"id\":340878423708,\"name\":\"Seminar PPT\",\"description\":\"<p>Mã môn học: MTH10346<br>Thuộc khối kiến thức: Đại cương<br>Số tín chỉ: 04(4LT)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết:<br>- Học phần học trước:<br>- Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV:<br>Nội dung môn học:<br>Sinh viên sẽ làm việc trực tiếp với giáo viên hướng dẫn về một chủ đề.<br>Phương pháp tìm tài liệu cần thiết.<br>Đọc tài liệu và tìm hiểu các kiến thức liên quan đến bài toán được nhận.<br>Viết báo cáo và tình bày những kiến thức đã nhận được trong quá trình làm việc.<br>Mục tiêu môn học:<br>Mục tiêu chung: Trang bị cho SV các kiến thức cơ bản để chuẩn bị làm luận văn tốt<br>nghiệp.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: nắm vững các kiến thức cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Kỹ năng: các kỹ năng cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p>\"}]},{\"id\":978791920596,\"name\":\"Mã hoá\",\"description\":\"<p>Chuyên ngành này tập trung vào việc nghiên cứu và phát triển các phương pháp mã hóa và bảo mật thông tin, đảm bảo tính bảo mật cao cho dữ liệu trong thời đại số.</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1666/330;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 13.17.39.png\\\" width=\\\"1666\\\" height=\\\"330\\\"></figure>\",\"categories\":[{\"id\":525253100414,\"name\":\"Cấu trúc dữ liệu và giải thuật\",\"description\":\"<p>Mã môn học: MTH10405<br>Số tín chỉ: 04 (3LT + 1TH)<br>Thuộc khối kiến thức: Đại cương<br>Các môn học tiên quyết: Nhập môn lập trình<br>Các yêu cầu đối với môn học (nếu có): lập trình visual C<br>Số tiết đối với các hoạt động:<br>- Nghe giảng lý thuyết: 45 tiết<br>- Làm bài tập trên lớp: 10 tiết Thực hành, thực tập (ở PTN, nhà máy, thực tập thực<br>tế...): 30 tiết<br>- Thảo luận: 15 tiết<br>- Tự học: 30 tiết<br>- Khác: 5 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Trình bày một số kiến thức cơ bản của cấu trúc dữ liệu về thuật giải,<br>cách trừu tượng hóa dữ liệu theo mục đích bài toán của mình. Hai phương pháp tìm<br>kiếm cơ bản và mười một thuật toán sắp xếp được trình bày bày trong phần thứ hai.<br>Chương ba và bốn sẽ trình bày cấu trúc dữ liệu động cơ bản.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: Nắm vững thuật giải, các phương pháp tìm kiếm, sắp xếp và cấu trúc dữ<br>liệu động<br>- Kỹ năng: phân tích thuật giải, trừu tượng hóa dữ liệu, cài đặt thuật toán<br>- Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học<br><strong>Mô tả môn học:</strong><br>Tổng quan thuật giải, phân tích thuật giải và trừu tượng hóa dữ liệu. Các phương pháp tìm kiếm<br>và sắp xếp trên mảng.Và cấu trúc dữ liệu động cơ bản: danh sách liên kết đơn và đôi, cây nhị<br>phân tìm kiếm.</p><p>&nbsp;</p><p><strong>Tài liệu học tập</strong><br>1. Mark Allen Weiss, Data Structures and Algorithm Analysis in C, 1997<br>2. Trần Hạnh Nhi - Dương Anh Đức, Nhập môn cấu trúc dữ liệu và giải thuật, 2003<br>3. Christopher J Van Wyk, Data structures and C programs, 1990</p>\"},{\"id\":978445040786,\"name\":\"Đại số A2\",\"description\":\"<p>Mã môn học: MTH10402<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết: Đại số tuyến tính A1<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br>Tóm tắt nội dung môn học:<br>Học phần giới thiệu các kiến thức cơ bản về rút gọn toán tử tuyến tính (sự chéo hóa,<br>dạng chính tắc Jordan), không gian Eucclid, dạng song tuyến tính và dạng toàn phương.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: Trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính<br>nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: Nắm vững sự chéo hóa và Jordan hóa của toán tử tuyến tính trên các<br>KGVT hữu hạn chiều, cấu trúc của không gian Euclid, dang song tuyến tính và<br>dạng toàn phương.<br>- Kỹ năng: Chéo hóa và Jordan hóa các ma trận trên các trường số, tính toán trong<br>không gian Euclid, biến đổi dạng toàn phương, sử dụng tốt MAPLE.<br>- Thái độ, chuyên cần: Nghiêm túc, cầu tiến.</p><p><strong>Tài liệu học tập</strong><br>1. Bùi Xuân Hải - Trần Ngọc Hội - Lê Văn Luyện, Đại số tuyến tính và Ứng dụng, Tập 2,<br>2017<br>2. Ngô Việt Trung, Giáo trình Đại số tuyến tính, 2001<br>3. Nguyễn Hữu Việt Hưng, Đại số tuyến tính, 2004</p>\"},{\"id\":342886871367,\"name\":\"LT Độ Đo & XS\",\"description\":\"<p>Mã học phần: MTH10401<br>Thuộc khối kiến thức: Cơ sở ngành<br>Bộ môn – Khoa phụ trách: Toán Tin<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Số tiết lý thuyết: 45<br>Số tiết thực hành, thực tập: 0<br>Số tiết bài tập trên lớp: 30<br>Số tiết thảo luận:<br>Số tiết làm việc nhóm:<br>Số tiết tự học:<br><strong>Điều kiện đăng ký học phần:</strong><br>- Học phần tiên quyết (các học phần SV phải đăng ký học trước và thi đạt):<br>- Học phần học trước (các học phần SV phải đăng ký học trước): Giải tích A1-<br>Giải tích cơ sở, Giải tích A1 - Vi tích phân, Giải tích A2.<br>- Học phần song hành (SV phải đăng ký học trong cùng học kỳ):<br>Các yêu cầu về kiến thức, kỹ năng của SV (nếu có):<br><strong>Mô tả môn học</strong><br>- Tiếng Việt: Trình bày về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến<br>ngẫu nhiên, luật số lớn và các định lý giới hạn.<br>- Tiếng Anh: Giving the introduction of elementary probability theory, abstract measure<br>theory, random variables, law of large numbers and limit theorems.</p><p><strong>Tài liệu học tập:</strong><br>[1] Đ. Đ. Áng, Nhập môn giải tích, NXBGD, 1997.<br>[2] Đ. Đ. Áng, Lý thuyết tích phân, NXBGD, 1997.<br>[3] P. Billingsley, Probability and measure, 3rd edition, John Wiley &amp; Sons, 1995.<br>[4] W. M. Bolstad, Introduction to Bayesian statistics, 2nd edition, Wiley, 2007.<br>[5] M. Bouyssel, Intégrale de Lebesgue, Cépaduès-éditions, 1997.<br>[6] K. L. Chung, A course in probability theory, 3rd edition, Academic Press, 2001.<br>[7] D. M. Đức, Lý thuyết độ đo và tích phân, NXB Đại Học Quốc Gia Tp. HCM,<br>2006.<br>[8] W. Feller, An introduction to probability theory and its applications, Vol. I, John<br>Wiley &amp; Sons, 1957.<br>[9] A. M. Mathai, H. J. Houbold, Special functions for applied scientists, Springer,<br>New York, 2008.<br>[10] P.H. Quân, Đ.N. Thanh, Xác suất thống kê, NXBGD, 2011<br>[11] S. Ross, A first course in probability, 5th edition, Prentice Hall, 1998.<br>[12] W. Rudin, Real and complex analysis, 3rd edition, McGraw-Hill, 1986.<br>[13] N. D. Tiến, V. V. Yên, Lý thuyết xác suất, NXBGD, 2006.<br>[14] Y. Viniotis, Probability and random processes for electrical engineers, McGrawHill,<br>1998.<br>[15] T. A. Dũng, Lý thuyết xác suất và thống kê toán, NXB Đại Học Quốc Gia Tp.<br>HCM, 2007.</p>\"},{\"id\":653976812786,\"name\":\"LTHĐT\",\"description\":\"<p>Mã học phần: MTH10407<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không<br>- Học phần học trước: Cấu trúc dữ liệu và thuật giải.<br>- Học phần song hành:<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Lập trình.<br><strong>Mục tiêu môn học</strong><br>Giới thiệu cho sinh viên các nguyên lý cơ bản của phương pháp luận hướng đối tượng<br>và các kỹ thuật lập trình hướng đối tượng làm cơ sở cho việc xây dựng các ứng dụng<br>sau này.<br><strong>Mô tả môn học:</strong><br>- Các nguyên lý của phương pháp luận hướng đối tượng.<br>- Các nguyên tắc xây dựng lớp đối tượng, cách nhận diện dữ liệu và thao tác, thiết lập quan hệ<br>giữa các lớp, đặc biệt là quan hệ kế thừa và tính đa hình.<br>- Thiết kế, xây dựng lớp, định nghĩa thao tác và phép toán trong C++.<br>- Cài đặt cụ thể quan hệ giữa các lớp, quan hệ kế thừa, đa hình trong C++.</p><p><strong>Tài liệu học tập</strong><br>[1] Bjarne Stroustrup, The C++ programming language, 3rd Edition, AT&amp;T, 1997.<br>[2] James O. Coplien, Advance C++ Programming Styles and Idioms, Addison-Wesley<br>Longman, 1991.<br>[3] Scott Robert Ladd, C++ Kỹ Thuật và Ứng Dụng, NXB Khoa Học Kỹ Thuật, 1992.<br>[4] J Rumbaugh, M Blaha, W Premerlani, F Eddy, W Lorensen, Object-Oriented<br>Modeling and Design, Prencetice Hall, 1991.<br>Phần mềm hay công cụ hỗ trợ thực hành<br>1. Microsoft Visual studio, phiên bản 6.0 trở lên, <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a><br>2. MSDN, thông tin kỹ thuật, địa chỉ <a href=\\\"http://www.microsoft.com/\\\">www.microsoft.com</a></p>\"},{\"id\":881080676656,\"name\":\"LT Thống kê\",\"description\":\"<p>Mã môn học: MTH10404<br>Số tín chỉ: 03 (1LT+1BT+1TH)<br>Số tiết đối với các hoạt động:<br>Nghe giảng lý thuyết: 15 tiết<br>Làm bài tập trên lớp: 30 tiết<br>Thực hành, thực tập (ở PTN, nhà máy, thực tập thực tế...): 30 tiết<br>Thảo luận: 30 tiết<br>Tự học: 60 tiết<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê toán –<br>phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê; sử dụng<br>thành thạo các phần mềm thống kê chuyên dụng (R, SPSS, … ) để phân tích, xử lý số<br>liệu thống kê.<br><strong>Mô tả vắn tắt nội dung môn học:</strong> (trình bày ngắn gọn vai trò, vị trí học phần/môn học,<br>kiến thức sẽ trang bị cho sinh viên, quan hệ với các học phần/môn học khác trong<br>chương trình đào tạo)<br>Phần lý thuyết: bao gồm các kiến thức về mẫu, các đặc trưng mẫu, ước lượng, các<br>phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai, tỷ lệ; xác định cỡ<br>mẫu. Kiểm định giả thuyết: trường hợp một mẫu, hai mẫu; kiểm định giả thuyết về phân<br>phối, kiểm định giả thuyết về tính độc lập. Hồi quy và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần<br>mềm thống kê R để giải quyết các bài toán thống kê trên máy tính với số liệu thực: mô<br>phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.</p><p><strong>Tài liệu học tập</strong><br>1. Trần Tuấn Điệp - Lý Hoàng Tú, Giáo trình lý thuyết xác suất và thống kê toán, 1979<br>2. Nguyễn Bác Văn, Xác suất và xử lý số liệu thống kê, 1996<br>3. Đặng Hùng Thằng, Mở đầu về lý thuyết xác suất và các ứng dụng</p>\"},{\"id\":613125480691,\"name\":\"Toán rời rạc\",\"description\":\"<p>Mã môn học: MTH10406<br>Thuộc khối kiến thức: Cơ sở ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có<br>- Học phần học trước: Không có<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Không có.<br><strong>Mục tiêu môn học</strong><br>Mục tiêu chung: cung cấp các kiến thức cơ bản cho sinh viên ngành Công nghệ thông<br>tin.<br><strong>Mục tiêu cụ thể/ chuẩn đầu ra môn học:</strong><br>- Kiến thức: nắm vững các vấn đề logic, tập hợp - ánh xạ, quan hệ trên tập hợp.<br>- Kỹ năng: trình bày chặt chẽ và chính xác các vấn đề chuyên môn, thực hiện tốt<br>các bài toán đếm, tìm nghiệm của một hệ thức đệ qui, các thuật toán số nguyên,<br>giải phương trình trên Zn , thuật toán tìm công thức đa thức tối tiểu cho hàm<br>Boole.<br>- Thái độ, chuyên cần: Có ý thức học tập nghiêm túc và tích cực, tham gia thảo<br>luận, nêu câu hỏi và phản hồi ý kiến, hoàn thành đầy đủ các yêu cầu học tập của<br>Giảng viên.&nbsp;<br><strong>Mô tả môn học:</strong><br>Môn học thuộc phạm vi kiến thức khoa học cơ bản. Nó cung cấp các kiến thức hết sức<br>cần thiết (về logic, các cấu trúc rời rạc, các thuật toán liên quan, …) cho sinh viên<br>ngành Công nghệ thông tin. Các kiến thức này hỗ trợ nhiều cho sinh viên tiếp thu các<br>môn học cơ sở và chuyên ngành của mình.</p><p><strong>Tài liệu học tập</strong><br>1. Lê Văn Hợp, Bài giảng Toán Rời Rạc<br>2. Nguyễn Hữu ANh, Toán Rời Rạc, 1999<br>3. Kenneth H.Rosen, Discrete Mathematics and Its Application, 2012<br>4. Richard Johnsonbaug, Discrete Mathematics, 2005</p>\"},{\"id\":416416603724,\"name\":\"Giải tích hàm\",\"description\":\"<p>Mã môn học: MTH10403<br>Số tín chỉ: 04 = 3 (LT) + 1 (BT)<br>Điều kiện đăng ký học phần:<br>- Bắt buộc: cho sinh viên theo hướng Toán học (Giải tích, Giải tích số, Đại số, Cơ<br>học, Xác suất thống kê, Tối ưu và hệ thống).<br>- Tự chọn: Môn tự chọn thỏa yêu cầu môn bắt buộc chung hoặc bắt buộc riêng<br>cho các hướng Tin học, các chuyên ngành Toán Tài chính, Sư phạm Toán.<br>- Đại cương:<br>- Các môn học tiên quyết: Học phần học trước và đạt: Giải tích 1A, 2A, 3A.<br><strong>Mục tiêu của môn học</strong><br>Mục tiêu chung: Giải tích hàm là nơi sinh viên có những hiểu biết đầu tiên cơ bản về<br>các không gian vô hạn chiều. Các kiến thức này là không thể thiếu cho nhiều chuyên<br>ngành Toán cả lí thuyết lẫn ứng dụng. Đây là nơi mà khả năng tiếp thu và sử dụng các lí<br>luận toán học trừu tượng và chính xác bước đầu được rèn luyện và kiểm tra.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>- Kiến thức: hiểu và vận dụng được vào tình huống cụ thể một số khái niệm và kết<br>quả căn bản về các không gian Euclid, không gian l^p, L^p, không gian các hàm<br>bị chặn, không gian các hàm liên tục, không gian các ánh xạ tuyến tính liên tục,<br>không gian Hilbert.<br>- Kỹ năng: hiểu được một cách có hệ thống về các đối tượng trên, nắm được các<br>quan hệ giữa các khái niệm và các kết quả. Thực hiện được một số lí luận không<br>có sẵn. Phân biệt và phản biện được những lí luận chưa đạt yêu cầu chính xác<br>của toán học.<br>- Thái độ, chuyên cần: Thấy được nhu cầu phát triển tổng quát hóa qua đó giúp<br>giải quyết những vấn đề ứng dụng, từ đó có thái độ học tập nghiêm túc, chủ<br>động, tự giác.<br><strong>Mô tả vắn tắt nội dung môn học:</strong><br>Không gian metric, không gian định chuẩn, ánh xạ tuyến tính liên tục giữa hai không<br>gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn này được<br>coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ ích<br>cho các mô hình toán học dùng không gian hàm và không gian vô hạn chiều (trong Đại<br>số, Tối ưu, Xác suất-Thống kê, Phương trình đạo hàm riêng, Giải tích, Phương pháp<br>Toán trong Tin học, ...).</p><p><strong>Tài liệu học tập</strong><br>1. Đinh Ngọc Thanh - Huỳnh Quang Vũ, Bài giảng Giải tích hàm,<br><a href=\\\"http://www.math.hcmus.edu.vn/~hqvu/fa.pdf\\\">http://www.math.hcmus.edu.vn/~hqvu/fa.pdf</a><br>2. Dương Minh Đức, Giải tích hàm, 2005<br>3. Đinh Ngọc Thanh - Đặng Đức Trọng, Giải tích hàm, 2011</p>\"},{\"id\":243562249657,\"name\":\"Seminar PPT\",\"description\":\"<p>Mã môn học: MTH10346<br>Thuộc khối kiến thức: Đại cương<br>Số tín chỉ: 04(4LT)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết:<br>- Học phần học trước:<br>- Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV:<br>Nội dung môn học:<br>Sinh viên sẽ làm việc trực tiếp với giáo viên hướng dẫn về một chủ đề.<br>Phương pháp tìm tài liệu cần thiết.<br>Đọc tài liệu và tìm hiểu các kiến thức liên quan đến bài toán được nhận.<br>Viết báo cáo và tình bày những kiến thức đã nhận được trong quá trình làm việc.<br>Mục tiêu môn học:<br>Mục tiêu chung: Trang bị cho SV các kiến thức cơ bản để chuẩn bị làm luận văn tốt<br>nghiệp.<br>Mục tiêu cụ thể/ chuẩn đầu ra môn học:<br>• Kiến thức: nắm vững các kiến thức cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Kỹ năng: các kỹ năng cơ bản để chuẩn bị làm luận văn tốt nghiệp<br>• Thái độ, chuyên cần: có thái độ, quan điểm và nhận thức đúng đắn về môn học</p>\"},{\"id\":227646179263,\"name\":\"NM Trí Tuệ NT\",\"description\":\"<p>Mã học phần: MTH10318<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần học trước: Cấu trúc dữ liệu, Toán rời rạc.<br>Học phần song hành: Không có.<br>Các yêu cầu về kiến thức, kỹ năng của SV: lập trình Visual C, Matlab.<br><strong>Mục tiêu môn học:</strong><br>Môn học cung cấp môt số kiến thức cơ bản của khoa học trí tuệ nhân tạo. Nội dung ̣<br>chính gồm: Các phương pháp giải quyết vấn đề và các áp dụng. Chú trọng phương pháp<br>heuristic. Trình bày một số phương pháp biểu diễn tri thức và môt số kỹ thuât xử lý tri<br>thức. Vấn đề lâp luận gần đúng. Giới thiệu một số hệ tri thức. Giới thiệu một số mô<br>hình và phương pháp khác. Khái niệm Ontology và Agent. Đồng thời môn học cũng<br>giới thiệu các kỹ thuật tính toán mềm như: Fuzzy, mạng Noron, thuật giải di truyền.<br><strong>Mô tả môn học</strong><br>• Tổng quan về khoa học TTNT<br>• Không gian trạng thái và vấn đề tìm kiếm<br>• Biểu diễn tri thức<br>• Giới thiệu về máy học và khám phá tri thức<br>• Giới thiệu Ontology và Intelligent Agent<br>• Giới thiệu Thuật giải di truyển(GA) ̣<br>• Giới thiệu Fuzzy Logic<br>• Mạng Neuron Network</p><p><strong>Tài liệu học tập:</strong><br>[1]. Trí tuệ nhân tạo: các phương pháp giải quyết vấn đề và kỹ thuật xử lý tri thức,<br>Nguyễn Thanh Thủy, 1996, NXB Giáo Dục<br>[2]. Giải một bài toán trên máy tính thư thế nào (1, 2, 3), Hoàng Kiếm, 2004, NXB<br>Giáo Dục<br>[3]. Trí tuệ nhân tạo, Đinh Mạnh Tường, 2002, NXB Khoa học và kỹ thuật<br>[4]. Máy học, Nguyễn Đình Thúc, 2002, NXB Lao động xả hội<br>[5]. Lập trình tiến hoá, Nguyễn Đình Thúc, 2001, NXB Giáo dục<br>[6]. Mạng Noron phương pháp và ứng dụng, Nguyễn Đình Thúc,2000, NXB Giáo<br>dục<br>[7]. Lý thuyết điều khiển mờ, Phan Xuân Minh, Nguyễn Doãn Phước, NXB Khoa<br>học và kỹ thuật<br>[8]. Logic Mờ Và Ứng Dụng, Đặng Thanh Hà, B. Bouchon Meunier, Hồ Thuần,<br>2007, NXB ĐH Quốc gia HN<br>[9]. Công nghệ phần mềm hướng tác tử, Lê Tấn Hùng, Từ Minh Phương, Huỳnh<br>Quyết Thắng, 2006, NXB Khoa học và kỹ thuật<br>[10].<br>[11]. Artificial Intelligence A Modern Approach, Stuart J. Russell, Peter Norvig,<br>1995, Prentice Hall<br>[12]. Problem – Solving and Articifial Intelligent, Jean – Louis Laurière, 1990,<br>Prentice Hall<br>[13]. An Introduction to Genetic Algorithms, Melanie Mitchell, 1999, MIT Press<br>[14]. Practical genetic algorithms, Randy L. Haupt, Sue Ellen Haupt, 2004, John<br>Wiley &amp; Sons, Inc., Hoboken, New Jersey<br>[15]. C++ Neural Networks and Fuzzy Logic, Valluru B. Rao, 1995, M&amp;T<br>Books, IDG Books Worldwide, Inc<br>[16]. Introduction to fuzzy sets, fuzzy logic, and fuzzy control systems , Guanrong<br>Chen,Trung Tat Pham, 2001, CRC Press LLC<br>[17]. Discovering Knowledge in Data: An Introduction to Data Mining, Daniel T.<br>Larose, 2004, John Wiley &amp; Sons Inc<br>[18]. First course on fuzzy theory and applications, Kwang H. Lee, 2005,<br>SpringerVerlag Berlin Heidelberg<br>[19]. Fuzzy logic: a practical approach / F. Martin McNeill, Ellen Thro, 1994 by<br>Academic Press<br>[20]. Fusion of Neural Networks, Fuzzy Systems and Genetic Algorithms: Industrial<br>Applications, Lakhmi C. Jain; N.M. Martin, 1998, CRC Press<br>[21]. Foundations of Neural Networks, Fuzzy Systems, and Knowledge Engineering,<br>Nikola K. Kasabov, 1998, MIT Press</p>\"},{\"id\":483830501017,\"name\":\"Số học & TT\",\"description\":\"<p>Mã học phần:MTH10324<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần học trước hoặc tiên quyết: đại số đại cương.<br>- Học phần song hành: không.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: tính toán, suy luận, chứng minh.<br><strong>Mục tiêu của học phần</strong><br>Trang bị cho sinh viên các kiến thức và kỹ năng lập trình cơ bản của số học thuật toán:<br>số nguyên tố, đồng dư, phân số liên tục, các hàm số học, thặng dư bình phương, đường<br>cong elliptic, các thuật toán tất định và xác suất kiểm tra số nguyên tố, phân tích số<br>nguyên và tính logarit rời rạc.<br><strong>Tóm tắt nội dung học phần</strong><br>- Nhắc lại các kiến thức cơ bản về thuật toán và độ phức tạp.<br>- Biểu diễn số nguyên, số nguyên tố, thuật toán chia Euclid, định lý thặng dư Trung<br>Hoa, định lý Fermat, và phân số liên tục<br>- Phi hàm Euler, số nguyên tố Mersenne, căn nguyên thuỷ<br>- Thặng dư bình phương, ký hiệu Legendre, ký hiệu Jacobi<br>- Các thuật toán kiểm tra số nguyên tố, phân tích số nguyên tố<br>- Đường cong elliptic trên trường số thực và trên trường hữu hạn<br>- Ứng dụng số học vào lý thuyết mật mã: hệ mã Caesar, RSA, ElGamal.<br>- Thuật toán tính logarit rời rạc.</p><p><strong>Tài liệu học tập:</strong><br>[1] Hà Huy Khoái – Phạm Huy Điển, Số học thuật toán, NXB ĐHQG Hà Nội, 2003.<br>[2] Menezes, Oorschot and Vanstone, Handbook of Applied Cryptography, CRC Press,<br>2001.<br>[3] Allen Downey, Think Python, <a href=\\\"http://www.thinkpython.com/\\\">http://www.thinkpython.com</a></p>\"},{\"id\":559476024731,\"name\":\"LT Thông Tin\",\"description\":\"<p>Mã môn học: MTH10347<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04(3LT+1TH)<br>Điều kiện đăng ký học phần:<br>Học phần tiên quyết: xác suất thống kê, đại số tuyến tính.<br>Học phần học trước:<br>Học phần song hành:<br>Các yêu cầu về kiến thức, kỹ năng của SV: matlab cơ bản.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản của lý thuyết thông tin.<br>- Entropy thông tin<br>- Lý thuyết mã tự sửa<br>- Mã nén dữ liệu<br>- Truyền thông trên kênh nhiễu<br><strong>Mục tiêu môn học:</strong><br>Trang bị cho SV các kiến thức cơ bản về lý thuyết thông tin; entropy thông tin, lý thuyết<br>mã, mã hoá và truyền tin trên kênh nhiễu.</p><p><strong>Tài liệu học tập:</strong><br>[1] David J.C. Mackay, Information Theory, Inference, and Learning Algorithms,<br>Cambridge University Press, 2003.<br>[2] C. E. Shannon, A Mathematical Theory of Communication, The Bell System<br>Technical Journal, Vol. 27, 1948.<br>[3] Learning Matlab 7, <a href=\\\"http://www.mathworks.com/\\\">www.mathworks.com.</a></p>\"},{\"id\":517363488569,\"name\":\"PT Thuật Toán\",\"description\":\"<p>Mã môn học: MTH10325<br>Thuộc khối kiến thức: Chuyên ngành.<br>Số tín chỉ: 04 (3LT + 1TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Không có.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: Nắm vững logic Toán học.<br><strong>Nội dung môn học:</strong><br>- Tổng quan về thuật toán và độ phức tạp của thuật toán.<br>- Hàm sinh và ứng dụng.<br>- Hoán vị, nghịch thế và ứng dụng.<br>- Đánh giá độ phức tạp của một số thuật toán thông dụng<br><strong>Mục tiêu môn học</strong><br>Trang bị cho sinh viên các kiến thức và kỹ năng lập trình đánh giá độ phức tạp của thuật<br>toán, tính đúng đắn của thuật toán, hàm sinh và ứng dụng; hoán vị và ứng dụng; đánh<br>giá một số thuật toán thông dụng.</p><p><strong>Tài liệu học tập:</strong><br>[1] Kenneth H. Rosen, Discrete Mathematics and Its Application, McGraw-Hill, 1998.<br>[2] Herbert S.Wilf, Algorithm Complexity, Internet Edition 1994.<br>[3] Herbert S.Wilf, Generating Functionology, Internet Edition 1994.<br>[4] Ian Parberry, Lecture Notes on Algorithm Analysis and Computational Complexity,<br>Internet Editon 2001.</p>\"},{\"id\":305758045481,\"name\":\"LT mã hoá TT\",\"description\":\"<p>Mã môn học: MTH10319<br>Thuộc khối kiến thức: Chuyên ngành<br>Số tín chỉ: 04 (03LT + 01TH)<br>Điều kiện đăng ký học phần:<br>- Học phần tiên quyết: Không có.<br>- Học phần học trước: Đại số tuyến tính; Đại số đại cương; Số học thuật toán.<br>- Học phần song hành: Không có.<br>- Các yêu cầu về kiến thức, kỹ năng của SV: các cấu trúc đại số cơ bản,<br>các thuật toán trên số nguyên, lập trình tính toán.<br><strong>Mục tiêu môn học</strong><br>- Trang bị cho SV các kiến thức cơ bản về: lý thuyết mật mã cổ điển, mã đối xứng hiện<br>đại, hệ mã công khai, hàm băm mật mã, chữ ký số, giao thức trao đổi chìa khoá.<br>- Song song đó, SV được củng cố: các kỹ năng lập trình qua các bài thực tập cài đặt các<br>thuật toán; kỹ năng nghiên cứu và trình bày đề tài mở; kỹ năng sử dụng phần mềm bảo<br>mật.<br><strong>Nội dung môn học:</strong><br>- Các khái niệm cơ bản của lý thuyết mật mã cổ điển.<br>- Hệ mã đối xứng thông dụng: DES, AES.<br>- Hàm băm mật mã thông dụng: MD5, SHA1.<br>- Hệ mã công khai thông dụng: RSA, ElGamal, ECC.<br>- Giao thức trao đổi chìa Diffie-Hellman<br>- Chữ ký số: RSA, DSA, ECC.<br>- Sử dụng phần mềm: PGP, SSH, Cryptool</p><p><strong>Tài liệu học tập:</strong><br>[1] Douglas R. Stinson, Cryptography: Theory and Practice, 3rd. ed., Chapman &amp; Hall/<br>CRC, 2006.<br>[2] Alfred J. Menezes, Paul C. van Oorschot and Scott A. Vanstone, <a href=\\\"http://www.cacr.math.uwaterloo.ca/hac/\\\">Handbook of</a><br><a href=\\\"http://www.cacr.math.uwaterloo.ca/hac/\\\">Applied Cryptography</a>, CRC Press, 2001.<br>[3] Phạm Huy Điển, Hà Huy Khoái, Mã hoá thông tin: Cơ sở toán học và ứng dụng,<br>NXB ĐHQG Hà Nội, 2003.</p>\"}]}]', 8, '2024-07-21 14:54:04', '2024-07-25 06:50:54');
INSERT INTO `majors` (`id`, `name`, `subjects`, `department_id`, `created_at`, `updated_at`) VALUES
(10, 'Tài chính định lượng', '[{\"id\":237016622908,\"name\":\"Tài chính định lượng\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1332/1018;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 08.04.54.png\\\" width=\\\"1332\\\" height=\\\"1018\\\"></figure>\",\"categories\":[{\"id\":709880883419,\"name\":\"Toán tài chính căn bản\",\"description\":\"<p><strong>MTH 10201&nbsp; (4TC):&nbsp; Toán tài chính căn bản</strong><br>Học phần cung cấp kiến thức căn bản về tài chính và toán tài chính cho mô hình rời rạc không ngẫu nhiên. Nội dung lý thuyết sẽ trình bày về lãi suất, chuỗi tiền tệ, các hình thức vay, thẩm định dự án đầu tư, định giá trái phiếu, cổ phiếu.</p>\"},{\"id\":304431898521,\"name\":\"Đại số A2\",\"description\":\"<p><strong>MTH 10402&nbsp; (4TC):&nbsp; Đại số A2</strong><br>Học phần trang bị cho sinh viên những kiến thức cơ bản về đại số tuyến tính nâng cao, làm nền tảng cho các học phần chuyên ngành về Toán.&nbsp; Cụ thể, sinh viên sẽ nắm vững sự chéo hoá và Jordan hoá của toán tử tuyến tính trên các không gian vector hữu hạn chiều, cấu trúc của không gian Euclide, dạng song tuyến tính và dạng toàn phương.</p>\"},{\"id\":524144162008,\"name\":\"Lý thuyết độ đo & XS\",\"description\":\"<p><strong>MTH 10401&nbsp; (4TC):&nbsp; Lý thuyết&nbsp; độ đo và xác suất</strong><br>Học phần cung cấp kiến thức về lý thuyết xác suất cơ bản, lý thuyết độ đo trừu tượng, biến ngẫu nhiên, luật số lớn và các định lý giới hạn.</p>\"},{\"id\":576084064549,\"name\":\"Giải tích hàm\",\"description\":\"<p><strong>MTH 10403 (4TC):&nbsp; Giải tích hàm</strong><br>Học phần cung cấp những kiến thức về: không gian metric, không gian định chuẩn, ánh xạ tuyến tính liện tục giữa hai không gian định chuẩn cùng các định lý cơ bản về chúng, không gian Hilbert. Môn học được coi là kiến thức căn bản cho trình độ đại học ngành Toán. Môn học cần thiết và bổ trợ cho các môn hình toán học dùng không gian hàm và không gian vô hạn chiều.&nbsp;</p>\"},{\"id\":569361831681,\"name\":\"Lý thuyết thống kê\",\"description\":\"<ul><li><strong>MTH 10404&nbsp; (4TC):&nbsp; Lý thuyết thống kê</strong><br>Học phần cung cấp kiến thức cơ bản của Lý thuyết Thống kê Toán-phân tích số liệu để đưa ra các mô hình tổng quát và các quyết định thống kê cũng như sử dụng thành thạo phần mềm thống kê chuyên dụng R để phân tích, xử lý số liệu thống kê.<br>Phần lý thuyết: bao gồm các kiến thức về mẫu (sample), các đặc trưng mẫu, ước lượng, các phương pháp ước lượng; khoảng tin cậy cho kỳ vọng, phương sai và tỷ lệ; cùng với bài toán xác định cỡ mẫu. Kiểm định giả thuyết: trường hợp 1 mẫu và trường hợp 2 mẫu; kiểm định giả thuyết về phân phối, kiểm định giả thuyết về tính độc lập. Kiến thức về hồi quy thống kê và tương quan.<br>Phần thực hành: áp dụng các kiến thức đã học trong phần lý thuyết và sử dụng phần mềm R để thực hành các bài toán thống kê trên máy tính với số liệu thực: mô phỏng, chọn mẫu, ước lượng, kiểm định giả thuyết, hồi quy và tương quan.&nbsp;</li></ul>\"},{\"id\":618261193400,\"name\":\"Vi mô định lượng\",\"description\":\"<ul><li><strong>MTH 10214&nbsp; (4TC):&nbsp; Vi mô định lượng&nbsp;</strong><br>Học phần cung cấp các nội dung cơ bản của kinh tế học vĩ mô và các công cụ toán học định lượng trong kinh tế học vĩ mô. Giới thiệu về mô hình cung, cầu và hoạt động thị trường. Các khái niệm thặng dư. Lý thuyết lựa chọn. Đường cung của doanh nghiệp và thị trường cạnh tranh. Độc quyền và độc quyền nhóm và cạnh tranh độc quyền.&nbsp;&nbsp;</li></ul>\"},{\"id\":879003767216,\"name\":\"Toán tài chính NC\",\"description\":\"<ul><li><strong>MTH 10209&nbsp; (4TC):&nbsp; Toán tài chính nâng cao</strong><br>Học phần cung cấp kiến thức nâng cao về tài chính và toán tài chính cho mô hình rời rạc ngẫu nhiên và mô hình liên tục. Chi tiết nội dung lý thuyết bao gồm lý thuyết cơ bản về thị trường phái sinh (market derivatives), hợp đồng kỳ hạn, quyền chọn, hợp đồng hoán đổi.&nbsp;</li></ul>\"},{\"id\":438314535501,\"name\":\"Vĩ mô định lượng\",\"description\":\"<ul><li><strong>MTH 10215 (4TC):&nbsp; Vĩ mô định lượng</strong><br>Học phần cung cấp các nội dung cơ bản của kinh tế học vĩ mô và các công cụ toán học định lượng trong kinh tế học vĩ mô.</li></ul>\"},{\"id\":146373553315,\"name\":\"Quản trị rủi ro\",\"description\":\"<p><strong>MTH 10216&nbsp; (4TC):&nbsp; Quản trị rủi ro</strong><br>Học phần cung cấp kiến thức cơ bản của rủi ro tài chính tác động lên doanh nghiệp cũng như các chiến lược và chương trình phòng ngừa.&nbsp;<br>Nội dung lý thuyết trình bày tổng quan về các rủi ro trong doanh nghiệp và các sản phẩm phái sinh. Định giá các sản phẩm phái sinh. Chiến lược phòng ngừa rủi ro bằng các sản phẩm phái sinh và thiết lập chương trình quản trị rủi ro tài chính.</p>\"},{\"id\":420961043702,\"name\":\"Tài chính doanh nghiệp\",\"description\":\"<p><strong>MTH 10217&nbsp; (4TC):&nbsp; Tài chính doanh nghiệp</strong><br>Học phần trang bị cho sinh viên kiến thức về vốn và nguồn vốn của doanh nghiệp, các nội dung liên quan đến chi phí sử dụng vốn và quyết định đầu tư vốn cũng như việc định giá chứng khoán và định giá doanh nghiệp.</p>\"},{\"id\":869114459431,\"name\":\"Phân tích tài chính\",\"description\":\"<p><strong>MTH 10219&nbsp; (4TC):&nbsp; Phân tích tài chính</strong><br>Học phần cung cấp kiến thức cơ bản liên quan đến việc thành lập và phân tích các báo cáo tài chính như Bảng cân đối kế toán, báo cáo hoạt động kinh doanh, báo cáo lưu chuyển tiền tệ.&nbsp;</p>\"},{\"id\":313431011858,\"name\":\"Toán bảo hiểm cơ bản\",\"description\":\"<p><strong>MTH 10220&nbsp; (4TC):&nbsp; Toán bảo hiểm cơ bản&nbsp;</strong><br>Học phần giới thiệu về các khái niệm cơ bản như xác suất sống sót và bảng thống kê tuổi thọ, các quy ước ký hiệu trong bảo hiểm; bảo hiểm nhân thọ truyền thống với các quyền lợi cơ bản; cũng như các phương pháp tính phí bảo hiểm.</p>\"},{\"id\":399990958330,\"name\":\"Mô hình toán tài chính\",\"description\":\"<p><strong>MTH 10203&nbsp; (4TC):&nbsp; Mô hình toán tài chính</strong><br>Học phần giới thiệu các mô hình tài chính cơ bản để giúp sinh viên có khả năng hiểu và vận dụng kiến thức tài chính nhằm giải quyết các bài toán tài chính căn bản như lập các quyết định, đánh giá rủi ro, định giá ... một cách có cơ sở khoa học rõ ràng.</p>\"},{\"id\":293901276372,\"name\":\"Dự báo\",\"description\":\"<p><strong>MTH 10202&nbsp; (4TC):&nbsp; Dự báo</strong><br>Học phần cung cấp kiến thức kỹ năng xây dựng các mô hình định lượng, mô phỏng trong kinh tế. Kết hợp với máy tính xây dựng các mô hình tính toán cho các bài toán về dự báo.</p>\"},{\"id\":322133406358,\"name\":\"Lý thuyết tc tiền tệ\",\"description\":\"<p><strong>MTH 10204&nbsp; (4TC):&nbsp; Lý thuyết tài chính tiền tệ</strong><br>Học phần khảo sát các vấn đề liên quan đến lãi suất, cách thức hoạt động của thị trường tài chính và các định chế tài chính. Phần 1 giới thiệu tổng quan về hệ thống tài chính, cách tính lãi suất và cấu trúc lãi suất. Phần 2 tìm hiểu cách thức hoạt động của thị trường chứng khoán nợ. Phần 3 tìm hiểu về thị trường cổ phiếu. Phần 4 tìm hiểu về thị trường chứng khoán phái sinh. Phần 5 tìm hiểu về hoạt động của ngân hàng thương mại và các tổ chức phi ngân hàng.&nbsp;</p>\"},{\"id\":152761461955,\"name\":\"Toán bảo hiểm nâng cao\",\"description\":\"<p><strong>MTH 10209&nbsp; (4TC):&nbsp; Toán bảo hiểm nâng cao</strong><br>Học phần giúp cho sinh viên hiểu được khái niệm về dự phòng và các phương pháp tính toán dự phòng; nắm vững các mô hình bảo hiểm nhân thọ cao cấp: mô hình nhiều decrement, mô hình nhiều trạng thái, mô hình nhiều người được bảo hiểm, universal life (UL). Ngoài ra, học phần cũng giúp cho sinh viên chuẩn bị cho kỳ thi SOA Exam MLC.</p>\"},{\"id\":960483777145,\"name\":\"Seminar toán tài chính\",\"description\":\"<p><strong>MTH 10218&nbsp; (4TC):&nbsp; Seminar toán tài chính</strong><br>Sinh viên sẽ liên hệ với sự đồng ý của giảng viên hướng dẫn để thực hiện báo cáo seminar khoa học về một chủ đề phù hợp trong Toán tài chính định lượng.</p>\"},{\"id\":913707050798,\"name\":\"Khoá luận tốt nghiệp\",\"description\":\"\"}]}]', 6, '2024-07-25 01:05:03', '2024-07-25 03:33:25'),
(11, 'Chuyên ngành Cơ', '[{\"id\":297491843813,\"name\":\"Chuyên ngành Cơ\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1348/1038;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 10.59.29.png\\\" width=\\\"1348\\\" height=\\\"1038\\\"></figure>\",\"categories\":[{\"id\":146131368851,\"name\":\"Nhập môn cơ học\",\"description\":\"<p>Giới thiệu các hướng phát triển của cơ học hiện đại. Trình bày một vài nội dung đơn giản giúp<br>sinh viên làm quen với lý thuyết và ứng dụng thực tế của cơ học hiện đại.</p>\"},{\"id\":760345927271,\"name\":\"Cơ học lý thuyết\",\"description\":\"<p>Cung cấp các kiến thức cơ bản về cơ học Newton. Giúp sinh viên biết cách đặt và giải các<br>bài toán cơ học cơ bản. Biết phân tích, giải thích các hiện tượng cơ học trên cơ sở các định luật<br>Newton. Thông qua môn học giới thiệu phương pháp mô hình hóa toán học các quá trình của<br>thế giới thực.</p>\"},{\"id\":447204215826,\"name\":\"Lập trình Symbolic cho bài toán ứng dụng\",\"description\":\"<p>Hướng dẫn sử dụng máy tính để trợ giúp việc mô phỏng và giải quyết các bài toán trong cơ học<br>và thực tế.</p>\"},{\"id\":548606817659,\"name\":\"Cơ học môi trường liên tục\",\"description\":\"<p>Cung cấp kiến thức nền tảng cho sinh viên về các môi trường liên tục trong cơ học và mô hình<br>toán học cho các môi trường liên tục. Giới thiệu cho sinh viên các hướng nghiên cứu<br>chuyên sâu về các đối tượng môi trường liên tục: Vật rắn biến dạng, Chất lưu.</p>\"},{\"id\":447978599435,\"name\":\"Phương pháp sai phân hữu hạn\",\"description\":\"<p>Trang bị cho sinh viên kiến thức cơ bản về phương pháp sai phân, một phương pháp đơn giản<br>được sử dụng thường xuyên để giải số các phương trình vi phân, phương trình đạo hàm riêng<br>và ứng dụng trong một số bài toán khoa học kỹ thuật.</p>\"},{\"id\":544863455218,\"name\":\"Cơ học rắn biến dạng\",\"description\":\"<p>Cung cấp cho sinh viên các khái niệm cơ sở của các qui luật ứng xử bên trong của vật thể rắn.<br>Trình bày một số qui luật cơ bản và các định lý liên quan đến nhiều bài toán thực tế của<br>Cơ học vật rắn biến dạng. Cung cấp cho sinh viên phương pháp đặt, giải toán và mô phỏng số<br>trên máy tính</p>\"},{\"id\":955887181526,\"name\":\"Cơ học chất lỏng\",\"description\":\"<p>Cung cấp các kiến thức cơ bản về cơ học chất lỏng. Giúp sinh viên biết cách đặt bài toán và<br>giải một số bài toán cơ học chất lỏng. Cung cấp cho sinh viên phương pháp đặt, giải toán và<br>mô phỏng số trên máy tính.</p>\"},{\"id\":467706812120,\"name\":\"Seminar cơ hoc\",\"description\":\"<p>Trình bày những hướng phát triển hiện đại, làm cơ sở cho việc nghiên cứu trong tương lai<br>của sinh viên.</p>\"},{\"id\":421909071102,\"name\":\"Thực tập đồ án thực tế\",\"description\":\"<p>Sinh viên tham gia thực tập tại các viện nghiên cứu, công ty với các đề tài hiện thực, tiên tiến.</p>\"},{\"id\":384752915659,\"name\":\"Phương pháp phần tử hữu hạn\",\"description\":\"<p>Cung cấp các kiến thức cơ bản về phương pháp phần tử hữu hạn. Giúp sinh viên biết cách<br>áp dụng phương pháp phần tử hữu hạn để giải các bài toán biên, bài toán biên-giá trị đầu<br>xuất hiện trong cơ học và vật lý.</p>\"},{\"id\":783946290323,\"name\":\"Dao động ngẫu nhiên\",\"description\":\"<p>Nghiên cứu dao động và phản ứng động lực của các hệ chịu kích động ngẫu nhiên,<br>Hệ nghiên cứu có thể là hệ rời rạc (ít bậc tư do) hoặc hệ liên tục (nhiều bậc tự do).</p>\"},{\"id\":932859295872,\"name\":\"Cơ học phá huỷ\",\"description\":\"<p>Cung cấp kiến thức chuyên sâu cho sinh viên về cơ học phá hủy và các phương pháp giải tích<br>và phương pháp số để giải một số bài toán trong cơ học phá hủy.</p>\"},{\"id\":116520874064,\"name\":\"Khoá luận tốt nghiệp - Cơ học\",\"description\":\"<p>Sinh viên thực hiện nghiên cứu tiên tiến, hiện đại.</p>\"}]}]', 4, '2024-07-25 04:02:24', '2024-07-25 04:02:24'),
(12, 'Chuyên ngành tối ưu và hệ thống', '[{\"id\":654024362464,\"name\":\"Tối ưu và hệ thống\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1822/1330;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.04.09.png\\\" width=\\\"1822\\\" height=\\\"1330\\\"></figure>\",\"categories\":[{\"id\":526372767372,\"name\":\"Quy hoạch tuyến tính\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1016/950;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.09.41.png\\\" width=\\\"1016\\\" height=\\\"950\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1088/360;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.10.31.png\\\" width=\\\"1088\\\" height=\\\"360\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1072/236;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.11.10.png\\\" width=\\\"1072\\\" height=\\\"236\\\"></figure>\"},{\"id\":970691755131,\"name\":\"Vận trù học\",\"description\":\"<p>Tên tiếng Anh: Operations Research</p><p>Mã học phần: MTH10446</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1084/1566;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.12.00.png\\\" width=\\\"1084\\\" height=\\\"1566\\\"></figure>\"},{\"id\":800280734684,\"name\":\"Mô hình toán kinh tế\",\"description\":\"\"},{\"id\":743018529884,\"name\":\"Quy hoạch tuyến tính nâng cao\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1028/560;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.15.19.png\\\" width=\\\"1028\\\" height=\\\"560\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1084/822;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.15.34.png\\\" width=\\\"1084\\\" height=\\\"822\\\"></figure>\"},{\"id\":436619557127,\"name\":\"Thuật toán tối ưu\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1084/1378;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.16.00.png\\\" width=\\\"1084\\\" height=\\\"1378\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1070/564;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.16.12.png\\\" width=\\\"1070\\\" height=\\\"564\\\"></figure>\"},{\"id\":368606690282,\"name\":\"Lý thuyết trò chơi\",\"description\":\"<figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1004/848;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.16.39.png\\\" width=\\\"1004\\\" height=\\\"848\\\"></figure><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1070/368;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.16.52.png\\\" width=\\\"1070\\\" height=\\\"368\\\"></figure>\"},{\"id\":780638228536,\"name\":\"Tối ưu đa mục tiêu\",\"description\":\"<p>Tên tiếng anh: Multiobjective optimization</p><p>Mã môn học: MTH10537</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1080/1330;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.18.20.png\\\" width=\\\"1080\\\" height=\\\"1330\\\"></figure>\"},{\"id\":862170014777,\"name\":\"Tối ưu hoá ứng dụng\",\"description\":\"<p>Tên tiếng Anh: Applied Optimization</p><p>Mã môn học: MTH10538</p><p>Thuộc khối kiến thức: Tự chọn&nbsp;</p><p>Số tín chỉ: 4 (4LT)&nbsp;</p><p>Điều kiện đăng ký học phần:&nbsp;</p><p>Học phần tiên quyết:&nbsp;</p><p>Học phần học trước:&nbsp;</p><p>Học phần song hành:</p><figure class=\\\"image\\\"><img style=\\\"aspect-ratio:1082/1072;\\\" src=\\\"http://localhost:8000/assets/user/images/about/Screenshot 2024-07-25 at 11.23.36.png\\\" width=\\\"1082\\\" height=\\\"1072\\\"></figure>\"},{\"id\":225398531495,\"name\":\"Lý thuyết và thuậ