INSERT INTO `thong_baos` (`id`, `tieude`, `noidung`, `ghichu`, `ngaybatdau`, `ngayhethan`, `created_at`, `updated_at`) VALUES
(1, 'Lịch học kỳ II', '<hr> \n						» Đã có lịch thi chính thức, SV truy cập trang web để xem lịch thi cụ thể.<hr> \n						» Đã có lịch thi chính thức, SV truy cập trang web để xem lịch thi cụ thể.<hr> \n						» Đã có lịch thi chính thức, SV truy cập trang web để xem lịch thi cụ thể.<hr> \n						» Đã có lịch thi chính thức, SV truy cập trang web để xem lịch sthi cụ thể.<hr> \n						<hr>', '* Thông báo cập nhật lúc 18h40 ngày 21/07/2024.', '2024-07-21', '2024-07-30', NULL, '2024-07-22 06:43:18');

