-- Đang đổ dữ liệu cho bảng `tin_tucs`
--

INSERT INTO `tin_tucs` (`id`, `tieude`, `mota`, `hinhdaidien`, `noidung`, `noibat`, `luotxem`, `id_loaitin`, `id_user`, `created_at`, `updated_at`, `is_hidden_cmt`) VALUES
(1, 'TMA Tech Group tuyển dụng việc làm dành cho SV mới tốt nghiệp', 'TMA Tech Group là tập đoàn công nghệ hàng đầu Việt Nam với 27 năm phát triển, hơn 4000 kỹ sư tài năng, có khách hàng trên 30 quốc gia và 16 năm đạt huy chương Vàng xuất khẩu phần mềm.', 'Logo-Math-CS-cyan-192.png', '<p>&nbsp;</p><p>TMA Tech Group là tập đoàn công nghệ hàng đầu Việt Nam với&nbsp;<strong>27 năm phát triển</strong>, hơn&nbsp;<strong>4000 kỹ sư&nbsp;</strong>tài năng, có khách hàng trên&nbsp;<strong>30 quốc gia</strong>&nbsp;và&nbsp;<strong>16 năm đạt huy chương Vàng</strong>&nbsp;xuất khẩu phần mềm.</p><p>&nbsp;</p>', 1, 3, 6, 1, '2019-04-27 10:46:08', '2024-07-19 06:58:19', 0),
(3, 'Trường hè “Sinh viên thực tập nghiên cứu khoa học 2024”', 'Đợt 1: Từ 24-29/06/2024 tại Viện Nghiên cứu cao cấp về Toán\n Đợt 2: Từ 01/7-23/8/2024, làm việc trực tuyến\n Đợt 3: 26-30/08/2024 tại Trường Đại học Xây dựng miền Trung, Thành phố Tuy Hoà, Tỉnh Phú Yên (dự kiến).', 'Logo-Math-CS-cyan-192.png', '<p><strong>Thời gian và Địa điểm</strong>&nbsp;:</p><ul><li>&nbsp;Đợt 1: Từ 24-29/06/2024 tại Viện Nghiên cứu cao cấp về Toán</li><li>&nbsp;Đợt 2: Từ 01/7-23/8/2024, làm việc trực tuyến</li><li>&nbsp;Đợt 3: 26-30/08/2024 tại Trường Đại học Xây dựng miền Trung, Thành phố Tuy Hoà, Tỉnh Phú Yên (dự kiến).</li></ul><p><strong>1. Mục tiêu</strong>&nbsp;:</p><ul><li>Mục tiêu của trường hè là giúp sinh viên sớm tiếp cận với quá trình nghiên cứu toán học, từ bước đi đầu tiên xác định vấn đề, tìm kiếm phương pháp giải quyết và trình bày kết quả thu được. Trải nghiệm này sẽ khuyến khích việc phát triển kỹ năng toán học một cách sâu rộng, nâng cao khả năng tự học, khả năng làm việc nhóm, và tư duy sáng tạo.</li><li>Chương trình sẽ tạo điều kiện thuận lợi nhất cho sinh viên được làm việc trực tiếp với các nhà nghiên cứu giàu kinh nghiệm, được làm việc với các vấn đề thách thức và có ý nghĩa, và được trình bày kết quả nghiên cứu của mình qua các seminar trực tuyến và trực tiếp.</li></ul><p><strong>2. Đối tượng tham dự</strong>&nbsp;:</p><ul><li>Sinh viên đại học&nbsp; thuộc các ngành đào tạo trong lĩnh vực các khoa học toán (mathematical sciences) trên cả nước.</li><li>Viện Nghiên cứu cao cấp về Toán sẽ lựa chọn sinh viên căn cứ trên thành tích cá nhân, bảng điểm đại học và thư giới thiệu của một nhà khoa học.</li><li>Yêu cầu đối với học viên: Cần tập trung làm việc trong toàn bộ thời gian diễn ra trường hè (10 tuần), nỗ lực theo sát hướng dẫn của giảng viên để đạt được kết quả tốt nhất.</li></ul><p><strong>3. Đơn vị tổ chức và tài trợ</strong>&nbsp;:</p><ul><li>Viện Nghiên cứu cao cấp về Toán</li><li>Trường Đại học Xây dựng miền Trung (dự kiến)</li></ul><p><strong>4. Ban Tổ chức</strong>&nbsp;:</p><ul><li>Lê Minh Hà, VIASM</li><li>Phan Thành Nam, LMU Munich, Đức</li></ul><p><strong>5. Ban chương trình</strong>&nbsp;:</p><ul><li>Lê Minh Hà, VIASM;</li><li>Nguyễn Hồng Đức, Trường Đại học Thăng Long;</li><li>Phan Thành Nam, LMU Munich, Đức;</li><li>Phạm Văn Thắng, Trường Đại học Khoa học Tự nhiên - ĐHQG Hà Nội.</li></ul><p><strong>6. Giảng viên chính</strong>&nbsp;:</p><ul><li>Tăng Quốc Bảo, Đại học Graz, Áo;</li><li>Nguyễn Thanh Bình, Trường Đại học Khoa học Tự nhiên, ĐHQG TP Hồ Chí Minh;</li><li>Hà Tuấn Dũng, Trường Đại học Sư phạm Hà Nội 2;</li><li>Nguyễn Quốc Hưng, Viện Hàn lâm Khoa học Trung Quốc;</li><li>Lương Đăng Kỳ; Trường Đại học Quy Nhơn;</li><li>Dabeen Lee, KAIST, Hàn Quốc;</li><li>Nguyễn Tuấn Minh, Đại học Monash, Úc;</li><li>Trần Giang Nam, Viện Toán học, Viện HLKH&amp;CN Việt Nam;</li><li>Nguyễn Xuân Việt Nhân, Trường Đại học FPT Đà Nẵng;</li><li>Nguyễn Phước Tài, Đại học Masaryk, Cộng hòa Séc</li><li>Phạm Minh Quý, Đại học Rochester, Mỹ;</li><li>Trần Mạnh Tuấn, Đại học Khoa học và Công nghệ Trung Quốc.</li></ul><p><strong>7. Giảng viên mời</strong>&nbsp;:</p><ul><li>Phạm Tuấn Huy, Clay Research Fellow, Đại học Stanford, Mỹ;</li><li>Ngô Đắc Tuấn, Trung tâm CNRS, Pháp.</li></ul><p><strong>6. Ngôn ngữ</strong>&nbsp;:&nbsp;Tiếng Việt và Tiếng Anh</p><p><strong>7. Hạn đăng ký tham dự và tài trợ</strong>:&nbsp;15/5/2024</p><p><strong>8. Liên hệ</strong>&nbsp;:&nbsp;Email: <a href=\"mailto:reu@viasm.edu.vn\">reu@viasm.edu.vn</a></p>', 1, 0, 11, 1, '2019-04-27 10:58:41', '2024-07-21 14:03:59', 0),
(4, 'CHƯƠNG TRÌNH HỖ TRỢ CÔNG BỐ KHOA HỌC VÀ CÔNG NGHỆ (Đợt 2/2024)', 'Mục tiêu: Nhằm khuyến khích Thầy Cô và người học tại Trường trong việc nghiên cứu khoa học, phát triển công nghệ, công bố các kết quả khoa học và công nghệ (KH&CN); từ đó nâng cao năng lực và hiệu quả trong nghiên cứu khoa học, phát triển công nghệ tại Trường, góp phần tích cực vào sự phát triển tại ĐHQG-HCM và phục vụ xã hội.', 'Logo-Math-CS-cyan-192.png', '<p>CHƯƠNG TRÌNH HỖ TRỢ CÔNG BỐ KHOA HỌC VÀ CÔNG NGHỆ (Đợt 2/2024)&nbsp;</p><ol><li>Mục tiêu: Nhằm khuyến khích Thầy Cô và người học tại Trường trong việc nghiên cứu khoa học, phát triển công nghệ, công bố các kết quả khoa học và công nghệ (KH&amp;CN); từ đó nâng cao năng lực và hiệu quả trong nghiên cứu khoa học, phát triển công nghệ tại Trường, góp phần tích cực vào sự phát triển tại ĐHQG-HCM và phục vụ xã hội.</li><li>Công bố khoa học và công nghệ (công bố KH&amp;CN) bao gồm:</li></ol><ul><li>&nbsp;<ul><li>Bài báo khoa học được đăng trên Tạp chí/Kỷ yếu Hội nghị hoặc chương sách thuộc danh mục Scopus hay Web of Science;</li></ul></li><li>&nbsp;<ul><li>Đăng ký sáng chế trong và ngoài nước (được chấp nhận đơn hay được cấp bằng độc quyền); đăng ký giải pháp hữu ích trong nước (được chấp nhận đơn hay được cấp bằng độc quyền); bản quyền phần mềm (được cấp giấy chứng nhận).</li></ul></li></ul><ol><li>Thời gian đăng ký đợt 2 (năm 2024) chương trình hỗ trợ công bố KH&amp;CN từ 01/04/2024 đến 30/04/2024. Đợt 2 này dành cho các công bố trong năm 2022 và 2023.</li></ol><p>Chi tiết về chương trình, vui lòng xem tại: <a href=\"https://research.hcmus.edu.vn/cong-bo-khoa-hoc\">https://research.hcmus.edu.vn/cong-bo-khoa-hoc</a></p><p><a href=\"https://www.facebook.com/hashtag/khoahoc?__eep__=6&amp;__cft__[0]=AZVI29fIzujkN6zLiUH-Ylv3lDl-12bqKfA9B5W2YMvAXbPXfIdiqQVRvXvO0sc-JK9WFlvu84HERuEfZh4ANDB2RG2oZfruShVEKv9WPOwNclCYS8-jZNEs_uGITfZ-Bat7BlSoTHPbI2v7elej3CSK&amp;__tn__=*NK-R\">#khoahoc</a> <a href=\"https://www.facebook.com/hashtag/congnghe?__eep__=6&amp;__cft__[0]=AZVI29fIzujkN6zLiUH-Ylv3lDl-12bqKfA9B5W2YMvAXbPXfIdiqQVRvXvO0sc-JK9WFlvu84HERuEfZh4ANDB2RG2oZfruShVEKv9WPOwNclCYS8-jZNEs_uGITfZ-Bat7BlSoTHPbI2v7elej3CSK&amp;__tn__=*NK-R\">#congnghe</a> <a href=\"https://www.facebook.com/hashtag/scopus?__eep__=6&amp;__cft__[0]=AZVI29fIzujkN6zLiUH-Ylv3lDl-12bqKfA9B5W2YMvAXbPXfIdiqQVRvXvO0sc-JK9WFlvu84HERuEfZh4ANDB2RG2oZfruShVEKv9WPOwNclCYS8-jZNEs_uGITfZ-Bat7BlSoTHPbI2v7elej3CSK&amp;__tn__=*NK-R\">#Scopus</a> <a href=\"https://www.facebook.com/hashtag/wos?__eep__=6&amp;__cft__[0]=AZVI29fIzujkN6zLiUH-Ylv3lDl-12bqKfA9B5W2YMvAXbPXfIdiqQVRvXvO0sc-JK9WFlvu84HERuEfZh4ANDB2RG2oZfruShVEKv9WPOwNclCYS8-jZNEs_uGITfZ-Bat7BlSoTHPbI2v7elej3CSK&amp;__tn__=*NK-R\">#WoS</a></p><p>&nbsp;</p>', 1, 0, 11, 1, '2019-04-27 11:00:03', '2024-07-11 14:10:52', 0),
(5, 'Thông báo Trường hè \"Sinh Viên Thực Tập Nghiên Cứu Khoa Học\"', 'Thông báo Trường hè \"Sinh Viên Thực Tập Nghiên Cứu Khoa Học\"', 'Logo-Math-CS-cyan-192.png', '<p>Thông tin chi tiết vui lòng xem thông báo<a href=\"https://drive.google.com/file/d/1mt9wZriTeiBpLvXu7ZNDtc5zYlwgLwX0/view?usp=sharing\"> tại đây</a>.</p><p>&nbsp;</p><p>&nbsp;</p>', 1, 0, 11, 1, '2019-04-27 11:01:05', '2024-07-11 14:07:37', 0),
(6, 'Bế mạc Olympic Toán học SV-HS năm 2024', 'Bế mạc Olympic Toán học SV-HS năm 2024', 'Logo-Math-CS-cyan-192.png', '<p>Lễ Tổng kết và Trao giải Kỳ thi&nbsp;<strong>Olympic Toán học sinh viên&nbsp;và học sinh</strong> lần thứ 30 diễn ra tại Trường ĐH Duy Tân, Đà Nẵng vào ngày 13/4. Kỳ thi do Hội Toán học Việt Nam phối hợp với Liên hiệp các Hội Khoa học Kỹ thuật Việt Nam, Trường ĐH Khoa học Tự nhiên (ĐH Quốc gia Hà Nội) tổ chức.</p><p>Ở phần thi dành cho sinh viên, Đội tuyển Trường Đại học Khoa học Tự nhiên dưới sự dẫn dắt của hai huấn luyện viên Tiến sĩ Nguyễn Hữu Trí Nhật và Tiến sĩ Vũ Đỗ Huy Cường đã xuất sắc giành được 09 giải trên tổng số. Cụ thể như sau:&nbsp;</p><ol><li>&nbsp;Môn Giải tích:&nbsp;</li></ol><ul><li>Giải nhất: bạn Đoàn Đức Tuấn</li><li>Giải nhì: bạn Nguyễn Xuân Tiến, bạn Võ Thịnh Phát.&nbsp;</li><li>Giải ba: bạn Nguyễn Đặng Hữu Thịnh, bạn Đoàn Quang Thắng.</li></ul><ol><li>Môn Đại số:&nbsp;</li></ol><ul><li>Giải nhì: bạn Đỗ Tiền Hải, bạn Nguyễn Vũ Hoàng Minh</li><li>Giải ba: bạn Nguyễn Xuân Tiến, Bạn Nguyễn Hoàng Khôi Nguyên</li></ul><p>Hi vọng những thành công này sẽ truyền cảm hứng cho những thế hệ sinh viên tiếp theo, khuyến khích các bạn bước ra ánh sáng và đặt ra những mục tiêu cao hơn, vươn tới những tầm cao mới trong hành trình khám phá tri thức và tinh thần toán học.&nbsp;</p><p>Chúc mừng các bạn và mong rằng thành công này sẽ là động lực để các bạn tiếp tục phấn đấu và gặt hái thêm nhiều thành tựu trong tương lai.</p><p>&nbsp;</p>', 1, 1, 1, 1, '2019-04-27 11:02:32', '2024-07-24 16:05:45', 0),
(7, 'Seminar Ứng dụng Tin học tháng 06.2024 - Principled Frameworks for Designing Deep Learning Models: Efficiency, Expressivity, and Robustness', 'Bộ môn Ứng dụng Tin học  tổ chức buổi \"Seminar Ứng dụng Tin học tháng 06.2024\" với bài báo cáo được thực hiện bởi Tiến sĩ Nguyễn Hùng Minh Tân.', 'seminar_26.6_thay_Tan.png', '<p>Bộ môn Ứng dụng Tin học &nbsp;tổ chức buổi \"Seminar Ứng dụng Tin học tháng 06.2024\" với bài báo cáo được thực hiện bởi <a href=\"https://tanmnguyen89.github.io/\">Tiến sĩ Nguyễn Hùng Minh Tân</a>.</p><p>Tiến sĩ Tân hiện đang là Trợ lý Giáo sư Khoa Toán học thuộc<a href=\"https://nus.edu.sg/\">&nbsp;Đại học Quốc gia Singapore (NUS)</a>. Trước đó, anh là nghiên cứu sinh sau tiến sĩ tại Khoa Toán học của Đại học California, Los Angeles, làm việc với Tiến sĩ Stanley J. Osher. Anh đã nhận bằng Tiến sĩ về Machine Learning tại Đại học Rice dưới sự hướng dẫn của Tiến sĩ Richard G. Baraniuk. Tiến sĩ Tân là người tổ chức Hội thảo đầu tiên về Tích hợp Mô hình thần kinh sâu và Phương trình vi phân tại ICLR 2020. Anh cũng đã có hai kỳ thực tập dài hạn tuyệt vời với Amazon AI và NVIDIA Research và nhận Học bổng Sau Tiến sĩ về Đổi mới Máy tính (CIFellows) danh giá từ Hiệp hội Nghiên cứu Máy tính (CRA), Học bổng Nghiên cứu Sau đại học của NSF và Học viên Kỹ thuật Thần kinh IGERT. Anh ấy đã nhận được bằng Thạc sĩ và Cử nhân ngành Kỹ thuật Điện và Máy tính tại Đại học Rice vào tháng 5 năm 2018 và tháng 5 năm 2014.</p><p>Các bạn sinh viên, học viên cao học, nghiên cứu sinh và các đồng nghiệp quan tâm có thể đăng ký tham dự.&nbsp;</p><ul><li>Thời gian:<strong>&nbsp;09h30</strong>&nbsp;sáng&nbsp;thứ 4 ngày&nbsp;<strong>26/06/2024.</strong></li><li>Địa điểm:&nbsp;<strong>Phòng E202</strong>, Cơ sở I, Trường ĐH Khoa học Tự nhiên,&nbsp;Số 227 Đường Nguyễn Văn Cừ, Phường4 Q5, TP. HCM.</li></ul><p>Tiêu đề:&nbsp;<strong>Principled Frameworks for Designing Deep Learning Models:&nbsp;Efficiency, Expressivity, and Robustness</strong></p><p>Thông tin buổi báo cáo như sau:&nbsp;</p><p>Designing deep learning models for practical applications, including those in computer vision, natural language processing, and mathematical modeling, is an art that often involves an expensive search over candidate architectures. In this talk, the speaker will introduce two novel optimization frameworks to facilitate the process of designing efficient and expressive deep learning models including the neural ordinary differential equations (Neural ODEs) and transformers.</p><p><a href=\"https://forms.gle/iiVHodGNgcatwgyr5\">Bấm vào link</a>&nbsp;hoặc quét mã QR bên dưới trước ngày 25/06/2024 để đăng ký tham dự.</p><p>Buổi trình bày cũng sẽ&nbsp;được kết hợp phát trực tuyến cho người ở xa muốn dự.&nbsp;&nbsp;</p><figure class=\"image\"><img style=\"aspect-ratio:1800/1800;\" src=\"http://localhost:8000/assets/user/images/about\\seminar_26.6_thay_Tan.png\" width=\"1800\" height=\"1800\"></figure>', 1, 0, 3, 1, '2019-04-27 11:04:03', '2024-07-24 15:55:56', 0),
(8, 'Chương trình Học bổng sau tiến sĩ nghiên cứu tại Nhật Bản (Postdoctoral Fellowship for Research in Japan)', '- Đơn ứng tuyển theo mẫu (theo đường link bên dưới);\n- Thư đề cử từ nhà nghiên cứu chịu trách nhiệm tại Nhật Bản;\n- Bằng hoặc chứng chỉ tiến sĩ theo đúng như Mục A của Tiêu chí ứng tuyển;\n- Thư đề cử từ giảng viên hướng dẫn tiến sĩ hoặc tương đương của ứng viên.\nLưu ý: Tất cả các tài liệu đều phải bằng tiếng Anh hoặc được gửi kèm bản dịch tiếng Anh.', 'Logo-Math-CS-cyan-192.png', '<p><strong>1.&nbsp;Chi tiết chương trình:</strong><br>- Thời lượng: Từ 01 tới 12 tháng trong khoảng 01/04/2025 - 31/03/2026, được thống nhất bởi ứng viên và nhà nghiên cứu chịu trách nhiệm.<br>- Hỗ trợ tài chính: JSPS chi trả phí 01 vé máy bay khứ hồi và sinh hoạt phí hàng tháng. Cụ thể:<br>+ Sinh hoạt phí cho ứng viên có bằng tiến sĩ: 362.000 JPY/tháng.<br>+ Sinh hoạt phí cho ứng viên chưa có bằng tiến sĩ: 200.000 JPY/tháng. Nếu ứng viên có bằng tiến sĩ trong thời gian làm việc, sinh hoạt phí này có thể được tăng lên 362.000 JPY/tháng.<br>+ Ngoài ra, các ứng viên với ít nhất 3 tháng làm việc sẽ được cấp 200.000 JPY phí di dời chỗ ở, và nhiều nhất 70.000 JPY/tháng nghiên cứu phí.<br><strong>2.&nbsp;Tiêu chí ứng tuyển:</strong><br>Các ứng viên phải đạt được một trong những tiêu chí sau:<br>- Mục A: Tiêu chí về bằng cấp<br>+ Có bằng tiến sĩ không quá 06 năm trước ở một nước không phải là Nhật Bản<br>+ Sẽ có bằng tiến sĩ ở một nước không phải là Nhật Bản trong vòng không quá 02 năm kể từ ngày bắt đầu làm việc<br>- Mục B: Tiêu chí về quốc tịch<br>+ Là người có quốc tịch là một trong những nước ASEAN<br>+ Là người có quốc tịch không phải là một trong những nước ASEAN nhưng đã tham gia vào các hoạt động nghiên cứu trong ba năm gần đây tại một viện nghiên cứu ở một trong những nước ASEAN<br><strong>3.&nbsp;Hồ sơ ứng tuyển:</strong><br>- Đơn ứng tuyển theo mẫu (theo đường link bên dưới);<br>- Thư đề cử từ nhà nghiên cứu chịu trách nhiệm tại Nhật Bản;<br>- Bằng hoặc chứng chỉ tiến sĩ theo đúng như Mục A của Tiêu chí ứng tuyển;<br>- Thư đề cử từ giảng viên hướng dẫn tiến sĩ hoặc tương đương của ứng viên.<br>Lưu ý: Tất cả các tài liệu đều phải bằng tiếng Anh hoặc được gửi kèm bản dịch tiếng Anh.<br><strong>4.&nbsp;Hạn nộp hồ sơ:</strong>&nbsp;31/7/2024<br><strong>5.&nbsp;Công bố kết quả:</strong>&nbsp;Tháng 02/2025<br>Thông tin&nbsp;chi tiết và nộp hồ sơ ứng tuyển:&nbsp;<a href=\"https://www.aunsec.org/aun-action/scholarships/jsps-international-fellowships-research-japan\">https://www.aunsec.org/aun-action/scholarships/jsps-international-fellowships-research-japan</a></p>', 1, 4, 10, 1, '2019-04-27 11:06:26', '2024-07-21 14:06:02', 0),
(9, 'Lịch bảo vệ Khóa luận tốt nghiệp', 'Lịch bảo vệ Khóa luận tốt nghiệp', 'Logo-Math-CS-cyan-192.png', '<p>Khoa Toán - Tin học xin thông báo lịch bảo vệ Khóa luận tốt nghiệp, Đồ án tốt nghiệp Đại học của các ngành, chuyên ngành như sau:</p><p><strong>Lịch bảo vệ Khóa luận tốt nghiệp</strong></p><p>1. Hội đồng chuyên ngành Cơ học. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1i9qHJMuxiqHIr3vimmzrigodALbqNdsX/edit\">Tại đây</a></p><p>2.&nbsp;Hội đồng chuyên ngành Đại số. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1hbYQj1kjwWRmGrjEI2QVm66JclYyp2hL/edit\">Tại đây</a></p><p>3.&nbsp;Hội đồng chuyên ngành Giải tích số. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1b5CL7pO-NpvBofMIPGnt5H2wQA493aT0/edit\">Tại đây</a></p><p>4.&nbsp;Hội đồng chuyên ngành&nbsp;Giải tích.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1atIWBXd88jplaqvvZSM1Fhich1hr2rsT/edit\">Tại đây</a></p><p>5.&nbsp;Hội đồng chuyên ngành Lý luận và phương pháp dạy học môn Toán. Xem chi tiết&nbsp; <a href=\"https://docs.google.com/document/d/1h93DMUIZXDnWrmFIwV0TGDBUcyyppA39/edit\">Tại đây</a>.&nbsp;</p><p>6. &nbsp;Hội đồng chuyên ngành Khoa học dữ liệu. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kGKKktO_eyfZAutToKdDkYOfirHhq1X-/edit\">Tại đây</a></p><p>7.&nbsp;Hội đồng chuyên ngành Phương pháp Toán trong tin học.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kKmEumhHh1QA6aR6asDgmWbYvcI0s2Hz/edit\">Tại đây</a></p><p>8.&nbsp;Hội đồng chuyên ngành Toán tin ứng dụng.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kL0NRGGnlV3GALpxomKX4sVuZedH9dIw/edit\">Tại đây</a></p><p>9.&nbsp;Hội đồng chuyên ngành Toán tài chính.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1f7f1kZpfo2MddlIFKWhx0XxsFWFjYs4u/edit\">Tại đây</a></p><p>10.&nbsp;Hội đồng chuyên ngành Tối ưu.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1fA595_GYb5AcgCisuSlH6KsCDZrzapx8/edit\">Tại đây</a></p><p><strong>Lịch bảo vệ Đồ án tốt nghiệp</strong></p><p>&nbsp;1. Hội đồng chấm Đồ án Tốt nghiệp ngành Khoa học dữ liệu. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kJFbFYWavBNuf_hKYhfaWuWsldH_7-0a/edit\">Tại đây</a></p>', 1, 3, 4, 1, '2019-04-27 11:11:14', '2024-07-22 07:28:05', 0),
(10, 'Lịch bảo vệ Khóa luận Tốt nghiệp - Đồ án tốt nghiệp, HK2/2023-2024', 'Khoa Toán - Tin học xin thông báo lịch bảo vệ Khóa luận tốt nghiệp, Đồ án tốt nghiệp Đại học của các ngành, chuyên ngành như sau:', 'Logo-Math-CS-cyan-192.png', '<p>Khoa Toán - Tin học xin thông báo lịch bảo vệ Khóa luận tốt nghiệp, Đồ án tốt nghiệp Đại học của các ngành, chuyên ngành như sau:</p><p><strong>Lịch bảo vệ Khóa luận tốt nghiệp</strong></p><p>1. Hội đồng chuyên ngành Cơ học. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1i9qHJMuxiqHIr3vimmzrigodALbqNdsX/edit\">Tại đây</a></p><p>2.&nbsp;Hội đồng chuyên ngành Đại số. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1hbYQj1kjwWRmGrjEI2QVm66JclYyp2hL/edit\">Tại đây</a></p><p>3.&nbsp;Hội đồng chuyên ngành Giải tích số. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1b5CL7pO-NpvBofMIPGnt5H2wQA493aT0/edit\">Tại đây</a></p><p>4.&nbsp;Hội đồng chuyên ngành&nbsp;Giải tích.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1atIWBXd88jplaqvvZSM1Fhich1hr2rsT/edit\">Tại đây</a></p><p>5.&nbsp;Hội đồng chuyên ngành Lý luận và phương pháp dạy học môn Toán. Xem chi tiết&nbsp; <a href=\"https://docs.google.com/document/d/1h93DMUIZXDnWrmFIwV0TGDBUcyyppA39/edit\">Tại đây</a>.&nbsp;</p><p>6. &nbsp;Hội đồng chuyên ngành Khoa học dữ liệu. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kGKKktO_eyfZAutToKdDkYOfirHhq1X-/edit\">Tại đây</a></p><p>7.&nbsp;Hội đồng chuyên ngành Phương pháp Toán trong tin học.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kKmEumhHh1QA6aR6asDgmWbYvcI0s2Hz/edit\">Tại đây</a></p><p>8.&nbsp;Hội đồng chuyên ngành Toán tin ứng dụng.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kL0NRGGnlV3GALpxomKX4sVuZedH9dIw/edit\">Tại đây</a></p><p>9.&nbsp;Hội đồng chuyên ngành Toán tài chính.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1f7f1kZpfo2MddlIFKWhx0XxsFWFjYs4u/edit\">Tại đây</a></p><p>10.&nbsp;Hội đồng chuyên ngành Tối ưu.&nbsp;Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1fA595_GYb5AcgCisuSlH6KsCDZrzapx8/edit\">Tại đây</a></p><p><strong>Lịch bảo vệ Đồ án tốt nghiệp</strong></p><p>&nbsp;1. Hội đồng chấm Đồ án Tốt nghiệp ngành Khoa học dữ liệu. Xem chi tiết&nbsp;<a href=\"https://docs.google.com/document/d/1kJFbFYWavBNuf_hKYhfaWuWsldH_7-0a/edit\">Tại đây</a></p>', 1, 0, 2, 1, '2019-05-04 19:10:44', '2024-07-22 06:55:16', 0),
(11, 'Chương trình chuyên tu liên kết chính sách hướng Nam mới dành cho sinh viên ưu tú - Trường Đại học Công nghệ Đài Bắc', 'Chương trình được giảng dạy bằng tiếng Anh về lĩnh vực công nghệ chất bán dẫn và AI, ngoài ra còn có chương trình giao lưu về chuyên môn và văn hóa như tiết học tiếng Hoa, tham quan nhà máy, trải nghiệm văn hóa địa phương...', '新南向海報ver.1 (2).png', '<p><strong>1. Thời gian đào tạo:</strong>&nbsp;05 tháng kể từ tháng 9/2024 đến tháng 01/2025</p><p><strong>2. Nội dung đào tạo:&nbsp;</strong>Chương trình được giảng dạy bằng tiếng Anh về lĩnh vực công nghệ chất bán dẫn và AI, ngoài ra còn có chương trình giao lưu về chuyên môn và văn hóa như tiết học tiếng Hoa, tham quan nhà máy, trải nghiệm văn hóa địa phương...</p><p><strong>3. Học bổng bao gồm:</strong></p><p>- Vé máy bay khứ hồi hạng phổ thông Việt Nam - Đài Loan;</p><p>- Học phí và chi phí khác trong trường;</p><p>- Sinh hoạt phí: 10,000 Đài tệ/tháng;</p><p>- Phí lưu trú: Thanh toán theo hóa đơn, mức tài trợ tối đa 18,000 Đài tệ/tháng.</p><p><strong>4. Điều kiện dự tuyển:</strong></p><p>- Sinh viên Việt Nam hoặc các nước nằm trong mục tiêu chính sách hướng Nam mới, đang theo học đại học năm thứ 2 đến năm thứ 4 của các khoa ngành tự nhiên, lĩnh vực công nghệ tương quan;</p><p>- Sinh viên không mang hộ chiếu Cộng hòa Nhân dân Trung Hoa hoặc hộ chiếu Trung Hoa Dân Quốc;</p><p>- Sinh viên không được cùng lúc nhận học bổng của các cơ quan, đơn vị khác.</p><p><strong>5. Phương thức đăng ký:</strong></p><p>Đăng ký online tại link trường ĐH CN Đài Bắc (<a href=\"https://forms.gle/wh5bc99iPqaRhoie9\">https://forms.gle/wh5bc99iPqaRhoie9</a>)&nbsp;trước ngày 10/7/2024. Trường sơ tuyển và thông báo phỏng vấn, danh sách được chọn dự kiến thông báo qua email vào ngày 17/7/2024.</p><figure class=\"image\"><img style=\"aspect-ratio:1414/2000;\" src=\"http://localhost:8000/assets/user/images/about\\新南向海報ver.1 (2).png\" width=\"1414\" height=\"2000\"></figure><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>', 1, 8, 1, 1, '2019-05-04 19:12:30', '2024-07-24 14:03:11', 0),
(13, 'Buổi ký kết thỏa thuận hợp tác (MOU) giữa Khoa Toán - Tin học, Trường ĐH KHTN, ĐHQG-HCM và HRS GROUP', 'Sáng ngày 24/05/2024, buổi lễ ký kết thỏa thuận hợp tác (MOU) giữa Khoa Toán - Tin học và HRS Group đã diễn ra thành công tốt đẹp. Tại buổi lễ, hai bên đã thống nhất các điều khoản thỏa thuận, nội dung hợp tác, hỗ trợ lẫn nhau trong các hoạt động đào tạo, chia sẻ kiến thức.', '71235f41ce3e6e60372f3.jpg', '<p>Sáng ngày 24/05/2024, buổi lễ ký kết thỏa thuận hợp tác (MOU) giữa Khoa Toán - Tin học và HRS Group đã diễn ra thành công tốt đẹp. Tại buổi lễ, hai bên đã thống nhất các điều khoản thỏa thuận, nội dung hợp tác, hỗ trợ lẫn nhau trong các hoạt động đào tạo, chia sẻ kiến thức. Thông qua thỏa thuận, HRS cam kết sẽ mang đến nhiều cơ hội để các bạn sinh viên được làm việc trong môi trường chuyên nghiệp, đúng chuyên môn. Nhân dịp này, HRS cũng đã tổ chức buổi seminar nhằm cung cấp thông tin cho các bạn sinh viên để chuẩn bị tốt hơn cho sự nghiệp tương lai và kỹ năng thực tế trong bối cảnh sự chuyển đổi mạnh mẽ của môi trường công nghệ.</p>', 1, 0, 1, 1, '2024-07-05 10:34:55', '2024-07-24 14:02:43', 0),
(14, 'Chuyên đề: \"KỸ NĂNG THUYẾT TRÌNH VÀ PHẢN BIỆN HỘI ĐỒNG KHOA HỌC\"', 'Chương trình thuộc Chuỗi hoạt động đồng hành cùng sinh viên học tốt, tích cực nghiên cứu khoa học năm 2024\" do Đoàn trường tổ chức. Đây là cơ hội để các bạn gặp gỡ các chuyên gia - là những Thầy, Cô đã có nhiều thành tựu nổi bật trong công tác nghiên cứu khoa học.\nSau các buổi chuyên đề bạn sẽ nhận được:\n- Nắm vững các kiến thức cơ bản và một số kỹ năng giúp ích trong việc nghiên cứu khoa học trong tương lai\n- Trao đổi, thảo luận trực tiếp với các chuyên gia\n- Định hướng hoặc xác định hướng nghiên cứu cụ thể\n- Ghi nhận hoạt động rèn luyện sau mỗi chuyên đề.\n- Nhận giấy chứng nhận khi tham gia hơn 50% trên tổng số buổi chuyên đề. Bạn có thể dùng để phục vụ xét chọn các Danh hiệu trong hệ thống Danh hiệu của Đoàn Trường - Hội Sinh viên trường.', 'AEA2024_-_CĐ7 (1).png', '<p>Chương trình thuộc Chuỗi hoạt động đồng hành cùng sinh viên học tốt, tích cực nghiên cứu khoa học năm 2024\" do Đoàn trường tổ chức. Đây là cơ hội để các bạn gặp gỡ các chuyên gia - là những Thầy, Cô đã có nhiều thành tựu nổi bật trong công tác nghiên cứu khoa học.<br>Sau các buổi chuyên đề bạn sẽ nhận được:<br>- Nắm vững các kiến thức cơ bản và một số kỹ năng giúp ích trong việc nghiên cứu khoa học trong tương lai<br>- Trao đổi, thảo luận trực tiếp với các chuyên gia<br>- Định hướng hoặc xác định hướng nghiên cứu cụ thể<br>- Ghi nhận hoạt động rèn luyện sau mỗi chuyên đề.<br>- Nhận giấy chứng nhận khi tham gia hơn 50% trên tổng số buổi chuyên đề. Bạn có thể dùng để phục vụ xét chọn các Danh hiệu trong hệ thống Danh hiệu của Đoàn Trường - Hội Sinh viên trường.<br><br><strong>Chuyên đề 07:</strong> \"<strong>KỸ NĂNG THUYẾT TRÌNH VÀ PHẢN BIỆN HỘI ĐỒNG KHOA HỌC</strong>\"</p><p>-<strong> Báo cáo viên:</strong>&nbsp;<strong>&nbsp;</strong> <strong>TS. Trương Lâm Sơn Hải</strong> - Giảng viên Khoa hóa học - Đạt giải thưởng quả cầu vàng năm 2022, Thành viên BCN Câu lạc bộ Các nhà Khoa học trẻ TP.HCM<br><strong>- Thời gian</strong>: 17h30 ngày 08/07/2024 (Thứ 2)</p><p><strong>- Địa điểm:</strong>&nbsp; <strong>Phòng I23</strong> - Trường Đại học Khoa học Tự nhiên, ĐHQG-HCM (Cơ sở Nguyễn Văn Cừ)<br><strong>- Lệ phí</strong>: miễn phí<br>- Thời gian đăng ký: từ nay đến trước 12h00 AM ngày 08/7/2024 (Thứ 2)<br>- Đăng ký ngay tại đây:&nbsp;<a href=\"https://bit.ly/chuyende07-AEA2024\">https://bit.ly/chuyende07-AEA2024</a><br>Chi tiết liên hệ Ban học tập Đoàn trường:<br>- Email: <a href=\"mailto:banhoctap@hcmus.edu.vn\">banhoctap@hcmus.edu.vn</a><br>- Fanpage: <a href=\"https://www.facebook.com/Tunhienhoc\">https://www.facebook.com/Tunhienhoc</a></p><figure class=\"image\"><img style=\"aspect-ratio:2364/3544;\" src=\"http://localhost:8000/assets/user/images/about\\AEA2024_-_CĐ7 (1).png\" width=\"2364\" height=\"3544\"></figure>', 1, 0, 4, 1, '2024-07-24 15:42:26', '2024-07-24 16:06:53', 0),
(15, 'Mathematics on the Crossroad of Centuries - A Conference in Honor of Maxim Kontsevich\'s 60th Birthday', 'Mathematics on the Crossroad of Centuries - A Conference in Honor of Maxim Kontsevich\'s 60th Birthday', 'Poster_MK60Conf_16-20Sept2024.png', '<p>&nbsp;</p><figure class=\"image\"><img style=\"aspect-ratio:3508/4961;\" src=\"http://localhost:8000/assets/user/images/about\\Poster_MK60Conf_16-20Sept2024.png\" width=\"3508\" height=\"4961\" alt=\"\"><figcaption>&nbsp;</figcaption></figure>', 0, 0, 4, 1, '2024-07-24 15:45:18', '2024-07-24 15:45:18', 0),
(16, 'Đăng ký tham gia Trường hè Toán học sinh viên 2024', 'rường hè Toán học sinh viên nhằm giới thiệu cho sinh viên một số chủ đề của Toán học lý thuyết và Toán ứng dụng đã và đang được quan tâm trên thế giới. Thông qua các bài giảng, sinh viên sẽ được mở rộng thêm kiến thức và hiểu biết về văn hóa Toán học, từ đó định hướng cho việc nghiên cứu, học tập tiếp theo.', 'y-nghia-hoa-cuc-tana-ha-lan.jpg', '<p>Trường hè Toán học sinh viên nhằm giới thiệu cho sinh viên một số chủ đề của Toán học lý thuyết và Toán ứng dụng đã và đang được quan tâm trên thế giới. Thông qua các bài giảng, sinh viên sẽ được mở rộng thêm kiến thức và hiểu biết về văn hóa Toán học, từ đó định hướng cho việc nghiên cứu, học tập tiếp theo. &nbsp;</p><ol><li><strong>Thông tin cơ bản:</strong><br>-&nbsp; &nbsp; &nbsp;Thời gian: 08:00 ngày 08/07/2024 đến 17:00 ngày 21/07/2024<br>- &nbsp; &nbsp; Địa điểm: &nbsp;Trường Đại học FPT Quy Nhơn, TP. Quy Nhơn, Bình Định &nbsp;<br>- &nbsp; &nbsp; Số lượng: Khoa sẽ xét chọn tối đa 12 sinh viên<br>- &nbsp; &nbsp; Thời gian đăng ký: <strong>Từ ngày 01/06/2024 đến 12 giờ ngày 07/06/2024</strong><br><br>&nbsp;</li><li><strong>Nội dung:</strong> Trường hè Toán học sinh viên 2024 được tổ chức trong thời gian 02 tuần, bao gồm các chuyên đề như sau:<br>- &nbsp; &nbsp; Chuyên đề chung cho tất cả sinh viên tham dự Trường hè (từ ngày 08/07-14/07/2024)<br>- &nbsp; &nbsp; Chuyên đề Cơ sở Toán học cho Học Máy (từ ngày 15/7-21/7/2024)<br><strong>&nbsp; &nbsp; &nbsp; Sinh viên chọn 1 trong 2 chuyên đề sau:</strong><br>&nbsp; &nbsp; &nbsp; &nbsp;1. Chuyên đề I: &nbsp;Nhập môn giải tích Fourier và một số ứng dụng<br>&nbsp; &nbsp; &nbsp; &nbsp;2. Chuyên đề II: Nhập môn lý thuyết mô đun: Mô đun trên miền chính và Dạng chuẩn của tự đồng cấu tuyến tính<br><br>&nbsp;</li><li><strong>Đơn vị tổ chức và phối hợp tổ chức</strong><br>- &nbsp; &nbsp; Viện Nghiên cứu cao cấp về Toán<br>- &nbsp; &nbsp; &nbsp;Trường Đại học FPT<br><br>&nbsp;</li><li><strong>Thông tin tài trợ</strong><br>- &nbsp; &nbsp; Trường hè miễn phí đối với người tham dự.<br>- &nbsp; &nbsp; Học viên được tài trợ chỗ ở, chi phí đi lại và một phần sinh hoạt phí trong suốt thời gian diễn ra Trường hè.<br><br>&nbsp;</li><li><strong>Đăng ký:</strong></li></ol><p>Các bạn quan tâm vui lòng <a href=\"https://docs.google.com/forms/d/e/1FAIpQLSfU4IQUuyoAK6fVjvmwa4olJzTy_aKK-AbQoV-MgmzMtTBRkQ/viewform\"><strong>đăng ký tại đường liên kết này.</strong></a></p>', 0, 0, 4, 1, '2024-07-24 15:46:33', '2024-07-24 15:46:33', 0),
(17, 'Cuộc thi Tìm kiếm giải pháp đổi mới sáng tạo trong khu vực công năm 2024 Saigon Govtech Challenge 2024 (Gov.Star 2024)\"', 'Cuộc thi Tìm kiếm giải pháp đổi mới sáng tạo trong khu vực công năm 2024 Saigon Govtech Challenge 2024 (Gov.Star 2024)\n1. Các giải pháp, mô hình đổi mới sáng tạo, ứng dụng công nghệ, chuyển đổi số trên các lĩnh vực:\n- Phục vụ hoạt động công vụ của các Sở, Thành phố Thủ Đức, quận, huyện, phường, xã, thị trấn.', 'govstar-2024-00.jpg', '<p><strong>Cuộc thi Tìm kiếm giải pháp đổi mới sáng tạo trong khu vực công năm 2024 Saigon Govtech Challenge 2024 (Gov.Star 2024)</strong></p><p><strong>1. Các giải pháp, mô hình đổi mới sáng tạo, ứng dụng công nghệ, chuyển đổi số trên các lĩnh vực:</strong></p><p>- Phục vụ hoạt động công vụ của các Sở, Thành phố Thủ Đức, quận, huyện, phường, xã, thị trấn.</p><p>- Hỗ trợ hoạt động khám chữa bệnh tại hệ thống y tế cơ sở.</p><p>- Phục vụ hoạt động quản lý, dạy và học.</p><p><strong>2. Đối tượng tham gia:</strong>&nbsp;Cá nhân hoặc nhóm</p><p><strong>3. Hạn chót nộp hồ sơ dự thi:</strong>&nbsp;15/6/2024.</p><p><strong>4. Email nhận bài dự thi:</strong>&nbsp;<a href=\"mailto:govstar.dosthcmc@gmail.com\">govstar.dosthcmc@gmail.com</a>&nbsp;</p><p><strong>5. Thể lệ chi tiết&nbsp;</strong><a href=\"https://drive.google.com/file/d/1UGOh4oxiVsvNh38kNFUm7VAOGY25ZWM5/view?usp=sharing\"><i>(Tệp đính kèm)</i></a></p><figure class=\"image\"><img style=\"aspect-ratio:1708/650;\" src=\"http://localhost:8000/assets/user/images/about\\govstar-2024-00.jpg\" width=\"1708\" height=\"650\"></figure>', 0, 0, 4, 1, '2024-07-24 15:48:05', '2024-07-24 15:48:05', 0),
(18, 'Seminar Cơ học tháng 07. 2024', 'Bộ môn Cơ học tổ chức buổi \"Seminar Cơ học tháng 07.2024\" với 2 bài báo cáo của Tiến sĩ Đặng Lê Quang và Tiến sĩ Vũ Hồ Thảo Thuận. Buổi trình bày này sẽ giới thiệu vai trò quan trọng của Khoa học Dữ liệu trong việc nâng cao hiệu suất và tối ưu hóa các quy trình kỹ thuật, góp phần thúc đẩy sự phát triển bền vững và đổi mới trong các ngành công nghiệp đồng thời buổi seminar cũng sẽ giới thiệu sơ lược về cơ học Hamilton và các ứng dụng', 'Logo-Math-CS-cyan-192.png', '<p>Bộ môn Cơ học tổ chức buổi \"Seminar Cơ học tháng 07.2024\" với 2 bài báo cáo của Tiến sĩ <strong>Đặng Lê Quang</strong> và Tiến sĩ <strong>Vũ Hồ Thảo Thuận</strong>.&nbsp;Buổi trình bày này sẽ giới thiệu vai trò quan trọng của Khoa học Dữ liệu trong việc nâng cao hiệu suất và tối ưu hóa các quy trình kỹ thuật, góp phần thúc đẩy sự phát triển bền vững và đổi mới trong các ngành công nghiệp đồng thời buổi seminar cũng sẽ giới thiệu&nbsp;sơ lược về cơ học Hamilton và các ứng dụng</p><p>Các bạn sinh viên, học viên cao học, nghiên cứu sinh và các đồng nghiệp quan tâm có thể <a href=\"https://forms.gle/cYqy2FPhz8SmgUk56\">đăng ký tham dự tại đây.</a></p><ul><li>Thời gian:<strong>&nbsp;09h00</strong>&nbsp;sáng thứ 6 ngày<strong> 26/07/2024.</strong></li><li>Địa điểm: <strong>Phòng C43A</strong> - Trường ĐH Khoa học tự nhiên, cơ sở 1, 227 Nguyễn Văn Cừ, Q5, TP. HCM.</li></ul><p>Nội dung:&nbsp;</p><ol><li>Bài báo cáo số 1:&nbsp;<ul><li>Tiêu đề:&nbsp;<strong>Ứng dụng Khoa học Dữ liệu trong các lĩnh vực kỹ thuật</strong></li><li>Diễn giả: <strong>TS Đặng Lê Quang</strong> – Đại học Ngoại Thương</li><li>Tóm tắt bài báo cáo như sau:&nbsp;<br>Khoa học Dữ liệu đã trở thành một công cụ thiết yếu trong nhiều lĩnh vực kỹ thuật, mang lại những cải tiến vượt bậc về quy trình làm việc và hiệu suất. Trong lĩnh vực kỹ thuật xây dựng, Khoa học Dữ liệu hỗ trợ dự đoán tuổi thọ công trình, phân tích địa chất và tối ưu hóa quy hoạch đô thị thông qua các mô hình dự đoán dựa trên dữ liệu. Các kỹ thuật phân tích dữ liệu tiên tiến giúp cải thiện thiết kế sản phẩm, dự đoán sự cố thiết bị và tối ưu hóa quy trình sản xuất trong kỹ thuật cơ khí. Trong kỹ thuật điện, Khoa học Dữ liệu đóng vai trò quan trọng trong việc quản lý và tối ưu hóa lưới điện, dự báo nhu cầu tiêu thụ năng lượng và giám sát hiệu suất hệ thống điện. Hơn nữa, trong ngành hàng không, ứng dụng Khoa học Dữ liệu đã cải thiện hiệu suất bay, tối ưu hóa lịch trình bảo trì và nâng cao an toàn bay thông qua việc phân tích dữ liệu cảm biến và hệ thống.<br>Đặc biệt, trong lĩnh vực mô phỏng số, Khoa học Dữ liệu đang tạo ra những thay đổi đột phá. Các mô hình mô phỏng số sử dụng các thuật toán học máy để tái tạo và dự đoán các hiện tượng phức tạp trong các hệ thống kỹ thuật. Điều này giúp giảm thiểu thời gian và chi phí cho việc tính toán, đồng thời tăng độ chính xác của các mô phỏng. Các ứng dụng nổi bật của Khoa học Dữ liệu bao gồm mô phỏng dòng chảy chất lỏng trong kỹ thuật thủy lực, dự đoán hiệu suất của các vật liệu mới trong kỹ thuật vật liệu, và mô phỏng khí động lực học trong ngành hàng không. Những ứng dụng này không chỉ cải thiện hiệu quả và độ chính xác mà còn mở ra những hướng nghiên cứu và phát triển mới trong các lĩnh vực kỹ thuật.</li></ul></li><li>&nbsp;Bài báo cáo số 2:&nbsp;<ul><li>Tiêu đề:&nbsp;<strong>Hamiltonian mechanics approach to 2-layer dispersive stratified fluids</strong></li><li>Diễn giả:&nbsp;<strong>TS. Vũ Hồ Thảo Thuận</strong> – Đại học Monash, Úc</li><li>Tóm tắt bài báo cáo như sau:<br>Bài thuyết trình giới thiệu sơ lược về cơ học Hamilton, và các ứng dụng cơ học Hamilton vào việc mô hình hệ chất lưu 2 lớp (stratified fluids). Mô hình có sử dụng phương pháp long-wave asymptotics, và một số khái niệm chuyên ngành từ hình học vi phân.</li></ul></li></ol><p><a href=\"https://forms.gle/cYqy2FPhz8SmgUk56\">Bấm vào link</a>&nbsp;trước ngày 25/07/2024 để đăng ký tham dự.</p>', 0, 0, 3, 1, '2024-07-24 15:48:55', '2024-07-24 15:48:55', 0),
(19, 'Thông báo chuỗi seminar: \"The role of mathematics and optimization in Machine Learning/Deep Learning\"', 'hoa Toán - Tin học thông báo chuỗi seminar gồm 3 bài giảng trong các ngày 29, 30 và 31/7, thông tin chi tiết như sau:', 'Poster_Prof._TT_Tuyen.jpg', '<p><strong>Khoa Toán - Tin học thông báo chuỗi seminar gồm 3 bài giảng trong các ngày 29, 30 và 31/7, thông tin chi tiết như sau:</strong></p><ul><li><strong>Tên chuỗi bài giảng:</strong>&nbsp;The role of mathematics and optimization in Machine Learning/Deep Learning.</li><li><strong>Báo cáo viên:</strong> Giáo sư Trương Trung Tuyến, Khoa Toán, Đại học Oslo, Na uy.</li><li><strong>Thời gian:</strong> các buổi sáng từ 9:15 đến 11:30 ngày 29, 30 và 31/7.&nbsp;</li><li><strong>Địa</strong> <strong>điểm:</strong> Trường Đại học Khoa học tự nhiên, ĐHQG-HCM, 227 Nguyễn Văn Cừ, Q5, TP. HCM (Phòng học cụ thể sẽ được BTC thông báo sau).</li><li><strong>Hình thức:</strong> trực tiếp và trực tuyến trên zoom.</li><li><strong>Ngôn ngữ:</strong> Tiếng Anh.</li><li><strong>Tóm tắt:</strong>&nbsp;This mini-series will present, from the speaker\'s experience, the role of Mathematics and Optimization in Machine Learning/Deep Learning. Various examples on explicit usages of different fields (Logic, Approximation theory, Calculus, Statistics, Linear Algebra, Optimization, Dynamical systems, Metric/Topology, Semi-algebraic/analytic geometry, Riemann geometry, and Mathematical education) in Machine Learning/Deep Learning/LLM will be presented.&nbsp;</li><li><strong>Giới thiệu về GS. Trương Trung Tuyến:&nbsp;</strong>Current position: Professor of Mathematics, University of Oslo, Norway. Undergraduate degree from University of Science, Ho Chi Minh City, Vietnam. PhD degree from Indiana University, USA. Research interest: both pure and applied mathematics, including optimization with applications in Deep Neural Networks, applications of Machine Learning/Deep Learning/LLM to health care and mathematical education.&nbsp;</li></ul><p><strong>Đăng ký tham dự:&nbsp;</strong></p><ul><li><strong>Đối tượng:</strong> giảng viên, nghiên cứu sinh, học viên cao học, sinh viên và nhà nghiên cứu về Toán, Toán - Tin, Toán ứng dụng, Công nghệ thông tin, Khoa học dữ liệu có quan tâm.&nbsp;</li><li><strong>Đăng ký tham dự:</strong> việc tham dự là miễn phí nhưng đăng ký là bắt buộc. Quý thầy cô, NCS, HVCH, SV và người quan tâm có thể đăng ký <a href=\"https://forms.gle/KMFGrGFSh9PPKcy28\">tại đây</a> hoặc quét mã QR trên poster. Người tham dự trực truyến trên zoom cũng cần đăng ký để nhận link tham dự.&nbsp;</li><li><strong>Hạn chót đăng ký: 26/7/2024.&nbsp;</strong></li></ul><p>Các câu hỏi và thông tin khác, vui lòng liên hệ: TS. Hoàng Văn Hà, <a href=\"mailto:hvha@hcmus.edu.vn\">hvha@hcmus.edu.vn</a>.&nbsp;</p>', 0, 0, 3, 1, '2024-07-24 15:50:04', '2024-07-24 15:50:04', 0),
(20, 'Mini – Course: High-dimensional Statistics', 'Khoa Toán - Tin học phối hợp với Viện Nghiên cứu Cao cấp về Toán tổ chức Mini-course: High-dimensional Statistics, từ 22/7 đến 26/7/2024. Thông tin chi tiết về khóa học như sau:\n\nThông tin mini-course trên website của Viện nghiên cứu cao cấp về Toán: https://viasm.edu.vn/hdkh/mini-course-HDSHCM2024', 'Mini-course_High_dimensional_statistis.png', '<p>Khoa Toán - Tin học phối hợp với Viện Nghiên cứu Cao cấp về Toán tổ chức Mini-course: High-dimensional Statistics, từ 22/7 đến 26/7/2024. Thông tin chi tiết về khóa học như sau:</p><p>Thông tin mini-course trên website của Viện nghiên cứu cao cấp về Toán:&nbsp;<a href=\"https://viasm.edu.vn/hdkh/mini-course-HDSHCM2024\">https://viasm.edu.vn/hdkh/mini-course-HDSHCM2024</a>&nbsp;</p><p><strong>Thời gian:&nbsp;</strong>08:30:22/7/2024 đến 16:30:26/7/2024, cụ thể:</p><ul><li>giờ học lý thuyết sẽ diễn ra trong các buổi sáng từ 22/7 đến 26/7 từ 8:30 đến 12:00;</li><li>giờ học bài tập/thực hành diễn ra. trong các buổi chiều hai, tư sáu từ 13:30 đến 16:30.&nbsp;</li></ul><p><strong>Địa điểm:&nbsp;</strong>Trường Đại học Khoa học Tự nhiên - ĐHQG TP. Hồ Chí Minh 227 Nguyễn Văn Cừ, Quận 5, TP. Hồ Chí Minh.</p><p><strong>Objective:</strong> Classical statistical methods developed during the last century were suitable when the number of observations is much larger than the number of parameters to infer. Unfortunately, many fields such as astronomy, genetics, medicine or neuroscience produce large and complex data sets, and consequently with models containing a large number of parameters for which classical tools are not adapted. This issue is often referred as the curse of dimensionality. The goal of this course is to provide most of fundamental statistical tools to face with high-dimensional data. The aim is to present the main concepts and ideas on some selected topics of high-dimensional statistics based on modern nonparametric methodologies such as multiple testing, kernel, wavelets and penalized estimators with a special focus on Lasso estimation. Theoretical aspects are motivated by applicable developments of presented methods.</p><p><strong>Đối tượng tham dự:</strong>&nbsp;</p><ul><li>Sinh viên (năm 3, 4), học viên cao học và nghiên cứu sinh thuộc các ngành Toán học, Thống kê, Khoa học dữ liệu và Công nghệ thông tin.</li><li>Giảng viên, nghiên cứu viên thuộc các lĩnh vực trên.&nbsp;</li></ul><p><strong>Đơn vị đồng tổ chức và tài trợ:</strong></p><ul><li>Viện nghiên cứu cao cấp về Toán (VIASM)</li><li>Trường Đại học Khoa học tự nhiên, ĐHQG - HCM</li></ul><p><strong>Giảng viên:&nbsp; </strong><a href=\"https://www.ceremade.dauphine.fr/~rivoirar/\">GS. Vincent Rivoirard</a><strong>, </strong>Univeristy of Paris Dauphine - PSL, France</p><p><strong>Ngôn ngữ giảng dạy:</strong>&nbsp;English</p><p><strong>Đăng ký tham dự và tài trợ:&nbsp;</strong></p><ul><li>Đăng ký tại đây:&nbsp;<a href=\"https://forms.gle/V3PCeizVJ3UqyJY96\">Here</a></li><li>Hạn chót cho đăng ký tham dự:&nbsp;<strong>14/7/2024</strong></li><li>Hạn chót cho đăng ký tài trợ (đi lại + chỗ ở): <strong>07/7/2024</strong></li></ul><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(BTC tài trợ với số lượng giới hạn cho sinh viên thuộc các tỉnh/thành phố ngoài TP. HCM đến tham dự lớp học. SV đăng ký xin tài trợ cần gửi CV + bảng điểm đầy đủ.)</strong></p><p><strong>Liên hệ:&nbsp;</strong></p><ul><li>Hoàng Văn Hà, email:&nbsp;<a href=\"mailto:hvha@hcmus.edu.vn\">hvha@hcmus.edu.vn</a>&nbsp;</li><li><p>Nguyễn Hồng Anh, email:&nbsp;<a href=\"mailto:nguyenhonganh@viasm.edu.vn\">nguyenhonganh@viasm.edu.vn</a>&nbsp;</p><figure class=\"image\"><img style=\"aspect-ratio:1757/2686;\" src=\"http://localhost:8000/assets/user/images/about\\Mini-course_High_dimensional_statistis.png\" width=\"1757\" height=\"2686\"></figure></li></ul>', 0, 0, 3, 1, '2024-07-24 15:51:45', '2024-07-24 15:51:45', 0),
(21, 'Thông báo mở học phần Hè (HK3/2023-2024) dành cho sinh viên học lại', 'Danh sách học phần dự kiến sẽ được mở vào Học kỳ hè (HK3/2023-2024) dành cho sinh viên học lại:\n\nMã MH	Tên MH	Số TC	sotiet LT	sotiet TH	sotiet BT	giaidoan	Sỉ số	Lịch học LT	Lịch học TH/BT	Gv phụ trách	Địa điểm	Phòng\nMTH10407	Lập trình Hướng đối tượng	4	45	30	0	2	80	T4 (1-5)	T4 (6-8)\nT4 (8-10)	Hà Văn Thảo	Linh Trung	NDH 6.7\n \n\nLưu ý: Khoa chỉ mở học phần học lại giai đoạn chuyên ngành (K2022 trở về trước). Sinh viên các khóa sau sẽ học học phần này trong kế hoạch của học kỳ chính thức.', 'Logo-Math-CS-cyan-192.png', '<p><strong>Danh sách học phần dự kiến sẽ được mở vào Học kỳ hè (HK3/2023-2024) dành cho sinh viên học lại:</strong></p><figure class=\"table\"><table><tbody><tr><td><strong>Mã MH</strong></td><td><strong>Tên MH</strong></td><td><strong>Số TC</strong></td><td><strong>sotiet LT</strong></td><td><strong>sotiet TH</strong></td><td><strong>sotiet BT</strong></td><td><strong>giaidoan</strong></td><td><strong>Sỉ số</strong></td><td><strong>Lịch học LT</strong></td><td><strong>Lịch học TH/BT</strong></td><td><strong>Gv phụ trách</strong></td><td><strong>Địa điểm</strong></td><td><strong>Phòng</strong></td></tr><tr><td>MTH10407</td><td>Lập trình Hướng đối tượng</td><td>4</td><td>45</td><td>30</td><td>0</td><td>2</td><td>80</td><td>T4 (1-5)</td><td>T4 (6-8)<br>T4 (8-10)</td><td>Hà Văn Thảo</td><td>Linh Trung</td><td>NDH 6.7</td></tr></tbody></table></figure><p>&nbsp;</p><p><strong>Lưu ý</strong>: Khoa chỉ mở học phần học lại giai đoạn chuyên ngành (K2022 trở về trước). Sinh viên các khóa sau sẽ học học phần này trong kế hoạch của học kỳ chính thức.</p>', 0, 0, 2, 1, '2024-07-24 16:02:56', '2024-07-24 16:02:56', 0),
(22, 'Thông báo Thời gian chấm Seminar Phương Pháp Toán trong Tin học, Toán Tin Ứng Dụng, và Khoa Học Dữ Liệu', 'Bộ môn sẽ chấm Seminar Phương Pháp Toán trong Tin học, Toán Tin Ứng Dụng, và Khoa Học Dữ Liệu.\n\nThời gian bắt đầu: 8h00 sáng thứ 7 ngày 20 tháng 07 năm 2024\nĐịa điểm: phòng E202B (Cơ sở Nguyễn Văn Cừ).', 'Logo-Math-CS-cyan-192.png', '<p>ộ môn sẽ chấm Seminar Phương Pháp Toán trong Tin học, Toán Tin Ứng Dụng, và Khoa Học Dữ Liệu.</p><ul><li>Thời gian bắt đầu: <strong>8h00</strong> <strong>sáng thứ 7 ngày 20 tháng 07 năm 2024</strong></li><li>Địa điểm: <strong>phòng E202B</strong> (Cơ sở Nguyễn Văn Cừ).</li></ul>', 0, 0, 2, 1, '2024-07-24 16:04:06', '2024-07-24 16:04:06', 0),
(23, '[Phòng đào tạo] Thông báo về việc học GDQP-AN cho sinh viên K2023', '[Phòng đào tạo] Thông báo về việc học GDQP-AN cho sinh viên K2023', '1.jpg', '<p>Thông báo số 184/TB-KHTN&nbsp;từ Phòng đào tạo về việc học GDQP-AN năm 2024 cho sinh viên K2023:&nbsp;<a href=\"https://hcmus.edu.vn/thong-bao-to-chuc-hoc-giao-duc-quoc-phong-an-ninh-cho-sinh-vien-khoa-2023-dai-hoc-chinh-quy/\">Xem tại đây</a></p>', 0, 0, 2, 1, '2024-07-24 16:04:43', '2024-07-24 16:04:43', 0),
(24, 'THÔNG BÁO VỀ VIỆC NỘP HỒ SƠ BẢO VỆ LUẬN VĂN/ĐỒ ÁN THẠC SĨ ĐỢT THÁNG 10.2024', 'THÔNG BÁO VỀ VIỆC NỘP HỒ SƠ BẢO VỆ LUẬN VĂN/ĐỒ ÁN THẠC SĨ ĐỢT THÁNG 10.2024', '1.jpg', '<p><i><strong>THÔNG BÁO VỀ VIỆC NỘP HỒ SƠ BẢO VỆ LUẬN VĂN/ĐỒ ÁN THẠC SĨ</strong></i></p><p><i><strong>ĐỢT THÁNG 10.2024</strong></i></p><p><i><strong>Ngành: Toán ứng dụng và Toán ứng dụng – CN Giáo dục Toán học</strong></i></p><p><i><strong>&nbsp;</strong></i></p><p>Khoa Toán-Tin học có kế hoạch tổ chức buổi bảo vệ luận văn/đồ án Thạc sĩ cho các đối tượng sau:</p><p>+ HVCH ngành Toán ứng dụng: &nbsp;khoá 2022 (K32, bảo vệ trước hạn nếu có nhu cầu) và những khoá trước đó (<i>Luận văn Thạc sĩ</i>).</p><p>+ HVCH ngành Toán ứng dụng – CN Giáo dục Toán học: khoá 2022 (K32, bảo vệ trước hạn nếu có nhu cầu) (<i>Đồ án Thạc sĩ</i>) và những khoá trước đó (<i>Luận văn Thạc sĩ</i>).</p><p>Thời gian bảo vệ: vào&nbsp;<i><strong>tuần đầu tháng 10.2024 </strong></i>(thời gian cụ thể sẽ thông báo khi nhận được hồ sơ đăng ký).</p><p>Thầy cô hướng dẫn và các học viên lưu ý các thông tin sau:</p><p><strong>1.</strong>&nbsp;<strong>Thời gian nộp hồ sơ cho GV phụ trách ngành: từ 26.08.2024 đến 30.08.2024</strong>.</p><p>Học viên cần:</p><p>+ Chuẩn bị đầy đủ hồ sơ theo hướng dẫn của phòng Sau đại học (xem trên website của phòng, mục hướng dẫn quy trình/biểu mẫu). Lưu ý: nếu đồ án thạc sĩ (K32 - GDTH) chưa có mẫu, HVCH có thể dùng mẫu của luận văn thạc sĩ nhưng sửa thành cụm từ “Đồ án”.</p><p>+ Xin đầy đủ chữ ký của cán bộ hướng dẫn.</p><p>+ Nộp hồ sơ (<i>kèm 1 quyển in luận văn/đồ án hoàn chỉnh</i>) cho GV phụ trách ngành: Thầy Nguyễn Lê Hoàng Anh (cách nộp: đến Văn phòng Khoa Toán – Tin học phòng F.08, gặp giáo vụ khoa và gửi hồ sơ cho Thầy Hoàng Anh).</p><p><i><strong>Lưu ý</strong></i>:</p><p>+ HVCH K32 bắt buộc phải có chứng chỉ Tiếng Anh, Triết học và hoàn thành các môn (không nợ môn, không thiếu tín chỉ) mới được nộp hồ sơ xin bảo vệ.</p><p>+ Sau khi gửi hồ sơ giấy tại văn phòng Khoa, HVCH gửi email thông báo đến Thầy Hoàng Anh (<a href=\"mailto:nlhanh@hcmus.edu.vn\">nlhanh@hcmus.edu.vn</a>), nhằm tránh thất lạc hồ sơ sau khi kiểm tra.</p><p><strong>2.</strong>&nbsp;<strong>GV phụ trách ngành trả hồ sơ cho học viên: từ ngày 04.09.2024 đến ngày 10.09.2024</strong>&nbsp;(sẽ có thông báo cụ thể đến từng học viên sau khi kiểm tra hồ sơ).</p><p>Học viên đến nhận lại hồ sơ theo hẹn, sau đó nộp hồ sơ + 5 quyển in luận văn cho phòng SĐH (3 quyển đối với đồ án thạc sĩ).</p><p><i><strong>Lưu ý</strong></i>:</p><p>+ Học viên cần nộp hồ sơ đúng hạn để kịp thời gian bảo vệ (khuyến khích nộp sớm để có thể chỉnh sửa nếu sai sót).</p><p>+ Khi phụ trách ngành xác nhận hồ sơ hợp lệ, học viên có thể in các quyển luận văn/đồ án, đến nhận lại hồ sơ thì đem theo các quyển để nộp cho phòng SĐH trong ngày.</p><p>+&nbsp;<i><strong>Hạn chót học viên nộp hồ sơ về phòng SĐH là ngày 10.09.2024</strong></i>.</p><p><strong>3.</strong>&nbsp;<strong>Phòng SĐH xử lý hồ sơ và liên hệ học viên đến nhận các bìa hồ sơ</strong>: khoảng 3-5 ngày tính từ ngày phòng SĐH nhận hồ sơ từ học viên.</p><p><strong>Đối với học viên không kịp bảo vệ đợt tháng 10.2024</strong></p><p>Theo dõi thông tin trên website Khoa cho đợt bảo vệ tiếp theo (dự kiến tháng 11 có thông báo cho đợt cuối tháng 12/2024, K32 (đúng hạn) + các khoá trước đó).</p>', 0, 0, 2, 1, '2024-07-24 16:05:16', '2024-07-24 16:05:16', 0);

